INSERT OR REPLACE INTO cards VALUES('node-e69248f9f34cce502fc13578b416dd4155352a75','9y9ve',replace('**Juliet**\n\nHow camest thou hither, tell me, and wherefore?\n\nThe orchard walls are high and hard to climb,\n\nAnd the place death, considering who thou art,\n\nIf any of my kinsmen find thee here.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',11.0,'0:2:1bc7c1f9',0);
