INSERT OR REPLACE INTO cards VALUES('node-5ded593139f584808e3508203915160c55b2ebb0','9y9ve',replace('**Paris**\n\nThy face is mine, and thou hast slander''d it.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',20.0,'0:2:1c4661cb',0);
INSERT OR REPLACE INTO cards VALUES('node-cc6f24dacc960364a819c0aa42b3cfd8b407721c','9y9ve',replace('**Juliet**\n\nBlister''d be thy tongue\n\nFor such a wish! he was not born to shame:\n\nUpon his brow shame is ashamed to sit;\n\nFor ''tis a throne where honour may be crown''d\n\nSole monarch of the universal earth.\n\nO, what a beast was I to chide at him!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',16.0,'0:2:1cc0e7da',0);
INSERT OR REPLACE INTO cards VALUES('node-5c3b6d77300a7cd261e26e59d49245dd8e197572','9y9ve',replace('**Sampson**\n\nNay, as they dare. I will bite my thumb at them;\n\nwhich is a disgrace to them, if they bear it.\n\nEnter Abraham and Balthasar','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',24.0,'0:2:1cdc259d',0);
INSERT OR REPLACE INTO cards VALUES('node-c58965152c41f334590c35b66be8e1618cc9339f','9y9ve',replace('**Juliet**\n\nSweet, so would I:\n\nYet I should kill thee with much cherishing.\n\nGood night, good night! parting is such\n\nsweet sorrow,\n\nThat I shall say good night till it be morrow.\n\nExit above','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',53.0,'0:2:1d8d71b3',0);
INSERT OR REPLACE INTO cards VALUES('node-6f4142d385811b5bc73e5c967b24c668fbc0d806','9y9ve',replace('**Nurse**\n\nBy my troth, it is well said; ''for himself to mar,''\n\nquoth a''? Gentlemen, can any of you tell me where I\n\nmay find the young Romeo?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',56.0,'0:2:1e04df95',0);
INSERT OR REPLACE INTO cards VALUES('node-6bae03c77e4d126999a932346e10de21622c6242','9y9ve',replace('**Juliet**\n\nI would not for the world they saw thee here.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',15.0,'0:2:1e2a7dce',0);
INSERT OR REPLACE INTO cards VALUES('node-ccfeaa1f3e1e5be3386d56d7892db8371256d90a','9y9ve',replace('**Romeo**\n\nWhose house?','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',21.0,'0:2:1e392512',0);
INSERT OR REPLACE INTO cards VALUES('node-72a24fde25c4e54a1219b7f5f07eaf3317a25072','9y9ve',replace('**Romeo**\n\nHow should they, when that wise men have no eyes?','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',15.0,'0:2:1e795596',0);
INSERT OR REPLACE INTO cards VALUES('node-8440cc90e19490a22884cddf62e4fdafb7df4a6a','9y9ve',replace('**Nurse**\n\nThere''s no trust,\n\nNo faith, no honesty in men; all perjured,\n\nAll forsworn, all naught, all dissemblers.\n\nAh, where''s my man? give me some aqua vitae:\n\nThese griefs, these woes, these sorrows make me old.\n\nShame come to Romeo!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',15.0,'0:2:1f0cc2c5',0);
INSERT OR REPLACE INTO cards VALUES('node-57752ce7c299948b93d9cff769c77d0272600e54','9y9ve',replace('**Lady Capulet**\n\n[Within]Ho, daughter! are you up?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',18.0,'0:2:1f1dc3b5',0);
INSERT OR REPLACE INTO cards VALUES('node-8c79e14feea787696b3684fff050afb79a718444','9y9ve',replace('**Lady Capulet**\n\nO me! this sight of death is as a bell,\n\nThat warns my old age to a sepulchre.\n\nEnter Montague and others','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',47.0,'0:2:1f7878e4',0);
INSERT OR REPLACE INTO cards VALUES('node-db09d52b0e47783cc33ad26743d3b7c89d755e34','9y9ve',replace('**Nurse**\n\nO lamentable day!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',10.0,'0:2:201b25dc',0);
INSERT OR REPLACE INTO cards VALUES('node-7fc5a2eb6f2dee4bc199015f2b0bd25610c18dd2','9y9ve',replace('**Sampson**\n\nWell, sir.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',35.0,'0:2:20d2be67',0);
INSERT OR REPLACE INTO cards VALUES('node-c96371ba2f18c059096ce16b9ad01af32c43be69','9y9ve',replace('**Romeo**\n\nAy, so I fear; the more is my unrest.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',40.0,'0:2:20d965f9',0);
INSERT OR REPLACE INTO cards VALUES('node-d01178310e2837d27f08855868bbf48b23885476','9y9ve',replace('**First Musician**\n\nNot a dump we; ''tis no time to play now.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',29.0,'0:2:2128b40e',0);
