INSERT OR REPLACE INTO cards VALUES('node-8876643c854841fed8db7e05dfb10c2cffdbab2a','9y9ve',replace('**Montague**\n\nI neither know it nor can learn of him.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',57.0,'0:2:21761bb5',0);
INSERT OR REPLACE INTO cards VALUES('node-fd4d2d563435d45a8cc150a671c5de3cd57627b2','9y9ve',replace('**Paris**\n\nO, I am slain!\n\nFalls\n\nIf thou be merciful,\n\nOpen the tomb, lay me with Juliet.\n\nDies','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',13.0,'0:2:2189db82',0);
INSERT OR REPLACE INTO cards VALUES('node-fb0788a8cea6d7b55e2bd4f528a35ee9da25239d','9y9ve',replace('**Gregory**\n\nI will frown as I pass by, and let them take it as\n\nthey list.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',23.0,'0:2:21d6414a',0);
INSERT OR REPLACE INTO cards VALUES('node-eeea07625f97fe74629275d8eb0c7d95600d5141','9y9ve',replace('**Third Watchman**\n\nHere is a friar, that trembles, sighs and weeps:\n\nWe took this mattock and this spade from him,\n\nAs he was coming from this churchyard side.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',37.0,'0:2:21eac580',0);
INSERT OR REPLACE INTO cards VALUES('node-79df4771769fab8b8d26ada09d8e9e7fce5c7897','9y9ve',replace('**Juliet**\n\nConceit, more rich in matter than in words,\n\nBrags of his substance, not of ornament:\n\nThey are but beggars that can count their worth;\n\nBut my true love is grown to such excess\n\nI cannot sum up sum of half my wealth.','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',7.0,'0:2:223f74d9',0);
INSERT OR REPLACE INTO cards VALUES('node-a18865a03b132f0d54ca7f61ab12bf971c23f6bf','9y9ve',replace('**Romeo**\n\nI warrant thee, my man''s as true as steel.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',84.0,'0:2:22650c92',0);
INSERT OR REPLACE INTO cards VALUES('node-33ee98f02c6a631ac68fb1c593fb7c8d8bb5ac5f','9y9ve',replace('**Juliet**\n\nI come, anon. But if thou mean''st not well,\n\nI do beseech thee=','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',35.0,'0:2:22ed8441',0);
INSERT OR REPLACE INTO cards VALUES('node-1e5467d59a5a49ab972233cc16d54d2011ab9cc5','9y9ve',replace('**Tybalt**\n\n''Tis he, that villain Romeo.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',18.0,'0:2:23132cb7',0);
INSERT OR REPLACE INTO cards VALUES('node-9c608b3434854e251b2382b3fb49aaf39ef633b0','9y9ve',replace('**Benvolio**\n\nFor what, I pray thee?','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',8.0,'0:2:2393184a',0);
INSERT OR REPLACE INTO cards VALUES('node-9f50b282128cd9aa0700aad95151b8c12878b4af','9y9ve',replace('**Sampson**\n\n''Tis all one, I will show myself a tyrant: when I\n\nhave fought with the men, I will be cruel with the\n\nmaids, and cut off their heads.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',12.0,'0:2:23b21e4d',0);
INSERT OR REPLACE INTO cards VALUES('node-12b9764557e330707b784a4b5dce45e59b5bd0d1','9y9ve',replace('**Juliet**\n\nMadam, I am not well.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',21.0,'0:2:247463e2',0);
INSERT OR REPLACE INTO cards VALUES('node-3e285c89e3543d9e2af37a0132e4160ac2521ea1','9y9ve',replace('**Benvolio**\n\nTell me in sadness, who is that you love.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',80.0,'0:2:2476ece1',0);
INSERT OR REPLACE INTO cards VALUES('node-37d8906dad05cd6589e583012bc99ef1de62350c','9y9ve',replace('**Juliet**\n\nThen have my lips the sin that they have took.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',32.0,'0:2:2507495',0);
INSERT OR REPLACE INTO cards VALUES('node-79ea616efadc6948651cfdf3cb9933b1af7ccb93','9y9ve',replace('**Nurse**\n\nGod in heaven bless her!\n\nYou are to blame, my lord, to rate her so.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',49.0,'0:2:252cd4ef',0);
INSERT OR REPLACE INTO cards VALUES('node-111fdf95e5c381d356446b64b7a94e752b960a5e','9y9ve',replace('**Romeo**\n\nSin from thy lips? O trespass sweetly urged!\n\nGive me my sin again.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',33.0,'0:2:2552004c',0);
INSERT OR REPLACE INTO cards VALUES('node-00b5219a132065f0204e6615dc8af38aa08e3881','9y9ve',replace('**Nurse**\n\nAn a'' speak any thing against me, I''ll take him\n\ndown, an a'' were lustier than he is, and twenty such\n\nJacks; and if I cannot, I''ll find those that shall.\n\nScurvy knave! I am none of his flirt-gills; I am\n\nnone of his skains-mates. And thou must stand by\n\ntoo, and suffer every knave to use me at his pleasure?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',69.0,'0:2:259e36c2',0);
INSERT OR REPLACE INTO cards VALUES('node-cdc253ca2b63bce6224342e5fa9ccd5ff7e55106','9y9ve',replace('**Romeo**\n\nAs if that name,\n\nShot from the deadly level of a gun,\n\nDid murder her; as that name''s cursed hand\n\nMurder''d her kinsman. O, tell me, friar, tell me,\n\nIn what vile part of this anatomy\n\nDoth my name lodge? tell me, that I may sack\n\nThe hateful mansion.\n\nDrawing his sword','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',30.0,'0:2:262d294e',0);
INSERT OR REPLACE INTO cards VALUES('node-01f3bb48897b98d4a88b2d640530b70578e6e05f','9y9ve',replace('**Benvolio**\n\nTwo, two; a shirt and a smock.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',45.0,'0:2:264020d',0);
INSERT OR REPLACE INTO cards VALUES('node-ae90bff56005f3581de5684f09aac54630a8d76d','9y9ve',replace('**Romeo**\n\nSleep dwell upon thine eyes, peace in thy breast!\n\nWould I were sleep and peace, so sweet to rest!\n\nHence will I to my ghostly father''s cell,\n\nHis help to crave, and my dear hap to tell.\n\nExit','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',54.0,'0:2:26b3082a',0);
INSERT OR REPLACE INTO cards VALUES('node-9d2669f6ea3e7b7a5d7c4d232abcc578faf34cf4','9y9ve',replace('**Paris**\n\nYounger than she are happy mothers made.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',3.0,'0:2:26cc538e',0);
INSERT OR REPLACE INTO cards VALUES('node-d6cd9615e11ca5cfb2a51bf9589026a565f790ee','9y9ve',replace('**Romeo**\n\nOut','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',69.0,'0:2:27f495ae',0);
INSERT OR REPLACE INTO cards VALUES('node-30c46126995392cd1fd01acd0227fc19022480eb','9y9ve',replace('**Romeo**\n\nNurse, commend me to thy lady and mistress. I\n\nprotest unto thee','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',72.0,'0:2:28a3d62f',0);
INSERT OR REPLACE INTO cards VALUES('node-1df45b6522c9a6410246f554ea52cf8661e31ce9','9y9ve',replace('**Romeo**\n\nIs it even so? then I defy you, stars!\n\nThou know''st my lodging: get me ink and paper,\n\nAnd hire post-horses; I will hence to-night.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',2.0,'0:2:28aab30d',0);
INSERT OR REPLACE INTO cards VALUES('node-e4cf4043552585dfe099f2f3b40cf1d6a10e22ea','9y9ve',replace('**Friar Laurence**\n\nRomeo!\n\nAdvances\n\nAlack, alack, what blood is this, which stains\n\nThe stony entrance of this sepulchre?\n\nWhat mean these masterless and gory swords\n\nTo lie discolour''d by this place of peace?\n\nEnters the tomb\n\nRomeo! O, pale! Who else? what, Paris too?\n\nAnd steep''d in blood? Ah, what an unkind hour\n\nIs guilty of this lamentable chance!\n\nThe lady stirs.\n\nJuliet wakes','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',27.0,'0:2:28b7f3b5',0);
INSERT OR REPLACE INTO cards VALUES('node-12e4a4f2094dd2a2dc3d529236a57ea4de09d9e6','9y9ve',replace('**Romeo**\n\nNay, that''s not so.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',13.0,'0:2:28eb122e',0);
INSERT OR REPLACE INTO cards VALUES('node-0cc37c4255aa063fe62d208905936832e2efb20b','9y9ve',replace('**Peter**\n\nPretty too! What say you, James Soundpost?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',43.0,'0:2:28fa1367',0);
INSERT OR REPLACE INTO cards VALUES('node-5c90a82fc7211cbd66f3c4d8faa1d0c07b677f05','9y9ve',replace('**Benvolio**\n\nThou wouldst else have made thy tale large.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',41.0,'0:2:29361495',0);
INSERT OR REPLACE INTO cards VALUES('node-87d463c4427d6e94f214c3c2b182a5877404287a','9y9ve',replace('**Juliet**\n\nAy me!','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',1.0,'0:2:2957fd8f',0);
INSERT OR REPLACE INTO cards VALUES('node-a3d7e8f9bbe101693e4f10da7a9f29257973d9a7','9y9ve',replace('**Romeo**\n\nA most courteous exposition.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',21.0,'0:2:29777a30',0);
INSERT OR REPLACE INTO cards VALUES('node-01b9ac134fb9ce5790ab6a1c06078d0720a4977f','9y9ve',replace('**Juliet**\n\nAy, those attires are best: but, gentle nurse,\n\nI pray thee, leave me to my self to-night,\n\nFor I have need of many orisons\n\nTo move the heavens to smile upon my state,\n\nWhich, well thou know''st, is cross, and full of sin.\n\nEnter Lady Capulet','\n',char(10)),'node-5a5e52ac71bf638baf35f7e8ff54a68376bf2aa7',0.0,'0:2:2993180a',0);
INSERT OR REPLACE INTO cards VALUES('node-cc6b3e670af4bda1da836d042a0a64ccb5ff108f','9y9ve',replace('**Capulet**\n\nWhat should it be, that they so shriek abroad?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',40.0,'0:2:29c5ed65',0);
INSERT OR REPLACE INTO cards VALUES('node-e30ab57f67d795725840f8eae9cc573e8368fc37','9y9ve',replace('**Juliet**\n\nGive me, give me! O, tell not me of fear!','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',30.0,'0:2:2a46093c',0);
INSERT OR REPLACE INTO cards VALUES('node-689f3e04d3da6023e4c4916e8a08bfc131a0ee9b','9y9ve',replace('**Nurse**\n\nAy, forsooth.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',5.0,'0:2:2b1512a3',0);
INSERT OR REPLACE INTO cards VALUES('node-831d1c65b7991c14744c84a46f3b0d14ae60461e','9y9ve',replace('**Nurse**\n\nWhat''s this? what''s this?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',51.0,'0:2:2b2cde0',0);
INSERT OR REPLACE INTO cards VALUES('node-d5590b995b2073af9711f7f574cce8ee6cb75e26','9y9ve',replace('**Peter**\n\nPretty! What say you, Hugh Rebeck?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',41.0,'0:2:2b325022',0);
INSERT OR REPLACE INTO cards VALUES('node-2be053e77babaac75626bbfa773dd46adc5bf987','9y9ve',replace('**Benvolio**\n\nA right fair mark, fair coz, is soonest hit.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',86.0,'0:2:2b509577',0);
INSERT OR REPLACE INTO cards VALUES('node-01a4a8bed1953973a07ba30121dc66426afe2dcc','9y9ve',replace('**Capulet**\n\nBut saying o''er what I have said before:\n\nMy child is yet a stranger in the world;\n\nShe hath not seen the change of fourteen years,\n\nLet two more summers wither in their pride,\n\nEre we may think her ripe to be a bride.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',2.0,'0:2:2bf95210',0);
INSERT OR REPLACE INTO cards VALUES('node-a96d89990b6e48bc0837df39f37f4d0557feecd3','9y9ve',replace('**Friar Laurence**\n\nMy leisure serves me, pensive daughter, now.\n\nMy lord, we must entreat the time alone.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',22.0,'0:2:2c391e6b',0);
INSERT OR REPLACE INTO cards VALUES('node-75ffcfb083814ee4ff868e19fe33f930dab5f70d','9y9ve',replace('**Nurse**\n\n```\nWithin\n```\n\nMadam!','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',36.0,'0:2:2cc9d9d1',0);
INSERT OR REPLACE INTO cards VALUES('node-40beff033e28fbc18e61d03f096e9167a29ffc1e','9y9ve',replace('**Mercutio**\n\nNo hare, sir; unless a hare, sir, in a lenten pie,\n\nthat is something stale and hoar ere it be spent.\n\nSings\n\nAn old hare hoar,\n\nAnd an old hare hoar,\n\nIs very good meat in lent\n\nBut a hare that is hoar\n\nIs too much for a score,\n\nWhen it hoars ere it be spent.\n\nRomeo, will you come to your father''s? we''ll\n\nto dinner, thither.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',64.0,'0:2:2cea8342',0);
INSERT OR REPLACE INTO cards VALUES('node-25ec4948078db02d2fd0c1856e652b8fe8883abb','9y9ve',replace('**Romeo**\n\nIn faith, I will. Let me peruse this face.\n\nMercutio''s kinsman, noble County Paris!\n\nWhat said my man, when my betossed soul\n\nDid not attend him as we rode? I think\n\nHe told me Paris should have married Juliet:\n\nSaid he not so? or did I dream it so?\n\nOr am I mad, hearing him talk of Juliet,\n\nTo think it was so? O, give me thy hand,\n\nOne writ with me in sour misfortune''s book!\n\nI''ll bury thee in a triumphant grave;\n\nA grave? O no! a lantern, slaughter''d youth,\n\nFor here lies Juliet, and her beauty makes\n\nThis vault a feasting presence full of light.\n\nDeath, lie thou there, by a dead man interr''d.\n\nLaying Paris in the tomb\n\nHow oft when men are at the point of death\n\nHave they been merry! which their keepers call\n\nA lightning before death: O, how may I\n\nCall this a lightning? O my love! my wife!\n\nDeath, that hath suck''d the honey of thy breath,\n\nHath had no power yet upon thy beauty:\n\nThou art not conquer''d; beauty''s ensign yet\n\nIs crimson in thy lips and in thy cheeks,\n\nAnd death''s pale flag is not advanced there.\n\nTybalt, liest thou there in thy bloody sheet?\n\nO, what more favour can I do to thee,\n\nThan with that hand that cut thy youth in twain\n\nTo sunder his that was thine enemy?\n\nForgive me, cousin! Ah, dear Juliet,\n\nWhy art thou yet so fair? shall I believe\n\nThat unsubstantial death is amorous,\n\nAnd that the lean abhorred monster keeps\n\nThee here in dark to be his paramour?\n\nFor fear of that, I still will stay with thee;\n\nAnd never from this palace of dim night\n\nDepart again: here, here will I remain\n\nWith worms that are thy chamber-maids; O, here\n\nWill I set up my everlasting rest,\n\nAnd shake the yoke of inauspicious stars\n\nFrom this world-wearied flesh. Eyes, look your last!\n\nArms, take your last embrace! and, lips, O you\n\nThe doors of breath, seal with a righteous kiss\n\nA dateless bargain to engrossing death!\n\nCome, bitter conduct, come, unsavoury guide!\n\nThou desperate pilot, now at once run on\n\nThe dashing rocks thy sea-sick weary bark!\n\nHere''s to my love!\n\nDrinks\n\nO true apothecary!\n\nThy drugs are quick. Thus with a kiss I die.\n\nDies\n\nEnter, at the other end of the churchyard, Friar Laurence, with a lantern, crow, and spade','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',14.0,'0:2:2d41a1d3',0);
INSERT OR REPLACE INTO cards VALUES('node-f4693ccb694a12b48dc160d7038131b268f4ca0b','9y9ve',replace('**Romeo**\n\nI thought all for the best.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',36.0,'0:2:2db3a038',0);
INSERT OR REPLACE INTO cards VALUES('node-2489e0e687215bf57d15844d880d6fa9c266cad1','9y9ve',replace('**Mercutio**\n\nO, thou art deceived; I would have made it short:\n\nfor I was come to the whole depth of my tale; and\n\nmeant, indeed, to occupy the argument no longer.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',42.0,'0:2:2deb8c4e',0);
INSERT OR REPLACE INTO cards VALUES('node-c363b5c9221168c22b4231e1af34c48a154ad785','9y9ve',replace('**Friar John**\n\nBrother, I''ll go and bring it thee.\n\nExit','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',6.0,'0:2:2e2a83f4',0);
INSERT OR REPLACE INTO cards VALUES('node-e3b9a4fd288ba65e4584377f3e21169eda617af9','9y9ve',replace('**Page**\n\nO Lord, they fight! I will go call the watch.\n\nExit','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',12.0,'0:2:2e50a2e7',0);
INSERT OR REPLACE INTO cards VALUES('node-b0df36547bf22042a2407b8b057e3665e909e1c0','9y9ve',replace('**Third Musician**\n\nFaith, I know not what to say.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',44.0,'0:2:2e6a1ebf',0);
INSERT OR REPLACE INTO cards VALUES('node-06325c2b885bd6bb5f2737c9cfe087a7ad09cc27','9y9ve',replace('**Romeo**\n\nHave not saints lips, and holy palmers too?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',27.0,'0:2:2e9e1bf1',0);
INSERT OR REPLACE INTO cards VALUES('node-5d5bcab2c583a16fe6addce1cbf3cc0e25bb610b','9y9ve',replace('**Mercutio**\n\nBy my heel, I care not.\n\nEnter Tybalt and others','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',9.0,'0:2:2efa4d41',0);
INSERT OR REPLACE INTO cards VALUES('node-c1b4e12501972f25534c402b7258acc69d9f3c4b','9y9ve',replace('**Capulet**\n\nDeath, that hath ta''en her hence to make me wail,\n\nTies up my tongue, and will not let me speak.\n\nEnter Friar Laurence and Paris, with Musicians','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',12.0,'0:2:2f023fa3',0);
INSERT OR REPLACE INTO cards VALUES('node-7b47ff8069c0aeffbb7583751a73a239d9ad93c6','9y9ve',replace('**Lady Capulet**\n\nTybalt, my cousin! O my brother''s child!\n\nO prince! O cousin! husband! O, the blood is spilt\n\nO my dear kinsman! Prince, as thou art true,\n\nFor blood of ours, shed blood of Montague.\n\nO cousin, cousin!','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',53.0,'0:2:2f4013df',0);
INSERT OR REPLACE INTO cards VALUES('node-342c9447e5a824c797e463d4a2e00f0a386c1bcb','9y9ve',replace('**Juliet**\n\nWilt thou be gone? it is not yet near day:\n\nIt was the nightingale, and not the lark,\n\nThat pierced the fearful hollow of thine ear;\n\nNightly she sings on yon pomegranate-tree:\n\nBelieve me, love, it was the nightingale.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',0.0,'0:2:2f513002',0);
INSERT OR REPLACE INTO cards VALUES('node-127199605ec838074fa0ee6449b4f5f095c76473','9y9ve',replace('**First Watchman**\n\nSovereign, here lies the County Paris slain;\n\nAnd Romeo dead; and Juliet, dead before,\n\nWarm and new kill''d.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',43.0,'0:2:2fa52e2f',0);
INSERT OR REPLACE INTO cards VALUES('node-8d1727c19b2699ee41283dc354ab773874f759bf','9y9ve',replace('**Juliet**\n\nWhat must be shall be.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',8.0,'0:2:2fb4b2b2',0);
INSERT OR REPLACE INTO cards VALUES('node-f8c780a8551dd546de43f1d317b60377cd64bea3','9y9ve',replace('**Romeo**\n\nNot I, believe me: you have dancing shoes\n\nWith nimble soles: I have a soul of lead\n\nSo stakes me to the ground I cannot move.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',4.0,'0:2:30401702',0);
INSERT OR REPLACE INTO cards VALUES('node-eec41fc399cabf1b7b65b9969bfc17089985a5fa','9y9ve',replace('**Nurse**\n\nMarry, farewell! I pray you, sir, what saucy\n\nmerchant was this, that was so full of his ropery?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',67.0,'0:2:3053d17',0);
INSERT OR REPLACE INTO cards VALUES('node-b301a83998ca69f2b074744526454a9430b109f1','9y9ve',replace('**Friar Laurence**\n\n```\nAside\n```\n\nI would I knew not why it should be slow''d.\n\nLook, sir, here comes the lady towards my cell.\n\n```\nEnter Juliet\n```\n\n','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',4.0,'0:2:306784aa',0);
INSERT OR REPLACE INTO cards VALUES('node-d8db815a0c81fdd6e474d7abb31c00c86c1c4b9a','9y9ve',replace('**Juliet**\n\nThree words, dear Romeo, and good night indeed.\n\nIf that thy bent of love be honourable,\n\nThy purpose marriage, send me word to-morrow,\n\nBy one that I''ll procure to come to thee,\n\nWhere and what time thou wilt perform the rite;\n\nAnd all my fortunes at thy foot I''ll lay\n\nAnd follow thee my lord throughout the world.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',33.0,'0:2:3192904d',0);
INSERT OR REPLACE INTO cards VALUES('node-7310bc8c27a6e64a56d968ff6089e4f68718af13','9y9ve',replace('**Gregory**\n\nThey must take it in sense that feel it.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',15.0,'0:2:31a3433c',0);
INSERT OR REPLACE INTO cards VALUES('node-b4cc288a5ba5fbe5f88f004e8e3af343afca8d94','9y9ve',replace('**Romeo**\n\nThis shall determine that.\n\nThey fight; Tybalt falls','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',44.0,'0:2:31be0dd8',0);
INSERT OR REPLACE INTO cards VALUES('node-5edc39c6fb90dfa573cc7de75a1e7d7b9d773204','9y9ve',replace('**Nurse**\n\nMistress! what, mistress! Juliet! fast, I warrant her, she:\n\nWhy, lamb! why, lady! fie, you slug-a-bed!\n\nWhy, love, I say! madam! sweet-heart! why, bride!\n\nWhat, not a word? you take your pennyworths now;\n\nSleep for a week; for the next night, I warrant,\n\nThe County Paris hath set up his rest,\n\nThat you shall rest but little. God forgive me,\n\nMarry, and amen, how sound is she asleep!\n\nI must needs wake her. Madam, madam, madam!\n\nAy, let the county take you in your bed;\n\nHe''ll fright you up, i'' faith. Will it not be?\n\nUndraws the curtains\n\nWhat, dress''d! and in your clothes! and down again!\n\nI must needs wake you; Lady! lady! lady!\n\nAlas, alas! Help, help! my lady''s dead!\n\nO, well-a-day, that ever I was born!\n\nSome aqua vitae, ho! My lord! my lady!\n\nEnter Lady Capulet','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',0.0,'0:2:31d832b2',0);
INSERT OR REPLACE INTO cards VALUES('node-b143f6004e480193694f50f11c64c3437ca009be','9y9ve',replace('**Servant**\n\nGod gi'' god-den. I pray, sir, can you read?','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',12.0,'0:2:33657d3e',0);
INSERT OR REPLACE INTO cards VALUES('node-cde9338ba68e417631b3fcf46e25d3c0c33ef52f','9y9ve',replace('**Friar Laurence**\n\nPeace, ho, for shame! confusion''s cure lives not\n\nIn these confusions. Heaven and yourself\n\nHad part in this fair maid; now heaven hath all,\n\nAnd all the better is it for the maid:\n\nYour part in her you could not keep from death,\n\nBut heaven keeps his part in eternal life.\n\nThe most you sought was her promotion;\n\nFor ''twas your heaven she should be advanced:\n\nAnd weep ye now, seeing she is advanced\n\nAbove the clouds, as high as heaven itself?\n\nO, in this love, you love your child so ill,\n\nThat you run mad, seeing that she is well:\n\nShe''s not well married that lives married long;\n\nBut she''s best married that dies married young.\n\nDry up your tears, and stick your rosemary\n\nOn this fair corse; and, as the custom is,\n\nIn all her best array bear her to church:\n\nFor though fond nature bids us an lament,\n\nYet nature''s tears are reason''s merriment.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',20.0,'0:2:33d39044',0);
INSERT OR REPLACE INTO cards VALUES('node-2392062b5ca3dccf10748eb267cec43b1c06a362','9y9ve',replace('**Juliet**\n\nGo ask his name: if he be married.\n\nMy grave is like to be my wedding bed.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',48.0,'0:2:3409f916',0);
INSERT OR REPLACE INTO cards VALUES('node-496bb93944f43934af1ec18d6ab361e0726885a5','9y9ve',replace('**Friar Laurence**\n\nHold; get you gone, be strong and prosperous\n\nIn this resolve: I''ll send a friar with speed\n\nTo Mantua, with my letters to thy lord.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',31.0,'0:2:343e5898',0);
INSERT OR REPLACE INTO cards VALUES('node-bdf067834fd56f805dbca88077a93dd9eeee4043','9y9ve',replace('**Capulet**\n\nHang thee, young baggage! disobedient wretch!\n\nI tell thee what: get thee to church o'' Thursday,\n\nOr never after look me in the face:\n\nSpeak not, reply not, do not answer me;\n\nMy fingers itch. Wife, we scarce thought us blest\n\nThat God had lent us but this only child;\n\nBut now I see this one is one too much,\n\nAnd that we have a curse in having her:\n\nOut on her, hilding!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',48.0,'0:2:347b8e77',0);
INSERT OR REPLACE INTO cards VALUES('node-f2ae1493f258cdb2bc97f7d62f382414cd33d461','9y9ve',replace('**Romeo**\n\nO single-soled jest, solely singular for the\n\nsingleness.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',27.0,'0:2:34ab87b3',0);
INSERT OR REPLACE INTO cards VALUES('node-9aea09376e068c239f1de3a6f9a407f0efbcae08','9y9ve',replace('**Mercutio**\n\nBut I''ll be hanged, sir, if he wear your livery:\n\nMarry, go before to field, he''ll be your follower;\n\nYour worship in that sense may call him ''man.''','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',19.0,'0:2:34be8aca',0);
INSERT OR REPLACE INTO cards VALUES('node-a5a87c6d28140f4450332a8c353d4bf6b0171934','9y9ve',replace('**Tybalt**\n\nMercutio, thou consort''st with Romeo,','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',14.0,'0:2:34d191ac',0);
INSERT OR REPLACE INTO cards VALUES('node-2bdd8ff317b62c273afbe10e760b3a58355a9825','9y9ve',replace('**Capulet**\n\nWell, he may chance to do some good on her:\n\nA peevish self-will''d harlotry it is.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',6.0,'0:2:3582aab6',0);
INSERT OR REPLACE INTO cards VALUES('node-d4ba58e5da57e836987c4e75e18fce3d6676d0e5','9y9ve',replace('**Romeo**\n\nYour plaintain-leaf is excellent for that.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',7.0,'0:2:3607cff',0);
INSERT OR REPLACE INTO cards VALUES('node-6fbeea8cf0278f5ae602f085054cd07f231e78bd','9y9ve',replace('**Capulet**\n\nO brother Montague, give me thy hand:\n\nThis is my daughter''s jointure, for no more\n\nCan I demand.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',61.0,'0:2:3744bb18',0);
INSERT OR REPLACE INTO cards VALUES('node-1d8327632b30857fca72340dc3e4b521ec57c95d','9y9ve',replace('**Benvolio**\n\nHe ran this way, and leap''d this orchard wall:\n\nCall, good Mercutio.','\n',char(10)),'node-5e06ac8ec784ae358a0c61c64cf0e889bc3d30ef',3.0,'0:2:37652676',0);
INSERT OR REPLACE INTO cards VALUES('node-ccc565c59e9fb0ce8a580b298db5e871b9e70c99','9y9ve',replace('**Romeo**\n\nNurse!','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',26.0,'0:2:376e39a2',0);
INSERT OR REPLACE INTO cards VALUES('node-0f78b99850f01f191610c099651b371f6007c601','9y9ve',replace('**Peter**\n\nI will then give it you soundly.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',32.0,'0:2:37954e55',0);
INSERT OR REPLACE INTO cards VALUES('node-de8a0f4e5af1c7f272b07bf97207fb904f3171fa','9y9ve',replace('**Second Musician**\n\nPray you, put up your dagger, and put out your wit.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',38.0,'0:2:381bdd83',0);
INSERT OR REPLACE INTO cards VALUES('node-36dae393a16e7d252869329ad21e90501cc53bce','9y9ve',replace('**Nurse**\n\nI will tell her, sir, that you do protest; which, as\n\nI take it, is a gentlemanlike offer.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',75.0,'0:2:384bda7c',0);
INSERT OR REPLACE INTO cards VALUES('node-173d24e17daa4a794a6dcd76ba5ad322d0ee0935','9y9ve',replace('**Second Servant**\n\nWe cannot be here and there too. Cheerly, boys; be\n\nbrisk awhile, and the longer liver take all.\n\nEnter Capulet, with Juliet and others of his house, meeting the Guests and Maskers','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',5.0,'0:2:385fb928',0);
INSERT OR REPLACE INTO cards VALUES('node-c242ad31447370d55677c640585ad3cd7e94ba8a','9y9ve',replace('**Lady Capulet**\n\nHold, take these keys, and fetch more spices, nurse.','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',0.0,'0:2:38a670e2',0);
INSERT OR REPLACE INTO cards VALUES('node-23ca8c01ff58706efa7af24575fe0f75b1d44083','9y9ve',replace('**Juliet**\n\nArt thou gone so? love, lord, ay, husband, friend!\n\nI must hear from thee every day in the hour,\n\nFor in a minute there are many days:\n\nO, by this count I shall be much in years\n\nEre I again behold my Romeo!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',11.0,'0:2:38ad6e55',0);
INSERT OR REPLACE INTO cards VALUES('node-13a7cfbefb81984ab507d339d2ab50900ed3effd','9y9ve',replace('**Capulet**\n\nSoft! take me with you, take me with you, wife.\n\nHow! will she none? doth she not give us thanks?\n\nIs she not proud? doth she not count her blest,\n\nUnworthy as she is, that we have wrought\n\nSo worthy a gentleman to be her bridegroom?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',43.0,'0:2:38b6a9be',0);
INSERT OR REPLACE INTO cards VALUES('node-047ccaeaefaff90645de6ac1108786809de0de04','9y9ve',replace('**Benvolio**\n\nI do but keep the peace: put up thy sword,\n\nOr manage it to part these men with me.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',42.0,'0:2:38e2836d',0);
INSERT OR REPLACE INTO cards VALUES('node-49ca224d2b854fbea2e7e3dfc36219ff87aa3dce','9y9ve',replace('**Nurse**\n\nI speak no treason.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',51.0,'0:2:38ed9bc0',0);
INSERT OR REPLACE INTO cards VALUES('node-f691900f559b64849d2a56f32d15b2caee0535c7','9y9ve',replace('**Nurse**\n\nHie to your chamber: I''ll find Romeo\n\nTo comfort you: I wot well where he is.\n\nHark ye, your Romeo will be here at night:\n\nI''ll to him; he is hid at Laurence'' cell.','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',21.0,'0:2:38f4db50',0);
INSERT OR REPLACE INTO cards VALUES('node-b2c664a658eeb7b3283401dca6f611cc594aca42','9y9ve',replace('**Nurse**\n\nThe son and heir of old Tiberio.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',43.0,'0:2:3914f48b',0);
INSERT OR REPLACE INTO cards VALUES('node-46e862bd46bd9747d6fc7d38931484eaee064306','9y9ve',replace('**Romeo**\n\nHow well my comfort is revived by this!','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',35.0,'0:2:392496f5',0);
INSERT OR REPLACE INTO cards VALUES('node-03cbf79f0ec9beaf5651ddf836c51c7a28143684','9y9ve',replace('**Romeo**\n\nWhat, shall this speech be spoke for our excuse?\n\nOr shall we on without a apology?','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',0.0,'0:2:398b3d0d',0);
INSERT OR REPLACE INTO cards VALUES('node-360160c042777b8ecec22c0316914f3971e82aea','9y9ve',replace('**Benvolio**\n\nGroan! why, no.\n\nBut sadly tell me who.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',82.0,'0:2:39e888cc',0);
INSERT OR REPLACE INTO cards VALUES('node-bc7f6bd17d79d14f6f0be7ac9ebc9321524b2bb1','9y9ve',replace('**Gregory**\n\nTo move is to stir; and to be valiant is to stand:\n\ntherefore, if thou art moved, thou runn''st away.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',7.0,'0:2:3b209cff',0);
INSERT OR REPLACE INTO cards VALUES('node-5f0cae81bf9ef91258c13850d5d78c7d9e5728c0','9y9ve',replace('**Romeo**\n\nI will follow you.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',65.0,'0:2:3bfe15ac',0);
INSERT OR REPLACE INTO cards VALUES('node-a7d648d31b03f979542c5e971370b09bb9269d92','9y9ve',replace('**Mercutio**\n\nO, then, I see Queen Mab hath been with you.\n\nShe is the fairies'' midwife, and she comes\n\nIn shape no bigger than an agate-stone\n\nOn the fore-finger of an alderman,\n\nDrawn with a team of little atomies\n\nAthwart men''s noses as they lie asleep;\n\nHer wagon-spokes made of long spiders'' legs,\n\nThe cover of the wings of grasshoppers,\n\nThe traces of the smallest spider''s web,\n\nThe collars of the moonshine''s watery beams,\n\nHer whip of cricket''s bone, the lash of film,\n\nHer wagoner a small grey-coated gnat,\n\nNot so big as a round little worm\n\nPrick''d from the lazy finger of a maid;\n\nHer chariot is an empty hazel-nut\n\nMade by the joiner squirrel or old grub,\n\nTime out o'' mind the fairies'' coachmakers.\n\nAnd in this state she gallops night by night\n\nThrough lovers'' brains, and then they dream of love;\n\nO''er courtiers'' knees, that dream on court''sies straight,\n\nO''er lawyers'' fingers, who straight dream on fees,\n\nO''er ladies '' lips, who straight on kisses dream,\n\nWhich oft the angry Mab with blisters plagues,\n\nBecause their breaths with sweetmeats tainted are:\n\nSometime she gallops o''er a courtier''s nose,\n\nAnd then dreams he of smelling out a suit;\n\nAnd sometime comes she with a tithe-pig''s tail\n\nTickling a parson''s nose as a'' lies asleep,\n\nThen dreams, he of another benefice:\n\nSometime she driveth o''er a soldier''s neck,\n\nAnd then dreams he of cutting foreign throats,\n\nOf breaches, ambuscadoes, Spanish blades,\n\nOf healths five-fathom deep; and then anon\n\nDrums in his ear, at which he starts and wakes,\n\nAnd being thus frighted swears a prayer or two\n\nAnd sleeps again. This is that very Mab\n\nThat plats the manes of horses in the night,\n\nAnd bakes the elflocks in foul sluttish hairs,\n\nWhich once untangled, much misfortune bodes:\n\nThis is the hag, when maids lie on their backs,\n\nThat presses them and learns them first to bear,\n\nMaking them women of good carriage:\n\nThis is she','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',22.0,'0:2:3cc7894c',0);
INSERT OR REPLACE INTO cards VALUES('node-5c440956012285dd014192c98e063f54ec18f63f','9y9ve',replace('**Romeo**\n\nGo to; I say you shall.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',78.0,'0:2:3d521322',0);
INSERT OR REPLACE INTO cards VALUES('node-b34b75179756e1ad2c8acea696d56d30dd684a57','9y9ve',replace('**Benvolio**\n\nBy giving liberty unto thine eyes;\n\nExamine other beauties.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',92.0,'0:2:3d6ba684',0);
INSERT OR REPLACE INTO cards VALUES('node-f4cc0a0fe670f8ce8aae50a032f7a54e046b6f56','9y9ve',replace('**Romeo**\n\nThou detestable maw, thou womb of death,\n\nGorged with the dearest morsel of the earth,\n\nThus I enforce thy rotten jaws to open,\n\nAnd, in despite, I''ll cram thee with more food!\n\nOpens the tomb','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',7.0,'0:2:3d703bc4',0);
INSERT OR REPLACE INTO cards VALUES('node-1f8f299c5de65d12fd90c20534ee73c726cfddf6','9y9ve',replace('**Capulet**\n\nMake haste, make haste.\n\nExit First Servant\n\nSirrah, fetch drier logs:\n\nCall Peter, he will show thee where they are.','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',8.0,'0:2:3d851338',0);
INSERT OR REPLACE INTO cards VALUES('node-83b7e227800692d444a1180f9483cf7ec776373f','9y9ve',replace('**Benvolio**\n\nCome, knock and enter; and no sooner in,\n\nBut every man betake him to his legs.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',10.0,'0:2:3d8d127c',0);
INSERT OR REPLACE INTO cards VALUES('node-11d7cfbe92b00f7feadfff8876a1d171284e4395','9y9ve',replace('**Romeo**\n\n```\nAside\n```\n\nShall I hear more, or shall I speak at this?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',4.0,'0:2:3de8f726',0);
INSERT OR REPLACE INTO cards VALUES('node-d617183e793cfbb39ed3d464416c4f96f2238b95','9y9ve',replace('**Romeo**\n\nI must indeed; and therefore came I hither.\n\nGood gentle youth, tempt not a desperate man;\n\nFly hence, and leave me: think upon these gone;\n\nLet them affright thee. I beseech thee, youth,\n\nPut not another sin upon my head,\n\nBy urging me to fury: O, be gone!\n\nBy heaven, I love thee better than myself;\n\nFor I come hither arm''d against myself:\n\nStay not, be gone; live, and hereafter say,\n\nA madman''s mercy bade thee run away.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',9.0,'0:2:3e2809c0',0);
INSERT OR REPLACE INTO cards VALUES('node-9ebd7259145117ca2a8279c92a4251ed5dc73125','9y9ve',replace('**Juliet**\n\nThe clock struck nine when I did send the nurse;\n\nIn half an hour she promised to return.\n\nPerchance she cannot meet him: that''s not so.\n\nO, she is lame! love''s heralds should be thoughts,\n\nWhich ten times faster glide than the sun''s beams,\n\nDriving back shadows over louring hills:\n\nTherefore do nimble-pinion''d doves draw love,\n\nAnd therefore hath the wind-swift Cupid wings.\n\nNow is the sun upon the highmost hill\n\nOf this day''s journey, and from nine till twelve\n\nIs three long hours, yet she is not come.\n\nHad she affections and warm youthful blood,\n\nShe would be as swift in motion as a ball;\n\nMy words would bandy her to my sweet love,\n\nAnd his to me:\n\nBut old folks, many feign as they were dead;\n\nUnwieldy, slow, heavy and pale as lead.\n\nO God, she comes!\n\nEnter Nurse and Peter\n\nO honey nurse, what news?\n\nHast thou met with him? Send thy man away.','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',0.0,'0:2:3e6a0a30',0);
INSERT OR REPLACE INTO cards VALUES('node-a840412a002fd6c9cabad73f479e69e25f5371e3','9y9ve',replace('**Benvolio**\n\nNot to his father''s; I spoke with his man.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',1.0,'0:2:3ee3e28f',0);
INSERT OR REPLACE INTO cards VALUES('node-25ab6039821d351806652a07ce9ed6739a136ca9','9y9ve',replace('**Apothecary**\n\nSuch mortal drugs I have; but Mantua''s law\n\nIs death to any he that utters them.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',9.0,'0:2:3f5e07d7',0);
INSERT OR REPLACE INTO cards VALUES('node-41cd70559da778b0ef003865b08705e39ee752de','9y9ve',replace('**Lady Capulet**\n\n                  O woful time!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',11.0,'0:2:3fa9cd3c',0);
INSERT OR REPLACE INTO cards VALUES('node-66f0d41492346b35e655b0d0c0f249ec23563e89','9y9ve',replace('**Benvolio**\n\nThen she hath sworn that she will still live chaste?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',88.0,'0:2:3fc159db',0);
INSERT OR REPLACE INTO cards VALUES('node-74a750c94c3749f1409276c2f96e3a5d356067d7','9y9ve',replace('**Friar Laurence**\n\nHold thy desperate hand:\n\nArt thou a man? thy form cries out thou art:\n\nThy tears are womanish; thy wild acts denote\n\nThe unreasonable fury of a beast:\n\nUnseemly woman in a seeming man!\n\nOr ill-beseeming beast in seeming both!\n\nThou hast amazed me: by my holy order,\n\nI thought thy disposition better temper''d.\n\nHast thou slain Tybalt? wilt thou slay thyself?\n\nAnd stay thy lady too that lives in thee,\n\nBy doing damned hate upon thyself?\n\nWhy rail''st thou on thy birth, the heaven, and earth?\n\nSince birth, and heaven, and earth, all three do meet\n\nIn thee at once; which thou at once wouldst lose.\n\nFie, fie, thou shamest thy shape, thy love, thy wit;\n\nWhich, like a usurer, abound''st in all,\n\nAnd usest none in that true use indeed\n\nWhich should bedeck thy shape, thy love, thy wit:\n\nThy noble shape is but a form of wax,\n\nDigressing from the valour of a man;\n\nThy dear love sworn but hollow perjury,\n\nKilling that love which thou hast vow''d to cherish;\n\nThy wit, that ornament to shape and love,\n\nMisshapen in the conduct of them both,\n\nLike powder in a skitless soldier''s flask,\n\nIs set afire by thine own ignorance,\n\nAnd thou dismember''d with thine own defence.\n\nWhat, rouse thee, man! thy Juliet is alive,\n\nFor whose dear sake thou wast but lately dead;\n\nThere art thou happy: Tybalt would kill thee,\n\nBut thou slew''st Tybalt; there are thou happy too:\n\nThe law that threaten''d death becomes thy friend\n\nAnd turns it to exile; there art thou happy:\n\nA pack of blessings lights up upon thy back;\n\nHappiness courts thee in her best array;\n\nBut, like a misbehaved and sullen wench,\n\nThou pout''st upon thy fortune and thy love:\n\nTake heed, take heed, for such die miserable.\n\nGo, get thee to thy love, as was decreed,\n\nAscend her chamber, hence and comfort her:\n\nBut look thou stay not till the watch be set,\n\nFor then thou canst not pass to Mantua;\n\nWhere thou shalt live, till we can find a time\n\nTo blaze your marriage, reconcile your friends,\n\nBeg pardon of the prince, and call thee back\n\nWith twenty hundred thousand times more joy\n\nThan thou went''st forth in lamentation.\n\nGo before, nurse: commend me to thy lady;\n\nAnd bid her hasten all the house to bed,\n\nWhich heavy sorrow makes them apt unto:\n\nRomeo is coming.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',31.0,'0:2:3fc9782c',0);
INSERT OR REPLACE INTO cards VALUES('node-4b3402a5c397e4a06fdd3e8300880f08c3d73b49','9y9ve',replace('**Prince**\n\nLook, and thou shalt see.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',50.0,'0:2:3fda0540',0);
INSERT OR REPLACE INTO cards VALUES('node-2d79646a7fd20f9f375b8c4f2638b9f52545d78f','9y9ve',replace('**Mercutio**\n\nCome between us, good Benvolio; my wits faint.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',28.0,'0:2:3fef30a6',0);
INSERT OR REPLACE INTO cards VALUES('node-580f24bbfcc5bf45b40e44705e79ecdee4cb501f','9y9ve',replace('**Prince**\n\nWhere are the vile beginners of this fray?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',51.0,'0:2:400734ea',0);
INSERT OR REPLACE INTO cards VALUES('node-5c1d30a8d53bf08d19a86ef2cf9adbfd2f1ac4ff','9y9ve',replace('**Juliet**\n\nYon light is not day-light, I know it, I:\n\nIt is some meteor that the sun exhales,\n\nTo be to thee this night a torch-bearer,\n\nAnd light thee on thy way to Mantua:\n\nTherefore stay yet; thou need''st not to be gone.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',2.0,'0:2:4028a37d',0);
INSERT OR REPLACE INTO cards VALUES('node-a34ca05cc997dee608ea944e1d207b7ec8634ae9','9y9ve',replace('**Balthasar**\n\nFull half an hour.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',22.0,'0:2:40451c81',0);
INSERT OR REPLACE INTO cards VALUES('node-fd16800fe7f33f4977ab396318655bbeb0a6e670','9y9ve',replace('**Mercutio**\n\nThat dreamers often lie.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',20.0,'0:2:408cc689',0);
INSERT OR REPLACE INTO cards VALUES('node-b596c476abb6bd3a607ac9ff4835b29b303cabc7','9y9ve',replace('**Benvolio**\n\nBe ruled by me, forget to think of her.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',90.0,'0:2:41f9cb7a',0);
INSERT OR REPLACE INTO cards VALUES('node-4b71245b2fc34364bcd9f2a9352f2fffac56092f','9y9ve',replace('**Capulet**\n\nAs rich shall Romeo''s by his lady''s lie;\n\nPoor sacrifices of our enmity!','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',63.0,'0:2:4209a62b',0);
INSERT OR REPLACE INTO cards VALUES('node-7d4dafeb25e50575b2fe4e3c20f4d4b0fc24d967','9y9ve',replace('**Nurse**\n\nO God''s lady dear!\n\nAre you so hot? marry, come up, I trow;\n\nIs this the poultice for my aching bones?\n\nHenceforward do your messages yourself.','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',13.0,'0:2:4213e888',0);
INSERT OR REPLACE INTO cards VALUES('node-6d612d174c06a4abf5a0b3d85d5fcc49ae9b4048','9y9ve',replace('**Nurse**\n\nI saw the wound, I saw it with mine eyes,\n\nGod save the mark! here on his manly breast:\n\nA piteous corse, a bloody piteous corse;\n\nPale, pale as ashes, all bedaub''d in blood,\n\nAll in gore-blood; I swounded at the sight.','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',7.0,'0:2:42c1b451',0);
INSERT OR REPLACE INTO cards VALUES('node-5b0d4e18702e3c9ca3e9f31c7e94388bfc83750c','9y9ve',replace('**Paris**\n\nMy lord, I would that Thursday were to-morrow.','\n',char(10)),'node-1ea0a4f951159826ef795e37aa3321911ddb6685',6.0,'0:2:43049eb2',0);
INSERT OR REPLACE INTO cards VALUES('node-ea16a8874f6ac631e6432e7ce95b412b9dd03e90','9y9ve',replace('**Mercutio**\n\nThis cannot anger him: ''twould anger him\n\nTo raise a spirit in his mistress'' circle\n\nOf some strange nature, letting it there stand\n\nTill she had laid it and conjured it down;\n\nThat were some spite: my invocation\n\nIs fair and honest, and in his mistres s'' name\n\nI conjure only but to raise up him.','\n',char(10)),'node-5e06ac8ec784ae358a0c61c64cf0e889bc3d30ef',6.0,'0:2:43ac5bde',0);
INSERT OR REPLACE INTO cards VALUES('node-ac495250d488fb99402f68980294dc6b6958a713','9y9ve',replace('**Paris**\n\nGive me thy torch, boy: hence, and stand aloof:\n\nYet put it out, for I would not be seen.\n\nUnder yond yew-trees lay thee all along,\n\nHolding thine ear close to the hollow ground;\n\nSo shall no foot upon the churchyard tread,\n\nBeing loose, unfirm, with digging up of graves,\n\nBut thou shalt hear it: whistle then to me,\n\nAs signal that thou hear''st something approach.\n\nGive me those flowers. Do as I bid thee, go.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',0.0,'0:2:43e251ef',0);
INSERT OR REPLACE INTO cards VALUES('node-cd59e8506562be5e477ed6f3f414cef18d81724b','9y9ve',replace('**Capulet**\n\nThings have fall''n out, sir, so unluckily,\n\nThat we have had no time to move our daughter:\n\nLook you, she loved her kinsman Tybalt dearly,\n\nAnd so did I: Well, we were born to die.\n\n''Tis very late, she''ll not come down to-night:\n\nI promise you, but for your company,\n\nI would have been a-bed an hour ago.','\n',char(10)),'node-1ea0a4f951159826ef795e37aa3321911ddb6685',0.0,'0:2:43fe3582',0);
INSERT OR REPLACE INTO cards VALUES('node-693bb283de8bd5fc2534456cd521f9b848710845','9y9ve',replace('**Romeo**\n\nThat last is true; the sweeter rest was mine.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',3.0,'0:2:4561641a',0);
INSERT OR REPLACE INTO cards VALUES('node-b7217864c42e56905969645684b6fbbc8219825c','9y9ve',replace('**Friar Laurence**\n\nBliss be upon you! Tell me, good my friend,\n\nWhat torch is yond, that vainly lends his light\n\nTo grubs and eyeless skulls? as I discern,\n\nIt burneth in the Capel''s monument.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',17.0,'0:2:46546d5a',0);
INSERT OR REPLACE INTO cards VALUES('node-18673b09735932fad12c53974fc0e633d481f9fe','9y9ve',replace('**Juliet**\n\nO, swear not by the moon, the inconstant moon,\n\nThat monthly changes in her circled orb,\n\nLest that thy love prove likewise variable.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',21.0,'0:2:465ac9b9',0);
INSERT OR REPLACE INTO cards VALUES('node-e104e4b2684105c8bc53ed9b7161c6bb2ccbe5d1','9y9ve',replace('**Juliet**\n\nLove give me strength! and strength shall help afford.\n\nFarewell, dear father!\n\nExeunt','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',32.0,'0:2:46775c0a',0);
INSERT OR REPLACE INTO cards VALUES('node-934bf2773fd62f572020bab9465c8abc72611933','9y9ve',replace('**Mercutio**\n\nMen''s eyes were made to look, and let them gaze;\n\nI will not budge for no man''s pleasure, I.\n\n```\nEnter Romeo\n```\n\n','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',17.0,'0:2:46b3ce85',0);
INSERT OR REPLACE INTO cards VALUES('node-f4ea1c8a6a33e2b20c7a4c4fcf9eb63be2fc5b6b','9y9ve',replace('**Benvolio**\n\nThe what?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',11.0,'0:2:47cb4808',0);
INSERT OR REPLACE INTO cards VALUES('node-0e24f57c795042361dd78255e453d3ac29a19a7a','9y9ve',replace('**Nurse**\n\nHave you got leave to go to shrift to-day?','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',15.0,'0:2:47d867a1',0);
INSERT OR REPLACE INTO cards VALUES('node-997bd37dcb113dd62dda9674d7801df4bc4b6b78','9y9ve',replace('**Nurse**\n\nSee where she comes from shrift with merry look.\n\n```\nEnter Juliet\n```\n\n','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',7.0,'0:2:48079b67',0);
