INSERT OR REPLACE INTO cards VALUES('node-ee4adbaca206f017b6945714b5b7f8a24fbee484','9y9ve',replace('**Juliet**\n\nO think''st thou we shall ever meet again?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',13.0,'0:2:48817ef8',0);
INSERT OR REPLACE INTO cards VALUES('node-89b0d5be85f69d756c67e07cf8f9a31b36a5dc08','9y9ve',replace('**Capulet**\n\nA jealous hood, a jealous hood!\n\nEnter three or four Servingmen, with spits, logs, and baskets\n\nNow, fellow,\n\nWhat''s there?','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',6.0,'0:2:48b2efb1',0);
INSERT OR REPLACE INTO cards VALUES('node-a8072fe034866460aeabec2f77080fd7908508bb','9y9ve',replace('**Peter**\n\nO, musicians, because my heart itself plays ''My\n\nheart is full of woe:'' O, play me some merry dump,\n\nto comfort me.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',28.0,'0:2:4990706a',0);
INSERT OR REPLACE INTO cards VALUES('node-c97bd04136415c390edade1f4ba90e6b87686a8a','9y9ve',replace('**Friar Laurence**\n\nFor doting, not for loving, pupil mine.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',12.0,'0:2:49995cfd',0);
INSERT OR REPLACE INTO cards VALUES('node-8a1954d051376656ce93765ba2bec37355a6a187','9y9ve',replace('**First Servant**\n\nAway with the joint-stools, remove the\n\ncourt-cuptree, look to the plate. Good thou, save\n\nme a piece of marchpane; and, as thou lovest me, let\n\nthe porter let in Susan Grindstone and Nell.\n\nAntony, and Potpan!','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',2.0,'0:2:49a6b076',0);
INSERT OR REPLACE INTO cards VALUES('node-a34192daab92ccc76c78c6c8e305c3dbbbb12667','9y9ve',replace('**Second Watchman**\n\nHere''s Romeo''s man; we found him in the churchyard.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',35.0,'0:2:49b44949',0);
INSERT OR REPLACE INTO cards VALUES('node-9349377c476ed532295fc383f7598879ac4f0bc0','9y9ve',replace('**Romeo**\n\nAlive, in triumph! and Mercutio slain!\n\nAway to heaven, respective lenity,\n\nAnd fire-eyed fury be my conduct now!\n\nRe-enter Tybalt\n\nNow, Tybalt, take the villain back again,\n\nThat late thou gavest me; for Mercutio''s soul\n\nIs but a little way above our heads,\n\nStaying for thine to keep him company:\n\nEither thou, or I, or both, must go with him.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',42.0,'0:2:49cfe24f',0);
INSERT OR REPLACE INTO cards VALUES('node-210643707cc4d4f3cf98a9e575e6f2cf5bbcd445','9y9ve',replace('**Romeo**\n\nThou canst not speak of that thou dost not feel:\n\nWert thou as young as I, Juliet thy love,\n\nAn hour but married, Tybalt murdered,\n\nDoting like me and like me banished,\n\nThen mightst thou speak, then mightst thou tear thy hair,\n\nAnd fall upon the ground, as I do now,\n\nTaking the measure of an unmade grave.\n\nKnocking within','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',17.0,'0:2:4a0665d0',0);
INSERT OR REPLACE INTO cards VALUES('node-41af6bfe9134ed5c95637eef72500063f95404f0','9y9ve',replace('**Romeo**\n\nFarewell, farewell! one kiss, and I''ll descend.\n\nHe goeth down','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',10.0,'0:2:4a43367c',0);
INSERT OR REPLACE INTO cards VALUES('node-1bba535b5b4b25aac773bdc37b53737ae15ddf8a','9y9ve',replace('**Lady Capulet**\n\nThe people in the street cry Romeo,\n\nSome Juliet, and some Paris; and all run,\n\nWith open outcry toward our monument.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',41.0,'0:2:4a5ffbab',0);
INSERT OR REPLACE INTO cards VALUES('node-2dc96b32d6ef8bf604e8551ee26f3df5215af958','9y9ve',replace('**Paris**\n\nThou wrong''st it, more than tears, with that report.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',18.0,'0:2:4a772ea7',0);
INSERT OR REPLACE INTO cards VALUES('node-8bea7bd55c63c2f70a9e459c21a90df4d3b34e9b','9y9ve',replace('**Montague**\n\nI would thou wert so happy by thy stay,\n\nTo hear true shrift. Come, madam, let''s away.\n\nExeunt Montague and Lady Montague','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',61.0,'0:2:4a9f611',0);
INSERT OR REPLACE INTO cards VALUES('node-147ac7fdb25b4e53dc069961f5108649521918d3','9y9ve',replace('**Nurse**\n\nNow, afore God, I am so vexed, that every part about\n\nme quivers. Scurvy knave! Pray you, sir, a word:\n\nand as I told you, my young lady bade me inquire you\n\nout; what she bade me say, I will keep to myself:\n\nbut first let me tell ye, if ye should lead her into\n\na fool''s paradise, as they say, it were a very gross\n\nkind of behavior, as they say: for the gentlewoman\n\nis young; and, therefore, if you should deal double\n\nwith her, truly it were an ill thing to be offered\n\nto any gentlewoman, and very weak dealing.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',71.0,'0:2:4ae02478',0);
INSERT OR REPLACE INTO cards VALUES('node-657cf2e75895a515ccf00a0b2e902bb7e05a6861','9y9ve',replace('**Romeo**\n\nAnd is it not well served in to a sweet goose?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',35.0,'0:2:4b192e41',0);
INSERT OR REPLACE INTO cards VALUES('node-a028c60f77c34bab457e5b67f0be006e0d281e97','9y9ve',replace('**Gregory**\n\nNo, marry; I fear thee!','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',21.0,'0:2:4b2e2ef9',0);
INSERT OR REPLACE INTO cards VALUES('node-6d0f09dd156350f696d131c3e1dace68994eec61','9y9ve',replace('**Nurse**\n\nWeeping and wailing over Tybalt''s corse:\n\nWill you go to them? I will bring you thither.','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',19.0,'0:2:4b84e94b',0);
INSERT OR REPLACE INTO cards VALUES('node-6b691f7e38654ff067442656af25ac273506af07','9y9ve',replace('**Benvolio**\n\nTybalt, the kinsman of old Capulet,\n\nHath sent a letter to his father''s house.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',3.0,'0:2:4bd1c2f5',0);
INSERT OR REPLACE INTO cards VALUES('node-3e8055a991db9604b31fed2a209d86bed7a94b7a','9y9ve',replace('**Romeo**\n\nA right good mark-man! And she''s fair I love.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',85.0,'0:2:4cce661a',0);
INSERT OR REPLACE INTO cards VALUES('node-5d8290ca059a662c5afdc86d6253b83061d7be25','9y9ve',replace('**Juliet**\n\nBut to be frank, and give it thee again.\n\nAnd yet I wish but for the thing I have:\n\nMy bounty is as boundless as the sea,\n\nMy love as deep; the more I give to thee,\n\nThe more I have, for both are infinite.\n\nNurse calls within\n\nI hear some noise within; dear love, adieu!\n\nAnon, good nurse! Sweet Montague, be true.\n\nStay but a little, I will come again.\n\nExit, above','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',31.0,'0:2:4cd36619',0);
INSERT OR REPLACE INTO cards VALUES('node-a94bbca8b82621a3431497c35256eacdaead4acf','9y9ve',replace('**Capulet**\n\nMy sword, I say! Old Montague is come,\n\nAnd flourishes his blade in spite of me.\n\n```\nEnter Montague and Lady Montague\n```','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',47.0,'0:2:4d0bf03c',0);
INSERT OR REPLACE INTO cards VALUES('node-80d41745e6edea26d2184453039ff16d1c0ea4ec','9y9ve',replace('**Juliet**\n\nHow art thou out of breath, when thou hast breath\n\nTo say to me that thou art out of breath?\n\nThe excuse that thou dost make in this delay\n\nIs longer than the tale thou dost excuse.\n\nIs thy news good, or bad? answer to that;\n\nSay either, and I''ll stay the circumstance:\n\nLet me be satisfied, is''t good or bad?','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',6.0,'0:2:4e0e2aa0',0);
INSERT OR REPLACE INTO cards VALUES('node-a3c3a6c5471909543487a8227efe5a3ae196df3a','9y9ve',replace('**Romeo**\n\nAy me! sad hours seem long.\n\nWas that my father that went hence so fast?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',65.0,'0:2:4e8d1fba',0);
INSERT OR REPLACE INTO cards VALUES('node-9bf538aa0edb75fe00fd3b3d4043a6b64e79c402','9y9ve',replace('**Mercutio**\n\nConsort! what, dost thou make us minstrels? an\n\nthou make minstrels of us, look to hear nothing but\n\ndiscords: here''s my fiddlestick; here''s that shall\n\nmake you dance. ''Zounds, consort!','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',15.0,'0:2:4ea6b1b2',0);
INSERT OR REPLACE INTO cards VALUES('node-891560f16a5ebcfde76efa407e59849db8360111','9y9ve',replace('**Lady Capulet**\n\nEnough of this; I pray thee, hold thy peace.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',11.0,'0:2:4f27ef02',0);
INSERT OR REPLACE INTO cards VALUES('node-0b15c6e143bc5e85aeeeb5f76389653012d9416e','9y9ve',replace('**Friar Laurence**\n\nSaint Francis be my speed! how oft to-night\n\nHave my old feet stumbled at graves! Who''s there?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',15.0,'0:2:4f3da68c',0);
INSERT OR REPLACE INTO cards VALUES('node-53bf78081c0f71bb96e95ee730495b8ef61ec9c3','9y9ve',replace('**Mercutio**\n\nGod ye good den, fair gentlewoman.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',51.0,'0:2:4f6ba9d9',0);
INSERT OR REPLACE INTO cards VALUES('node-2aaaa62e7fbee761edf26c18812a56983f000182','9y9ve',replace('**Mercutio**\n\nNay, gentle Romeo, we must have you dance.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',3.0,'0:2:4f6c2459',0);
INSERT OR REPLACE INTO cards VALUES('node-3f5c765fb022b7b009bd40b8212bbe302a0968d2','9y9ve',replace('**Mercutio**\n\nAny man that can write may answer a letter.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',6.0,'0:2:4fe3328',0);
INSERT OR REPLACE INTO cards VALUES('node-985b5e1afe0f14630900330e0f577d668e9b51e1','9y9ve',replace('**Nurse**\n\nAnd from my soul too;\n\nOr else beshrew them both.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',62.0,'0:2:505740f',0);
INSERT OR REPLACE INTO cards VALUES('node-4f1c43e022ac59abfe5ae37781658be58ac0a02f','9y9ve',replace('**Gregory**\nAy, while you live, draw your neck out o'' the collar.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',3.0,'0:2:5062a05d',0);
INSERT OR REPLACE INTO cards VALUES('node-b5a6ea0e4e70b9e91a103de1895202e973888103','9y9ve',replace('**Benvolio**\n\nHere were the servants of your adversary,\n\nAnd yours, close fighting ere I did approach:\n\nI drew to part them: in the instant came\n\nThe fiery Tybalt, with his sword prepared,\n\nWhich, as he breathed defiance to my ears,\n\nHe swung about his head and cut the winds,\n\nWho nothing hurt withal hiss''d him in scorn:\n\nWhile we were interchanging thrusts and blows,\n\nCame more and more and fought on part and part,\n\nTill the prince came, who parted either part.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',52.0,'0:2:50843a2f',0);
INSERT OR REPLACE INTO cards VALUES('node-af8671ce8a3aafed639d01d95bebdfce998fb670','9y9ve',replace('**Juliet**\n\n''Tis but thy name that is my enemy;\n\nThou art thyself, though not a Montague.\n\nWhat''s Montague? it is nor hand, nor foot,\n\nNor arm, nor face, nor any other part\n\nBelonging to a man. O, be some other name!\n\nWhat''s in a name? that which we call a rose\n\nBy any other name would smell as sweet;\n\nSo Romeo would, were he not Romeo call''d,\n\nRetain that dear perfection which he owes\n\nWithout that title. Romeo, doff thy name,\n\nAnd for that name which is no part of thee\n\nTake all myself.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',5.0,'0:2:509b1e11',0);
INSERT OR REPLACE INTO cards VALUES('node-b7ee11f61471a13201f9aea764c9d21f5de3f50b','9y9ve',replace('**Servant**\n\nFind them out whose names are written here! It is\n\nwritten, that the shoemaker should meddle with his\n\nyard, and the tailor with his last, the fisher with\n\nhis pencil, and the painter with his nets; but I am\n\nsent to find those persons whose names are here\n\nwrit, and can never find what names the writing\n\nperson hath here writ. I must to the learned. In good time.\n\nEnter Benvolio and Romeo','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',5.0,'0:2:50b1f341',0);
INSERT OR REPLACE INTO cards VALUES('node-6cfe1c69eb2e34ef86230404153fb8047f9858b8','9y9ve',replace('**Benvolio**\n\nAt thy good heart''s oppression.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',76.0,'0:2:50d76e93',0);
INSERT OR REPLACE INTO cards VALUES('node-486df2dc182c9b6b389296f7881224474112223a','9y9ve',replace('**Juliet**\n\nNot proud, you have; but thankful, that you have:\n\nProud can I never be of what I hate;\n\nBut thankful even for hate, that is meant love.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',44.0,'0:2:514386a4',0);
INSERT OR REPLACE INTO cards VALUES('node-f30a5dd46a4423636c50a7b425222d15abbbdfd7','9y9ve',replace('**Juliet**\n\nO God, I have an ill-divining soul!\n\nMethinks I see thee, now thou art below,\n\nAs one dead in the bottom of a tomb:\n\nEither my eyesight fails, or thou look''st pale.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',15.0,'0:2:514a070',0);
INSERT OR REPLACE INTO cards VALUES('node-89b6f9d3fe6d26ac2716aa3ec032ed40898561f4','9y9ve',replace('**Gregory**\n\nThat shows thee a weak slave; for the weakest goes\n\nto the wall.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',9.0,'0:2:51aabfc',0);
INSERT OR REPLACE INTO cards VALUES('node-01eb6c8295af1863f98d8a2e21c18551ce5aae2b','9y9ve',replace('**Paris**\n\nThis is that banish''d haughty Montague,\n\nThat murder''d my love''s cousin, with which grief,\n\nIt is supposed, the fair creature died;\n\nAnd here is come to do some villanous shame\n\nTo the dead bodies: I will apprehend him.\n\nComes forward\n\nStop thy unhallow''d toil, vile Montague!\n\nCan vengeance be pursued further than death?\n\nCondemned villain, I do apprehend thee:\n\nObey, and go with me; for thou must die.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',8.0,'0:2:51bad860',0);
INSERT OR REPLACE INTO cards VALUES('node-99a448a11c27f9b7f0fd167cf5b52d393d2a03a2','9y9ve',replace('**Romeo**\n\nFather, what news? what is the prince''s doom?\n\nWhat sorrow craves acquaintance at my hand,\n\nThat I yet know not?','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',1.0,'0:2:5259467a',0);
INSERT OR REPLACE INTO cards VALUES('node-bb4a5d6aa6a3e15f9506dde6da7885510dfb5cf9','9y9ve',replace('**Nurse**\n\nPeter, stay at the gate.\n\nExit Peter','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',1.0,'0:2:528f644f',0);
INSERT OR REPLACE INTO cards VALUES('node-1e72643d178e8b1ff67a683684dc1b6cbc784a30','9y9ve',replace('**Benvolio**\n\nRomeo will answer it.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',5.0,'0:2:52e241e4',0);
INSERT OR REPLACE INTO cards VALUES('node-ea722d296eb1783acdc8ee9260f1cabcde7bf3c8','9y9ve',replace('**Juliet**\n\nYea, noise? then I''ll be brief. O happy dagger!\n\nSnatching Romeo''s dagger\n\nThis is thy sheath;\n\nStabs herself\n\nthere rust, and let me die.\n\nFalls on Romeo''s body, and dies\n\nEnter Watch, with the Page of Paris','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',32.0,'0:2:5305096a',0);
INSERT OR REPLACE INTO cards VALUES('node-3d3d4cb2c99abaad80fb8a3b29555d79c3fb6141','9y9ve',replace('**Capulet**\n\nWelcome, gentlemen! ladies that have their toes\n\nUnplagued with corns will have a bout with you.\n\nAh ha, my mistresses! which of you all\n\nWill now deny to dance? she that makes dainty,\n\nShe, I''ll swear, hath corns; am I come near ye now?\n\nWelcome, gentlemen! I have seen the day\n\nThat I have worn a visor and could tell\n\nA whispering tale in a fair lady''s ear,\n\nSuch as would please: ''tis gone, ''tis gone, ''tis gone:\n\nYou are welcome, gentlemen! come, musicians, play.\n\nA hall, a hall! give room! and foot it, girls.\n\nMusic plays, and they dance\n\nMore light, you knaves; and turn the tables up,\n\nAnd quench the fire, the room is grown too hot.\n\nAh, sirrah, this unlook''d-for sport comes well.\n\nNay, sit, nay, sit, good cousin Capulet;\n\nFor you and I are past our dancing days:\n\nHow long is''t now since last yourself and I\n\nWere in a mask?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',6.0,'0:2:536851a1',0);
INSERT OR REPLACE INTO cards VALUES('node-4980c28931ee6228f6cc66d7f325cf99afaacf74','9y9ve',replace('**Mercutio**\n\nA challenge, on my life.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',4.0,'0:2:537b0f7f',0);
INSERT OR REPLACE INTO cards VALUES('node-d3ba2d30ebb3a3f8a1ea6390782c7cfd0bffe795','9y9ve',replace('**Nurse**\n\nJesu, what haste? can you not stay awhile?\n\nDo you not see that I am out of breath?','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',5.0,'0:2:54d66b2a',0);
INSERT OR REPLACE INTO cards VALUES('node-f95f55b0bc86bbd9b4196c4576e46fc0af1da274','9y9ve',replace('**Juliet**\n\nRomeo!','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',43.0,'0:2:55a5b',0);
INSERT OR REPLACE INTO cards VALUES('node-6469ee8da883e02745455fe2dc7f792ac05acb59','9y9ve',replace('**Mercutio**\n\nThe pox of such antic, lisping, affecting\n\nfantasticoes; these new tuners of accents! ''By Jesu,\n\na very good blade! a very tall man! a very good\n\nwhore!'' Why, is not this a lamentable thing,\n\ngrandsire, that we should be thus afflicted with\n\nthese strange flies, these fashion-mongers, these\n\nperdona-mi''s, who stand so much on the new form,\n\nthat they cannot at ease on the old bench? O, their\n\nbones, their bones!\n\n```\nEnter Romeo\n```\n\n','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',12.0,'0:2:55b10f5d',0);
INSERT OR REPLACE INTO cards VALUES('node-f0cd8a2aa929f8eaff9b39c2530c4b50a2772ade','9y9ve',replace('**Capulet**\n\nGo to, go to;\n\nYou are a saucy boy: is''t so, indeed?\n\nThis trick may chance to scathe you, I know what:\n\nYou must contrary me! marry, ''tis time.\n\nWell said, my hearts! You are a princox; go:\n\nBe quiet, or more light, more light! For shame!\n\nI''ll make you quiet. What, cheerly, my hearts!','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',23.0,'0:2:55bc53fd',0);
INSERT OR REPLACE INTO cards VALUES('node-47912ba7053dd15376b6d8ab59d454961454118b','9y9ve',replace('**Nurse**\n\nWell, sir; my mistress is the sweetest lady, Lord,\n\nLord! when ''twas a little prating thing: O, there\n\nis a nobleman in town, one Paris, that would fain\n\nlay knife atree; but she, good soul, had as lief\n\nsee a toad, a very toad, as see him. I anger her\n\nsometimes and tell her that Paris is the properer\n\nman; but, I''ll warrant you, when I say so, she looks\n\nas pale as any clout in the versal world. Doth not\n\nrosemary and Romeo begin both with a letter?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',85.0,'0:2:5649e64b',0);
INSERT OR REPLACE INTO cards VALUES('node-8bf87bd8ae232ccdd61e0f71745d1dc16333a8b9','9y9ve',replace('**Juliet**\n\nCome hither, nurse. What is yond gentleman?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',42.0,'0:2:56d77a30',0);
INSERT OR REPLACE INTO cards VALUES('node-fba85a493a99273b035d4716c96977fcb406731e','9y9ve',replace('**Romeo**\n\n```\nTo Juliet\n```\n\nIf I profane with my unworthiest hand\n\nThis holy shrine, the gentle fine is this:\n\nMy lips, two blushing pilgrims, ready stand\n\nTo smooth that rough touch with a tender kiss.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',25.0,'0:2:5713b4bc',0);
INSERT OR REPLACE INTO cards VALUES('node-9329bb18f7939501df7c42a76c9cee9813ec2513','9y9ve',replace('**Romeo**\n\nAmen, amen! but come what sorrow can,\n\nIt cannot countervail the exchange of joy\n\nThat one short minute gives me in her sight:\n\nDo thou but close our hands with holy words,\n\nThen love-devouring death do what he dare;\n\nIt is enough I may but call her mine.','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',1.0,'0:2:576558e1',0);
INSERT OR REPLACE INTO cards VALUES('node-bf67eaffcc7d3fe7e44317237a019c057695d6ea','9y9ve',replace('**Balthasar**\n\nAs I did sleep under this yew-tree here,\n\nI dreamt my master and another fought,\n\nAnd that my master slew him.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',26.0,'0:2:57695bf9',0);
INSERT OR REPLACE INTO cards VALUES('node-f560c2fb7e4a0ca25bf9addb5c4a043d533faf39','9y9ve',replace('**Juliet**\n\nO serpent heart, hid with a flowering face!\n\nDid ever dragon keep so fair a cave?\n\nBeautiful tyrant! fiend angelical!\n\nDove-feather''d raven! wolvish-ravening lamb!\n\nDespised substance of divinest show!\n\nJust opposite to what thou justly seem''st,\n\nA damned saint, an honourable villain!\n\nO nature, what hadst thou to do in hell,\n\nWhen thou didst bower the spirit of a fiend\n\nIn moral paradise of such sweet flesh?\n\nWas ever book containing such vile matter\n\nSo fairly bound? O that deceit should dwell\n\nIn such a gorgeous palace!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',14.0,'0:2:57b2fe68',0);
INSERT OR REPLACE INTO cards VALUES('node-6db08081f01c2ed5265db4c3c71dd5fac8918017','9y9ve',replace('**Peter**\n\nAnon!','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',90.0,'0:2:57f1cc8c',0);
INSERT OR REPLACE INTO cards VALUES('node-06727dc856d49cae0c8d21407db1b991cb378d05','9y9ve',replace('**Romeo**\n\nWouldst thou withdraw it? for what purpose, love?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',30.0,'0:2:58775784',0);
INSERT OR REPLACE INTO cards VALUES('node-386bf122e54c47de4c15cdc76587c1017ed1b1ac','9y9ve',replace('**Friar John**\n\nGoing to find a bare-foot brother out\n\nOne of our order, to associate me,\n\nHere in this city visiting the sick,\n\nAnd finding him, the searchers of the town,\n\nSuspecting that we both were in a house\n\nWhere the infectious pestilence did reign,\n\nSeal''d up the doors, and would not let us forth;\n\nSo that my speed to Mantua there was stay''d.','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',2.0,'0:2:58922028',0);
INSERT OR REPLACE INTO cards VALUES('node-8fd0d05aebd71a163f7e60de2e6ca9a3653ee9ab','9y9ve',replace('**Nurse**\n\nI''ll lay fourteen of my teeth,\n\nAnd yet, to my teeth be it spoken, I have but four\n\nShe is not fourteen. How long is it now\n\nTo Lammas-tide?','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',8.0,'0:2:59370aaf',0);
INSERT OR REPLACE INTO cards VALUES('node-96ce69cde607a5517ba7fb26c3d239f2e5296fc5','9y9ve',replace('**Romeo**\n\nSo thrive my soul','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',38.0,'0:2:599f2433',0);
INSERT OR REPLACE INTO cards VALUES('node-9adaff8ffe1909cb8c5330071699a78b870b4e17','9y9ve',replace('**Benvolio**\n\nI aim''d so near, when I supposed you loved.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',84.0,'0:2:59dad342',0);
INSERT OR REPLACE INTO cards VALUES('node-ee323cbf9ba68b9f4c31e9cd3e398f89281cdc22','9y9ve',replace('**Friar Laurence**\n\nRomeo shall thank thee, daughter, for us both.','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',4.0,'0:2:59f08e2c',0);
INSERT OR REPLACE INTO cards VALUES('node-53e81a948649927dd8900f79226ef9150e6568c5','9y9ve',replace('**First Musician**\n\nThen I will give you the serving-creature.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',35.0,'0:2:59f9a91f',0);
INSERT OR REPLACE INTO cards VALUES('node-840608be8cd490621ff8d5aa804be076b54718e5','9y9ve',replace('**Nurse**\n\nNow, by my maidenhead, at twelve year old,\n\nI bade her come. What, lamb! what, ladybird!\n\nGod forbid! Where''s this girl? What, Juliet!\n\n```\nEnter Juliet\n```\n\n','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',1.0,'0:2:5a75f8cf',0);
INSERT OR REPLACE INTO cards VALUES('node-8b9d3fd80001c3ebe378bfa9ae31ae893f0d2773','9y9ve',replace('**Tybalt**\n\nWhat, art thou drawn among these heartless hinds?\n\nTurn thee, Benvolio, look upon thy death.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',41.0,'0:2:5afefaed',0);
INSERT OR REPLACE INTO cards VALUES('node-514da5b18f2299b32f8db5efbfaf6aacd717351b','9y9ve',replace('**Prince**\n\nA glooming peace this morning with it brings;\n\nThe sun, for sorrow, will not show his head:\n\nGo hence, to have more talk of these sad things;\n\nSome shall be pardon''d, and some punished:\n\nFor never was a story of more woe\n\nThan this of Juliet and her Romeo.\n\n```\nExeunt\n```\n\n','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',64.0,'0:2:5b7dca1d',0);
INSERT OR REPLACE INTO cards VALUES('node-c09672cad442779f2c4f8ff877de7afa9e5c802d','9y9ve',replace('**Romeo**\n\nIs love a tender thing? it is too rough,\n\nToo rude, too boisterous, and it pricks like thorn.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',8.0,'0:2:5ba65375',0);
INSERT OR REPLACE INTO cards VALUES('node-e42c8b199ef8d4b9bfd1cfb51059180a253f8fa9','9y9ve',replace('**Romeo**\n\nCourage, man; the hurt cannot be much.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',34.0,'0:2:5bccf360',0);
INSERT OR REPLACE INTO cards VALUES('node-e24422aeeea4820d34a12a10f7611b1454e3a8a2','9y9ve',replace('**Lady Capulet**\n\nGood night:\n\nGet thee to bed, and rest; for thou hast need.\n\nExeunt Lady Capulet and Nurse','\n',char(10)),'node-5a5e52ac71bf638baf35f7e8ff54a68376bf2aa7',3.0,'0:2:5c1ca03b',0);
INSERT OR REPLACE INTO cards VALUES('node-2bbd840496790949cbbce7ef83bf6679a1d613ac','9y9ve',replace('**Romeo**\n\nPeace, peace, Mercutio, peace!\n\nThou talk''st of nothing.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',23.0,'0:2:5cfa9c0a',0);
INSERT OR REPLACE INTO cards VALUES('node-7983ab6165cb3578caf6b7b1321d9140c23747ea','9y9ve',replace('**Balthasar**\n\nI do beseech you, sir, have patience:\n\nYour looks are pale and wild, and do import\n\nSome misadventure.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',3.0,'0:2:5d3397fa',0);
INSERT OR REPLACE INTO cards VALUES('node-ac24c11a4abcd4a79adf213ad462c35807caedec','9y9ve',replace('**Juliet**\n\nI would thou hadst my bones, and I thy news:\n\nNay, come, I pray thee, speak; good, good nurse, speak.','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',4.0,'0:2:5d7b0072',0);
INSERT OR REPLACE INTO cards VALUES('node-c0c6d85ca9cd300a52f7836af5135894c28e79fd','9y9ve',replace('**Juliet**\n\nHere''s such a coil! come, what says Romeo?','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',14.0,'0:2:5e1a279a',0);
INSERT OR REPLACE INTO cards VALUES('node-7ff7bae7cbaa7a6220f625d2cc8d4e563ecf40d0','9y9ve',replace('**Mercutio**\n\nNay, if thy wits run the wild-goose chase, I have\n\ndone, for thou hast more of the wild-goose in one of\n\nthy wits than, I am sure, I have in my whole five:\n\nwas I with you there for the goose?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',30.0,'0:2:5e5a9ce7',0);
INSERT OR REPLACE INTO cards VALUES('node-ad15ecbdd68b4c2cd411b8747967a38312e4c63f','9y9ve',replace('**Sampson**\n\nGregory, o'' my word, we''ll not carry coals.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',0.0,'0:2:5ee7f0ef',0);
INSERT OR REPLACE INTO cards VALUES('node-854462662262a9dddf20e94303465d444c73db5c','9y9ve',replace('**Juliet**\n\nO comfortable friar! where is my lord?\n\nI do remember well where I should be,\n\nAnd there I am. Where is my Romeo?\n\nNoise within','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',28.0,'0:2:5fd8ef73',0);
INSERT OR REPLACE INTO cards VALUES('node-e2e6ea9b94f5fa837d5df00c351a16d8e2c5ff2c','9y9ve',replace('**Juliet**\n\nI gave thee mine before thou didst request it:\n\nAnd yet I would it were to give again.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',29.0,'0:2:5ffedd2b',0);
INSERT OR REPLACE INTO cards VALUES('node-890c1b9735d29c5f7e7ced9f9586c9a70a68476a','9y9ve',replace('**Romeo**\n\nShe hath, and in that sparing makes huge waste,\n\nFor beauty starved with her severity\n\nCuts beauty off from all posterity.\n\nShe is too fair, too wise, wisely too fair,\n\nTo merit bliss by making me despair:\n\nShe hath forsworn to love, and in that vow\n\nDo I live dead that live to tell it now.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',89.0,'0:2:600cbc2a',0);
INSERT OR REPLACE INTO cards VALUES('node-9b4a57bab71660c7f6bbc7f465d6ecfc832d38ec','9y9ve',replace('**Tybalt**\n\nBoy, this shall not excuse the injuries\n\nThat thou hast done me; therefore turn and draw.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',22.0,'0:2:60177f72',0);
INSERT OR REPLACE INTO cards VALUES('node-6b16c3753f683092f604fb8b469cafe4d67f5f15','9y9ve',replace('**Lady Capulet**\n\nMarry, my child, early next Thursday morn,\n\nThe gallant, young and noble gentleman,\n\nThe County Paris, at Saint Peter''s Church,\n\nShall happily make thee there a joyful bride.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',38.0,'0:2:603620a5',0);
INSERT OR REPLACE INTO cards VALUES('node-02cc9ce83149bccf7ce5f344942f584874620656','9y9ve',replace('**Juliet**\n\nAy me! what news? why dost thou wring thy hands?','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',2.0,'0:2:609d19a9',0);
INSERT OR REPLACE INTO cards VALUES('node-726d5cfe09283c875a43c4c435bf39977f618557','9y9ve',replace('**Lady Capulet**\n\nHere comes your father; tell him so yourself,\n\nAnd see how he will take it at your hands.\n\nEnter Capulet and Nurse','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',40.0,'0:2:60a21749',0);
INSERT OR REPLACE INTO cards VALUES('node-5e5f2ff383f776d161d399baf0711702b5a1ad1e','9y9ve',replace('**Juliet**\n\nA thousand times good night!\n\nExit, above','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',39.0,'0:2:60fab142',0);
INSERT OR REPLACE INTO cards VALUES('node-c1e2f079baf5f1bb6a0e6c3679902b787da1022f','9y9ve',replace('**Romeo**\n\nGood morrow to you both. What counterfeit did I give you?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',15.0,'0:2:6113978e',0);
INSERT OR REPLACE INTO cards VALUES('node-d3418156db3aa7460e36cceb76dc2d3da35465b8','9y9ve',replace('**Capulet**\n\nWhat, man! ''tis not so much, ''tis not so much:\n\n''Tis since the nuptials of Lucentio,\n\nCome pentecost as quickly as it will,\n\nSome five and twenty years; and then we mask''d.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',8.0,'0:2:61e65749',0);
INSERT OR REPLACE INTO cards VALUES('node-58bc575681bea9f6470f279e90abc8f4dcf43682','9y9ve',replace('**Page**\n\nThis is the place; there, where the torch doth burn.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',33.0,'0:2:62609956',0);
INSERT OR REPLACE INTO cards VALUES('node-009b0f9ff979a590e8d6439be50947f192e2f272','9y9ve',replace('**Paris**\n\nImmoderately she weeps for Tybalt''s death,\n\nAnd therefore have I little talk''d of love;\n\nFor Venus smiles not in a house of tears.\n\nNow, sir, her father counts it dangerous\n\nThat she doth give her sorrow so much sway,\n\nAnd in his wisdom hastes our marriage,\n\nTo stop the inundation of her tears;\n\nWhich, too much minded by herself alone,\n\nMay be put from her by society:\n\nNow do you know the reason of this haste.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',3.0,'0:2:62a5c364',0);
INSERT OR REPLACE INTO cards VALUES('node-dc2ab8dd20389849415fb6cf2f3ff7fb0a7921e0','9y9ve',replace('**Juliet**\n\nIt is an honour that I dream not of.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',16.0,'0:2:62b19d78',0);
INSERT OR REPLACE INTO cards VALUES('node-59831719d119d14b27a66fcdcdad050a3a5ef5a4','9y9ve',replace('**First Musician**\n\nNo.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',31.0,'0:2:637e73da',0);
INSERT OR REPLACE INTO cards VALUES('node-5940c5d288ee19e9d33af5420b3b5d4df9982f34','9y9ve',replace('**Mercutio**\n\nThou desirest me to stop in my tale against the hair.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',40.0,'0:2:63a4d6c8',0);
INSERT OR REPLACE INTO cards VALUES('node-98dfa78ecd5e5d984cd3d7f12302d991ad178d91','9y9ve',replace('**Romeo**\n\nWilt thou provoke me? then have at thee, boy!\n\nThey fight','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',11.0,'0:2:63dc295e',0);
INSERT OR REPLACE INTO cards VALUES('node-94fd4752f8d4fa37058c9eb9c8d041e9df6af9da','9y9ve',replace('**Benvolio**\n\nAm I like such a fellow?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',2.0,'0:2:63ea5f38',0);
INSERT OR REPLACE INTO cards VALUES('node-61f7bafa396efdcb8ece7cd034f55752d46b6196','9y9ve',replace('**Friar Laurence**\n\nI''ll give thee armour to keep off that word:\n\nAdversity''s sweet milk, philosophy,\n\nTo comfort thee, though thou art banished.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',12.0,'0:2:63f6da49',0);
INSERT OR REPLACE INTO cards VALUES('node-21609ff959ab28947bf7614899905b8d72fa2757','9y9ve',replace('**Capulet**\n\nHow now, how now, chop-logic! What is this?\n\n''Proud,'' and ''I thank you,'' and ''I thank you not;''\n\nAnd yet ''not proud,'' mistress minion, you,\n\nThank me no thankings, nor, proud me no prouds,\n\nBut fettle your fine joints ''gainst Thursday next,\n\nTo go with Paris to Saint Peter''s Church,\n\nOr I will drag thee on a hurdle thither.\n\nOut, you green-sickness carrion! out, you baggage!\n\nYou tallow-face!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',45.0,'0:2:641b8ef4',0);
INSERT OR REPLACE INTO cards VALUES('node-b80367164e75e32479a91abc34420508147ba76f','9y9ve',replace('**Romeo**\n\nAnd we mean well in going to this mask;\n\nBut ''tis no wit to go.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',15.0,'0:2:64922d2b',0);
INSERT OR REPLACE INTO cards VALUES('node-b46fbb49f1bc3deed9e7058d06db14836efa1be4','9y9ve',replace('**Lady Capulet**\n\nWhy, how now, Juliet!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',20.0,'0:2:64e8c943',0);
INSERT OR REPLACE INTO cards VALUES('node-edcd72c375df28565f5e77e2cf289fc1eabf9fbb','9y9ve',replace('**Paris**\n\nI do defy thy conjurations,\n\nAnd apprehend thee for a felon here.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',10.0,'0:2:6514d038',0);
INSERT OR REPLACE INTO cards VALUES('node-9e4796782eb3e51c9273f3e4651b0efc3e69afe0','9y9ve',replace('**Tybalt**\n\nIt fits, when such a villain is a guest:\n\nI''ll not endure him.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',20.0,'0:2:656f12f6',0);
INSERT OR REPLACE INTO cards VALUES('node-668728241bfdd4f36b2e9fe6e6c91f80ff99df74','9y9ve',replace('**Nurse**\n\nAh. mocker! that''s the dog''s name; R is for\n\nthe No; I know it begins with some other\n\nletter: and she hath the prettiest sententious of\n\nit, of you and rosemary, that it would do you good\n\nto hear it.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',87.0,'0:2:6575c272',0);
INSERT OR REPLACE INTO cards VALUES('node-9e022f83853583a63ba4c6d7ede06cdec5982369','9y9ve',replace('**First Watchman**\n\n```\nWithin\n```\n\nLead, boy: which way?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',31.0,'0:2:65e9270e',0);
INSERT OR REPLACE INTO cards VALUES('node-09328baaa4e15938af8ffcc893f303e10f7985dd','9y9ve',replace('**Juliet**\n\nWhere I have learn''d me to repent the sin\n\nOf disobedient opposition\n\nTo you and your behests, and am enjoin''d\n\nBy holy Laurence to fall prostrate here,\n\nAnd beg your pardon: pardon, I beseech you!\n\nHenceforward I am ever ruled by you.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',9.0,'0:2:660778ce',0);
INSERT OR REPLACE INTO cards VALUES('node-ae1991ab66ae7d2daa2df9dba5d7f5ff1b0ed386','9y9ve',replace('**Romeo**\n\nNeither, fair saint, if either thee dislike.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',10.0,'0:2:660924b6',0);
INSERT OR REPLACE INTO cards VALUES('node-3a96194d33fe97caa882aba4003ece37d0eb854d','9y9ve',replace('**Juliet**\n\nO, break, my heart! poor bankrupt, break at once!\n\nTo prison, eyes, ne''er look on liberty!\n\nVile earth, to earth resign; end motion here;\n\nAnd thou and Romeo press one heavy bier!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',8.0,'0:2:6647b192',0);
INSERT OR REPLACE INTO cards VALUES('node-8aec7054673ef9051679dfee8839906464df607a','9y9ve',replace('**Capulet**\n\nMass, and well said; a merry whoreson, ha!\n\nThou shalt be logger-head. Good faith, ''tis day:\n\nThe county will be here with music straight,\n\nFor so he said he would: I hear him near.\n\nMusic within\n\nNurse! Wife! What, ho! What, nurse, I say!\n\nRe-enter Nurse\n\nGo waken Juliet, go and trim her up;\n\nI''ll go and chat with Paris: hie, make haste,\n\nMake haste; the bridegroom he is come already:\n\nMake haste, I say.\n\nExeunt','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',10.0,'0:2:66a49627',0);
INSERT OR REPLACE INTO cards VALUES('node-71e39d5c65e8f7e01ff96827af94755085fafe15','9y9ve',replace('**Romeo**\n\nI pay thy poverty, and not thy will.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',12.0,'0:2:66a8bab7',0);
INSERT OR REPLACE INTO cards VALUES('node-47bcce4ab5043bd567fa8192724d3f0d02ed38af','9y9ve',replace('**Capulet**\n\nHe shall be endured:\n\nWhat, goodman boy! I say, he shall: go to;\n\nAm I the master here, or you? go to.\n\nYou''ll not endure him! God shall mend my soul!\n\nYou''ll make a mutiny among my guests!\n\nYou will set cock-a-hoop! you''ll be the man!','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',21.0,'0:2:672a729e',0);
INSERT OR REPLACE INTO cards VALUES('node-31ab6d976e4bc8e8231fbea3d991263e623ef8db','9y9ve',replace('**Benvolio**\n\nThis wind, you talk of, blows us from ourselves;\n\nSupper is done, and we shall come too late.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',25.0,'0:2:6739cfe6',0);
INSERT OR REPLACE INTO cards VALUES('node-2ad03d18b896a7657fb23612bc1680d093ef4295','9y9ve',replace('**Sampson**\n\nA dog of that house shall move me to stand: I will\n\ntake the wall of any man or maid of Montague''s.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',8.0,'0:2:6756190',0);
INSERT OR REPLACE INTO cards VALUES('node-e013b8d9a0875ac9662b1fb50372cefd951e7920','9y9ve',replace('**Romeo**\n\nBid her devise\n\nSome means to come to shrift this afternoon;\n\nAnd there she shall at Friar Laurence'' cell\n\nBe shrived and married. Here is for thy pains.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',76.0,'0:2:68231d16',0);
INSERT OR REPLACE INTO cards VALUES('node-e5743b9833b95a01348f49793ee000039b8aee4f','9y9ve',replace('**First Citizen**\n\nUp, sir, go with me;\n\nI charge thee in the princes name, obey.\n\nEnter Prince, attended; Montague, Capulet, their Wives, and others','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',50.0,'0:2:68557840',0);
INSERT OR REPLACE INTO cards VALUES('node-d13406a92810a7a2e53ee004bb9a241ddbac516a','9y9ve',replace('**Benvolio**\n\nBy my head, here come the Capulets.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',8.0,'0:2:689d6800',0);
INSERT OR REPLACE INTO cards VALUES('node-39429fe86c9e616f8e09f5dba1c83b73158b9da7','9y9ve',replace('**Mercutio**\n\nThe ship, sir, the slip; can you not conceive?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',16.0,'0:2:68cf75cb',0);
INSERT OR REPLACE INTO cards VALUES('node-a53e0baaab631f2dfc2536978547ecdda6bd2a17','9y9ve',replace('**Romeo**\n\nWhat shall I swear by?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',22.0,'0:2:696509cd',0);
INSERT OR REPLACE INTO cards VALUES('node-20f3054f23e32bbad657240800e868077e0d057b','9y9ve',replace('**Musician**\n\nMarry, sir, because silver hath a sweet sound.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',40.0,'0:2:6a36ae37',0);
INSERT OR REPLACE INTO cards VALUES('node-ea1b2bb61a48c22864bfa1ae5a9f919880dfe40b','9y9ve',replace('**Nurse**\n\nEven or odd, of all days in the year,\n\nCome Lammas-eve at night shall she be fourteen.\n\nSusan and she, God rest all Christian souls!\n\nWere of an age: well, Susan is with God;\n\nShe was too good for me: but, as I said,\n\nOn Lammas-eve at night shall she be fourteen;\n\nThat shall she, marry; I remember it well.\n\n''Tis since the earthquake now eleven years;\n\nAnd she was wean''d, I never shall forget it,\n\nOf all the days of the year, upon that day:\n\nFor I had then laid wormwood to my dug,\n\nSitting in the sun under the dove-house wall;\n\nMy lord and you were then at Mantua:\n\nNay, I do bear a brain: but, as I said,\n\nWhen it did taste the wormwood on the nipple\n\nOf my dug and felt it bitter, pretty fool,\n\nTo see it tetchy and fall out with the dug!\n\nShake quoth the dove-house: ''twas no need, I trow,\n\nTo bid me trudge:\n\nAnd since that time it is eleven years;\n\nFor then she could stand alone; nay, by the rood,\n\nShe could have run and waddled all about;\n\nFor even the day before, she broke her brow:\n\nAnd then my husband God be with his soul!\n\nA'' was a merry man took up the child:\n\n''Yea,'' quoth he, ''dost thou fall upon thy face?\n\nThou wilt fall backward when thou hast more wit;\n\nWilt thou not, Jule?'' and, by my holidame,\n\nThe pretty wretch left crying and said ''Ay.''\n\nTo see, now, how a jest shall come about!\n\nI warrant, an I should live a thousand years,\n\nI never should forget it: ''Wilt thou not, Jule?'' quoth he;\n\nAnd, pretty fool, it stinted and said ''Ay.''','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',10.0,'0:2:6a5843aa',0);
INSERT OR REPLACE INTO cards VALUES('node-d35a2d2c30f7933a3f4324cb1e3e1fa244d5a830','9y9ve',replace('**Lady Capulet**\n\nVerona''s summer hath not such a flower.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',20.0,'0:2:6aa120cf',0);
INSERT OR REPLACE INTO cards VALUES('node-0ba1c0fae46fa120ed575181d35d14dc90b52bd5','9y9ve',replace('**Lady Capulet**\n\nAy, sir; but she will none, she gives you thanks.\n\nI would the fool were married to her grave!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',42.0,'0:2:6aa713a5',0);
INSERT OR REPLACE INTO cards VALUES('node-392189f0d11ec1802efbf93fba0de05cefea3337','9y9ve',replace('**Romeo**\n\nFarewell!\n\nI will omit no opportunity\n\nThat may convey my greetings, love, to thee.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',12.0,'0:2:6ad05591',0);
INSERT OR REPLACE INTO cards VALUES('node-0c6051a0da3cff337f007389b5c3d3e5c48201a5','9y9ve',replace('**Page**\n\n```\nAside\n```\n\nI am almost afraid to stand alone\n\nHere in the churchyard; yet I will adventure.\n\nRetires','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',1.0,'0:2:6ada1449',0);
INSERT OR REPLACE INTO cards VALUES('node-78066e37125a739d7cdb4550831656ec9c9b6196','9y9ve',replace('**Mercutio**\n\nCome, come, thou art as hot a Jack in thy mood as\n\nany in Italy, and as soon moved to be moody, and as\n\nsoon moody to be moved.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',3.0,'0:2:6ae1f865',0);
INSERT OR REPLACE INTO cards VALUES('node-c9a3bad180fc46b0840043477d5f892f9e08f376','9y9ve',replace('**Juliet**\n\nWhat''s he that follows there, that would not dance?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',46.0,'0:2:6ae2fbd5',0);
INSERT OR REPLACE INTO cards VALUES('node-b3c162f5baab48e4ce4f57b0a74fc57188704500','9y9ve',replace('**Nurse**\n\nGod ye good morrow, gentlemen.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',50.0,'0:2:6b51040c',0);
INSERT OR REPLACE INTO cards VALUES('node-33f94296577a3c24d62741902daecdcecb0ade4c','9y9ve',replace('**Balthasar**\n\nThen she is well, and nothing can be ill:\n\nHer body sleeps in Capel''s monument,\n\nAnd her immortal part with angels lives.\n\nI saw her laid low in her kindred''s vault,\n\nAnd presently took post to tell it you:\n\nO, pardon me for bringing these ill news,\n\nSince you did leave it for my office, sir.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',1.0,'0:2:6b73828',0);
INSERT OR REPLACE INTO cards VALUES('node-a11cc3a330adaecf5ce7b9e9bd6e22e4f2a9077e','9y9ve',replace('**Tybalt**\n\nUncle, this is a Montague, our foe,\n\nA villain that is hither come in spite,\n\nTo scorn at our solemnity this night.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',16.0,'0:2:6bae745',0);
INSERT OR REPLACE INTO cards VALUES('node-c198d6ece3343969827f5de8174749c21298b68b','9y9ve',replace('**Friar Laurence**\n\nThere on the ground, with his own tears made drunk.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',24.0,'0:2:6bd2fbdd',0);
INSERT OR REPLACE INTO cards VALUES('node-dbbadc5a8b156df576a8529d69fac6c8f4888efd','9y9ve',replace('**Benvolio**\n\nGood-morrow, cousin.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',62.0,'0:2:6beefd07',0);
INSERT OR REPLACE INTO cards VALUES('node-1296fe64347ff63093e69b816624e0270347b411','9y9ve',replace('**Lady Capulet**\n\nEvermore weeping for your cousin''s death?\n\nWhat, wilt thou wash him from his grave with tears?\n\nAn if thou couldst, thou couldst not make him live;\n\nTherefore, have done: some grief shows much of love;\n\nBut much of grief shows still some want of wit.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',22.0,'0:2:6bfc0e44',0);
INSERT OR REPLACE INTO cards VALUES('node-ff0fe06b52c964d01918c0df4a4cd483c63de1d0','9y9ve',replace('**Tybalt**\n\nThis, by his voice, should be a Montague.\n\nFetch me my rapier, boy. What dares the slave\n\nCome hither, cover''d with an antic face,\n\nTo fleer and scorn at our solemnity?\n\nNow, by the stock and honour of my kin,\n\nTo strike him dead, I hold it not a sin.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',14.0,'0:2:6c133560',0);
INSERT OR REPLACE INTO cards VALUES('node-8de4a57ca096e6413a867a0e23d80f817b466712','9y9ve',replace('**Juliet**\n\nO, find him! give this ring to my true knight,\n\nAnd bid him come to take his last farewell.\n\nExeunt','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',22.0,'0:2:6ca588ec',0);
INSERT OR REPLACE INTO cards VALUES('node-7f45a24dd425907172715147864238f773bb3806','9y9ve',replace('**Romeo**\n\nIs the day so young?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',63.0,'0:2:6d45c60c',0);
INSERT OR REPLACE INTO cards VALUES('node-7d004da552d2c386e872af74a33da6a3ef329ba7','9y9ve',replace('**Romeo**\n\nStay, fellow; I can read.\n\nReads\n\n''Signior Martino and his wife and daughters;\n\nCounty Anselme and his beauteous sisters; the lady\n\nwidow of Vitravio; Signior Placentio and his lovely\n\nnieces; Mercutio and his brother Valentine; mine\n\nuncle Capulet, his wife and daughters; my fair niece\n\nRosaline; Livia; Signior Valentio and his cousin\n\nTybalt, Lucio and the lively Helena.'' A fair\n\nassembly: whither should they come?','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',17.0,'0:2:6e0fa231',0);
INSERT OR REPLACE INTO cards VALUES('node-f95e1889c7530a59c2978cfc3aa9a2548e1acede','9y9ve',replace('**Romeo**\n\nOut of her favour, where I am in love.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',71.0,'0:2:6eba959c',0);
INSERT OR REPLACE INTO cards VALUES('node-a3d9893a3404645587f4cfb0335c065f3711d18d','9y9ve',replace('**Friar Laurence**\n\nToo familiar\n\nIs my dear son with such sour company:\n\nI bring thee tidings of the prince''s doom.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',2.0,'0:2:6eeac43d',0);
INSERT OR REPLACE INTO cards VALUES('node-5c0c33ff3c62a9168ce077f5cdcb15fee4b9f8d4','9y9ve',replace('**Juliet**\n\nWhat satisfaction canst thou have to-night?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',27.0,'0:2:6f46a37d',0);
INSERT OR REPLACE INTO cards VALUES('node-f13433a6f016d0f9d82c40dab65b1525f7b4fa47','9y9ve',replace('**Tybalt**\n\nPatience perforce with wilful choler meeting\n\nMakes my flesh tremble in their different greeting.\n\nI will withdraw: but this intrusion shall\n\nNow seeming sweet convert to bitter gall.\n\nExit','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',24.0,'0:2:6f951212',0);
INSERT OR REPLACE INTO cards VALUES('node-510a0739c784a8c441b4c24eb8772ac4550479ba','9y9ve',replace('**Paris**\n\nSweet flower, with flowers thy bridal bed I strew,\n\nO woe! thy canopy is dust and stones;\n\nWhich with sweet water nightly I will dew,\n\nOr, wanting that, with tears distill''d by moans:\n\nThe obsequies that I for thee will keep\n\nNightly shall be to strew thy grave and weep.\n\nThe Page whistles\n\nThe boy gives warning something doth approach.\n\nWhat cursed foot wanders this way to-night,\n\nTo cross my obsequies and true love''s rite?\n\nWhat with a torch! muffle me, night, awhile.\n\nRetires\n\nEnter Romeo and Balthasar, with a torch, mattock,  & c','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',2.0,'0:2:6f9c9569',0);
INSERT OR REPLACE INTO cards VALUES('node-306504a660ffac39d319214d3841406c1647a5ef','9y9ve',replace('**Juliet**\n\n```\nAside\n```\n\nVillain and he be many miles asunder.\n\nGod Pardon him! I do, with all my heart;\n\nAnd yet no man like he doth grieve my heart.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',29.0,'0:2:6fa6d3fa',0);
INSERT OR REPLACE INTO cards VALUES('node-75ba559005a780656ff74ca2d118e02ff5ddd3cd','9y9ve',replace('**Nurse**\n\nLook, look! O heavy day!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',4.0,'0:2:6fd91bb1',0);
INSERT OR REPLACE INTO cards VALUES('node-b864140c85e55852eac3d6a95c79225ac8ebc77e','9y9ve',replace('**Romeo**\n\nAnd stay, good nurse, behind the abbey wall:\n\nWithin this hour my man shall be with thee\n\nAnd bring thee cords made like a tackled stair;\n\nWhich to the high top-gallant of my joy\n\nMust be my convoy in the secret night.\n\nFarewell; be trusty, and I''ll quit thy pains:\n\nFarewell; commend me to thy mistress.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',80.0,'0:2:70b51fa1',0);
INSERT OR REPLACE INTO cards VALUES('node-e425ce390945f9421b9117ce66166b25634c8ea3','9y9ve',replace('**Romeo**\n\nO, thou wilt speak again of banishment.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',11.0,'0:2:70f68863',0);
INSERT OR REPLACE INTO cards VALUES('node-1c6e82ea1e0b9091cfaefeebaf41b1c869bf426a','9y9ve',replace('**Gregory**\n\nThe quarrel is between our masters and us their men.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',11.0,'0:2:71b36556',0);
INSERT OR REPLACE INTO cards VALUES('node-59f6c116be8fe54c6502b00b05cb75338dc15c54','9y9ve',replace('**Romeo**\n\nNot I; unless the breath of heartsick groans,\n\nMist-like, infold me from the search of eyes.\n\nKnocking','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',19.0,'0:2:721e8e08',0);
INSERT OR REPLACE INTO cards VALUES('node-61a60e13bfa5a6bbd7ce3e0af7f3efc7a587a772','9y9ve',replace('**Romeo**\n\nSwitch and spurs, switch and spurs; or I''ll cry a match.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',29.0,'0:2:727557ce',0);
INSERT OR REPLACE INTO cards VALUES('node-f7b201d44453f631e8b8159c6882b441821918b6','9y9ve',replace('**Juliet**\n\nWhat''s he that now is going out of door?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',44.0,'0:2:73486ace',0);
INSERT OR REPLACE INTO cards VALUES('node-15bb57fcbb319ab3778c15a2ff3669236391acd4','9y9ve',replace('**Friar Laurence**\n\nBenedicite!\n\nWhat early tongue so sweet saluteth me?\n\nYoung son, it argues a distemper''d head\n\nSo soon to bid good morrow to thy bed:\n\nCare keeps his watch in every old man''s eye,\n\nAnd where care lodges, sleep will never lie;\n\nBut where unbruised youth with unstuff''d brain\n\nDoth couch his limbs, there golden sleep doth reign:\n\nTherefore thy earliness doth me assure\n\nThou art up-roused by some distemperature;\n\nOr if not so, then here I hit it right,\n\nOur Romeo hath not been in bed to-night.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',2.0,'0:2:73537a50',0);
INSERT OR REPLACE INTO cards VALUES('node-17e1a092fbf604202e0167176d7229c6eba20fbd','9y9ve',replace('**Lady Capulet**\n\nI will, and know her mind early to-morrow;\n\nTo-night she is mew''d up to her heaviness.','\n',char(10)),'node-1ea0a4f951159826ef795e37aa3321911ddb6685',2.0,'0:2:73b89f1a',0);
INSERT OR REPLACE INTO cards VALUES('node-defe8bd54e0b963699907754cdbc2cc2812cee23','9y9ve',replace('**Apothecary**\n\nWho calls so loud?','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',7.0,'0:2:74043b9f',0);
INSERT OR REPLACE INTO cards VALUES('node-d069d8795068c555fe86585be2fbf8672e967bf4','9y9ve',replace('**Nurse**\n\nif you be he, sir, I desire some confidence with\n\nyou.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',60.0,'0:2:7444fe7d',0);
INSERT OR REPLACE INTO cards VALUES('node-2c7b688e1d85b719a7e2eef4cf360abcfb5044ff','9y9ve',replace('**Romeo**\n\nHa, banishment! be merciful, say ''death;''\n\nFor exile hath more terror in his look,\n\nMuch more than death: do not say ''banishment.''','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',5.0,'0:2:74be95d3',0);
INSERT OR REPLACE INTO cards VALUES('node-1403b73f37a9918c667f27578b074a989390ddf1','9y9ve',replace('**Mercutio**\n\nNay, an there were two such, we should have none\n\nshortly, for one would kill the other. Thou! why,\n\nthou wilt quarrel with a man that hath a hair more,\n\nor a hair less, in his beard, than thou hast: thou\n\nwilt quarrel with a man for cracking nuts, having no\n\nother reason but because thou hast hazel eyes: what\n\neye but such an eye would spy out such a quarrel?\n\nThy head is as fun of quarrels as an egg is full of\n\nmeat, and yet thy head hath been beaten as addle as\n\nan egg for quarrelling: thou hast quarrelled with a\n\nman for coughing in the street, because he hath\n\nwakened thy dog that hath lain asleep in the sun:\n\ndidst thou not fall out with a tailor for wearing\n\nhis new doublet before Easter? with another, for\n\ntying his new shoes with old riband? and yet thou\n\nwilt tutor me from quarrelling!','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',5.0,'0:2:754782b6',0);
INSERT OR REPLACE INTO cards VALUES('node-c72b47759d815053e041233f33b770a84b9011e9','9y9ve',replace('**Gregory**\n\n''Tis well thou art not fish; if thou hadst, thou\n\nhadst been poor John. Draw thy tool! here comes\n\ntwo of the house of the Montagues.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',17.0,'0:2:75fd94c2',0);
INSERT OR REPLACE INTO cards VALUES('node-680d2f3247e865a8cc32b79960373664c3157816','9y9ve',replace('**Balthasar**\n\n```\nAside\n```\n\nFor all this same, I''ll hide me hereabout:\n\nHis looks I fear, and his intents I doubt.\n\nRetires','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',6.0,'0:2:76352f85',0);
INSERT OR REPLACE INTO cards VALUES('node-a2a06e09aab8a4324789eb7bae53a6c90959b110','9y9ve',replace('**Nurse**\n\nHere, sir, a ring she bid me give you, sir:\n\nHie you, make haste, for it grows very late.\n\nExit','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',34.0,'0:2:7676ce41',0);
INSERT OR REPLACE INTO cards VALUES('node-ee7190f165965ec7241878096df2e2e69cc61882','9y9ve',replace('**Romeo**\n\nPardon, good Mercutio, my business was great; and in\n\nsuch a case as mine a man may strain courtesy.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',17.0,'0:2:76850d83',0);
INSERT OR REPLACE INTO cards VALUES('node-5bf1d8179877bd8fa3a516182be8f5b402c32baf','9y9ve',replace('**Nurse**\n\nOut upon you! what a man are you!','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',54.0,'0:2:76f1ba84',0);
INSERT OR REPLACE INTO cards VALUES('node-c7fbed993d7be4ec37bd37dcd8dde5260b0621d7','9y9ve',replace('**Romeo**\n\nThen move not, while my prayer''s effect I take.\n\nThus from my lips, by yours, my sin is purged.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',31.0,'0:2:77155668',0);
INSERT OR REPLACE INTO cards VALUES('node-a934e7a189109f29ff67b1c1bcc50cd4c5081e32','9y9ve',replace('**Montague**\n\nThou villain Capulet, Hold me not, let me go.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',48.0,'0:2:77317a44',0);
INSERT OR REPLACE INTO cards VALUES('node-f7206237bcb055a8048f5a5860be266616b7da5f','9y9ve',replace('**Balthasar**\n\nI dare not, sir\n\nMy master knows not but I am gone hence;\n\nAnd fearfully did menace me with death,\n\nIf I did stay to look on his intents.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',24.0,'0:2:777c6791',0);
INSERT OR REPLACE INTO cards VALUES('node-a29134c24ea7adfa73e091c729424b117d78c9e2','9y9ve',replace('**Mercutio**\n\nCould you not take some occasion without giving?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',13.0,'0:2:7794d263',0);
