INSERT OR REPLACE INTO cards VALUES('node-5f443403fca0f51e43e187d61e9f3fb4b6072e2b','9y9ve',replace('**Peter**\n\nMusicians, O, musicians, ''Heart''s ease, Heart''s\n\nease:'' O, an you will have me live, play ''Heart''s ease.''','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',26.0,'0:2:7902e252',0);
INSERT OR REPLACE INTO cards VALUES('node-f8ceb217404cfafda52c8f0a6a0491c649c3ef4d','9y9ve',replace('**Lady Capulet**\n\nO me, O me! My child, my only life,\n\nRevive, look up, or I will die with thee!\n\nHelp, help! Call help.\n\n```\nEnter Capulet\n```\n\n','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',5.0,'0:2:795872a5',0);
INSERT OR REPLACE INTO cards VALUES('node-108713d3e3c8221550b8fbdc869b3439059c563f','9y9ve',replace('**Nurse**\n\nPeace, I have done. God mark thee to his grace!\n\nThou wast the prettiest babe that e''er I nursed:\n\nAn I might live to see thee married once,\n\nI have my wish.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',14.0,'0:2:797b0f5c',0);
INSERT OR REPLACE INTO cards VALUES('node-7da0b04b834b34e64db394d285e43b18fbc6e435','9y9ve',replace('**Juliet**\n\nShall I speak ill of him that is my husband?\n\nAh, poor my lord, what tongue shall smooth thy name,\n\nWhen I, thy three-hours wife, have mangled it?\n\nBut, wherefore, villain, didst thou kill my cousin?\n\nThat villain cousin would have kill''d my husband:\n\nBack, foolish tears, back to your native spring;\n\nYour tributary drops belong to woe,\n\nWhich you, mistaking, offer up to joy.\n\nMy husband lives, that Tybalt would have slain;\n\nAnd Tybalt''s dead, that would have slain my husband:\n\nAll this is comfort; wherefore weep I then?\n\nSome word there was, worser than Tybalt''s death,\n\nThat murder''d me: I would forget it fain;\n\nBut, O, it presses to my memory,\n\nLike damned guilty deeds to sinners'' minds:\n\n''Tybalt is dead, and Romeo banished;''\n\nThat ''banished,'' that one word ''banished,''\n\nHath slain ten thousand Tybalts. Tybalt''s death\n\nWas woe enough, if it had ended there:\n\nOr, if sour woe delights in fellowship\n\nAnd needly will be rank''d with other griefs,\n\nWhy follow''d not, when she said ''Tybalt''s dead,''\n\nThy father, or thy mother, nay, or both,\n\nWhich modern lamentations might have moved?\n\nBut with a rear-ward following Tybalt''s death,\n\n''Romeo is banished,'' to speak that word,\n\nIs father, mother, Tybalt, Romeo, Juliet,\n\nAll slain, all dead. ''Romeo is banished!''\n\nThere is no end, no limit, measure, bound,\n\nIn that word''s death; no words can that woe sound.\n\nWhere is my father, and my mother, nurse?','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',18.0,'0:2:79c53aeb',0);
INSERT OR REPLACE INTO cards VALUES('node-9f15e86011568270ae2c3148095ef33b5bfb1129','9y9ve',replace('**Juliet**\n\nNo, madam; we have cull''d such necessaries\n\nAs are behoveful for our state to-morrow:\n\nSo please you, let me now be left alone,\n\nAnd let the nurse this night sit up with you;\n\nFor, I am sure, you have your hands full all,\n\nIn this so sudden business.','\n',char(10)),'node-5a5e52ac71bf638baf35f7e8ff54a68376bf2aa7',2.0,'0:2:7a001b05',0);
INSERT OR REPLACE INTO cards VALUES('node-401a4ebc86bc4987dac7ed124b6aab78336583ae','9y9ve',replace('**Mercutio**\n\n''Tis no less, I tell you, for the bawdy hand of the\n\ndial is now upon the prick of noon.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',53.0,'0:2:7a14014',0);
INSERT OR REPLACE INTO cards VALUES('node-17488ef62a146b3806aa5cf348bcd55a26d88c66','9y9ve',replace('**Romeo**\n\nHe jests at scars that never felt a wound.\n\nJuliet appears above at a window\n\nBut, soft! what light through yonder window breaks?\n\nIt is the east, and Juliet is the sun.\n\nArise, fair sun, and kill the envious moon,\n\nWho is already sick and pale with grief,\n\nThat thou her maid art far more fair than she:\n\nBe not her maid, since she is envious;\n\nHer vestal livery is but sick and green\n\nAnd none but fools do wear it; cast it off.\n\nIt is my lady, O, it is my love!\n\nO, that she knew she were!\n\nShe speaks yet she says nothing: what of that?\n\nHer eye discourses; I will answer it.\n\nI am too bold, ''tis not to me she speaks:\n\nTwo of the fairest stars in all the heaven,\n\nHaving some business, do entreat her eyes\n\nTo twinkle in their spheres till they return.\n\nWhat if her eyes were there, they in her head?\n\nThe brightness of her cheek would shame those stars,\n\nAs daylight doth a lamp; her eyes in heaven\n\nWould through the airy region stream so bright\n\nThat birds would sing and think it were not night.\n\nSee, how she leans her cheek upon her hand!\n\nO, that I were a glove upon that hand,\n\nThat I might touch that cheek!','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',0.0,'0:2:7a43b65b',0);
INSERT OR REPLACE INTO cards VALUES('node-fac227dc5ca7ff424ed089eb5f68218dba421db1','9y9ve',replace('**Nurse**\n\nGood heart, and, i'' faith, I will tell her as much:\n\nLord, Lord, she will be a joyful woman.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',73.0,'0:2:7a9d7e37',0);
INSERT OR REPLACE INTO cards VALUES('node-be3fe8c7fcdb0c3459ea130226bf784c2d293d7b','9y9ve',replace('**Juliet**\n\nThe tears have got small victory by that;\n\nFor it was bad enough before their spite.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',17.0,'0:2:7ad4fdf1',0);
INSERT OR REPLACE INTO cards VALUES('node-c6a69a9400927840ae1b159d8b1f13eacd0e1cc2','9y9ve',replace('**Romeo**\n\nWhy, then is my pump well flowered.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',25.0,'0:2:7b4640ed',0);
