INSERT OR REPLACE INTO cards VALUES('node-7df607f60aa1db165d51756ff49fda53408a302f','9y9ve',replace('**Montague**\n\nAlas, my liege, my wife is dead to-night;\n\nGrief of my son''s exile hath stopp''d her breath:\n\nWhat further woe conspires against mine age?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',49.0,'0:2:7c3f98c6',0);
