INSERT OR REPLACE INTO cards VALUES('node-7fa56cfc58f38ef7c2d1f5a9cdc75d096dff8ead','9y9ve',replace('**Mercutio**\n\nTrue, I talk of dreams,\n\nWhich are the children of an idle brain,\n\nBegot of nothing but vain fantasy,\n\nWhich is as thin of substance as the air\n\nAnd more inconstant than the wind, who wooes\n\nEven now the frozen bosom of the north,\n\nAnd, being anger''d, puffs away from thence,\n\nTurning his face to the dew-dropping south.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',24.0,'0:2:7e18dd3e',0);
INSERT OR REPLACE INTO cards VALUES('node-8c7fb35c6a8e6f54a1362325b448ed21c1178b84','9y9ve',replace('**Juliet**\n\nIt is, it is: hie hence, be gone, away!\n\nIt is the lark that sings so out of tune,\n\nStraining harsh discords and unpleasing sharps.\n\nSome say the lark makes sweet division;\n\nThis doth not so, for she divideth us:\n\nSome say the lark and loathed toad change eyes,\n\nO, now I would they had changed voices too!\n\nSince arm from arm that voice doth us affray,\n\nHunting thee hence with hunt''s-up to the day,\n\nO, now be gone; more light and light it grows.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',4.0,'0:2:7e472567',0);
INSERT OR REPLACE INTO cards VALUES('node-15ff461862f3c16330f44f0ac789162313f3e4f5','9y9ve',replace('**Friar Laurence**\n\nUnhappy fortune! by my brotherhood,\n\nThe letter was not nice but full of charge\n\nOf dear import, and the neglecting it\n\nMay do much danger. Friar John, go hence;\n\nGet me an iron crow, and bring it straight\n\nUnto my cell.','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',5.0,'0:2:7ec85d63',0);
INSERT OR REPLACE INTO cards VALUES('node-de70059c2094e7c026b62c8f0298fe6d9ff5b3cd','9y9ve',replace('**Capulet**\n\nWhat noise is this? Give me my long sword, ho!','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',45.0,'0:2:7ee3c16e',0);
INSERT OR REPLACE INTO cards VALUES('node-0b62d8593891973ae57925d82e4590c23a8f87e6','9y9ve',replace('**Benvolio**\n\nRomeo, away, be gone!\n\nThe citizens are up, and Tybalt slain.\n\nStand not amazed: the prince will doom thee death,\n\nIf thou art taken: hence, be gone, away!','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',45.0,'0:2:7f0dc13c',0);
