INSERT OR REPLACE INTO cards VALUES('node-e5db51db228d836b0e6a3af2b2e8a1901601608f','9y9ve',replace('**Lady Capulet**\n\nA fortnight and odd days.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',9.0,'0:2:7f82f0e9',0);
INSERT OR REPLACE INTO cards VALUES('node-f17e9c1fb0b35d7320c6d6d90350884c46a9d3d8','9y9ve',replace('**Juliet**\n\nO Romeo, Romeo! wherefore art thou Romeo?\n\nDeny thy father and refuse thy name;\n\nOr, if thou wilt not, be but sworn my love,\n\nAnd I''ll no longer be a Capulet.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',3.0,'0:2:7fe12a14',0);
INSERT OR REPLACE INTO cards VALUES('node-e4c3648f70d11e1ecf39d29ba89e75cd0eb234e3','9y9ve',replace('**Juliet**\n\nThou know''st the mask of night is on my face,\n\nElse would a maiden blush bepaint my cheek\n\nFor that which thou hast heard me speak to-night\n\nFain would I dwell on form, fain, fain deny\n\nWhat I have spoke: but farewell compliment!\n\nDost thou love me? I know thou wilt say ''Ay,''\n\nAnd I will take thy word: yet if thou swear''st,\n\nThou mayst prove false; at lovers'' perjuries\n\nThen say, Jove laughs. O gentle Romeo,\n\nIf thou dost love, pronounce it faithfully:\n\nOr if thou think''st I am too quickly won,\n\nI''ll frown and be perverse an say thee nay,\n\nSo thou wilt woo; but else, not for the world.\n\nIn truth, fair Montague, I am too fond,\n\nAnd therefore thou mayst think my ''havior light:\n\nBut trust me, gentleman, I''ll prove more true\n\nThan those that have more cunning to be strange.\n\nI should have been more strange, I must confess,\n\nBut that thou overheard''st, ere I was ware,\n\nMy true love''s passion: therefore pardon me,\n\nAnd not impute this yielding to light love,\n\nWhich the dark night hath so discovered.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',19.0,'0:2:81d22318',0);
INSERT OR REPLACE INTO cards VALUES('node-071d2cd643d9ea1d37dba5670ca3f6576b395b3d','9y9ve',replace('**Paris**\n\nThese times of woe afford no time to woo.\n\nMadam, good night: commend me to your daughter.','\n',char(10)),'node-1ea0a4f951159826ef795e37aa3321911ddb6685',1.0,'0:2:81ec4d49',0);
INSERT OR REPLACE INTO cards VALUES('node-5e79a329c108c99311252751e2db2a2f49877842','9y9ve',replace('**Peter**\n\nNo money, on my faith, but the gleek;\n\nI will give you the minstrel.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',34.0,'0:2:830c3e5',0);
INSERT OR REPLACE INTO cards VALUES('node-14baed9f561fd069214a4740985c7f25041f75b1','9y9ve',replace('**Prince**\n\nSearch, seek, and know how this foul murder comes.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',44.0,'0:2:83610f28',0);
INSERT OR REPLACE INTO cards VALUES('node-50c62b02d549cc2a6d2b35906a791d65f84a0f19','9y9ve',replace('**Benvolio**\n\nNay, he will answer the letter''s master, how he\n\ndares, being dared.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',7.0,'0:2:836ad896',0);
INSERT OR REPLACE INTO cards VALUES('node-d8d7f9d620187b554ddcde66e889aa79ab28da7e','9y9ve',replace('**Second Musician**\n\nHang him, Jack! Come, we''ll in here; tarry for the\n\nmourners, and stay dinner.\n\nExeunt\n\n\n\n      Shakespeare homepage','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',47.0,'0:2:83c6ab8b',0);
