INSERT OR REPLACE INTO cards VALUES('node-c2913d8c63511bc1f43d1db51348a34e0a228b5a','9y9ve',replace('**Romeo**\n\nSpakest thou of Juliet? how is it with her?\n\nDoth she not think me an old murderer,\n\nNow I have stain''d the childhood of our joy\n\nWith blood removed but little from her own?\n\nWhere is she? and how doth she? and what says\n\nMy conceal''d lady to our cancell''d love?','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',28.0,'0:2:83db77f5',0);
INSERT OR REPLACE INTO cards VALUES('node-061ad30313019bc6e10dae1691741f0d70497731','9y9ve',replace('**Tybalt**\n\nWell, peace be with you, sir: here comes my man.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',18.0,'0:2:84f7099c',0);
