INSERT OR REPLACE INTO cards VALUES('node-7138178ed43a2c8ef6cc77aaf11559a6b01c65cf','9y9ve',replace('**Lady Capulet**\n\nFie, fie! what, are you mad?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',46.0,'0:2:86a6836c',0);
INSERT OR REPLACE INTO cards VALUES('node-ed1da34a16311a0100d46beabc7fb6bd13b06d3d','9y9ve',replace('**Romeo**\n\nI pray thee, chide not; she whom I love now\n\nDoth grace for grace and love for love allow;\n\nThe other did not so.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',15.0,'0:2:86e3bb49',0);
INSERT OR REPLACE INTO cards VALUES('node-489460569eb908efd63250e92fa24379bc47f92b','9y9ve',replace('**Paris**\n\nBeguiled, divorced, wronged, spited, slain!\n\nMost detestable death, by thee beguil''d,\n\nBy cruel cruel thee quite overthrown!\n\nO love! O life! not life, but love in death!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',18.0,'0:2:870ec1b8',0);
INSERT OR REPLACE INTO cards VALUES('node-f313a8f28fb48286135f12a83289043b246a90cb','9y9ve',replace('**Mercutio**\n\nAnd, to sink in it, should you burden love;\n\nToo great oppression for a tender thing.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',7.0,'0:2:88cf96b1',0);
INSERT OR REPLACE INTO cards VALUES('node-17d7bbc8f2af27241f6185a3130f3a526ae4095b','9y9ve',replace('**Lady Capulet**\n\nTalk not to me, for I''ll not speak a word:\n\nDo as thou wilt, for I have done with thee.\n\nExit','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',58.0,'0:2:88e99b9b',0);
INSERT OR REPLACE INTO cards VALUES('node-8f4eed117aa168160cb4a1d4c9bb3f3c1c31e49e','9y9ve',replace('**Juliet**\n\nGo, get thee hence, for I will not away.\n\nExit Friar Laurence\n\nWhat''s here? a cup, closed in my true love''s hand?\n\nPoison, I see, hath been his timeless end:\n\nO churl! drunk all, and left no friendly drop\n\nTo help me after? I will kiss thy lips;\n\nHaply some poison yet doth hang on them,\n\nTo make die with a restorative.\n\nKisses him\n\nThy lips are warm.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',30.0,'0:2:890c9639',0);
INSERT OR REPLACE INTO cards VALUES('node-d42558f4fa4e452fdc4d21ee97e692655834509a','9y9ve',replace('**Nurse**\n\nWhat?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',64.0,'0:2:89237ac7',0);
INSERT OR REPLACE INTO cards VALUES('node-197ed6d05f94b3aaec0a90d51ae69d4ea7ed0c35','9y9ve',replace('**Romeo**\n\nLet me stand here till thou remember it.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',48.0,'0:2:892b9e91',0);
INSERT OR REPLACE INTO cards VALUES('node-03a212e561e30e08482911cc1ce915614ca5077a','9y9ve',replace('**Juliet**\n\nO God! did Romeo''s hand shed Tybalt''s blood?','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',12.0,'0:2:89697665',0);
INSERT OR REPLACE INTO cards VALUES('node-814a61fcc5c90bee44b238874e97852305457d56','9y9ve',replace('**Capulet**\n\nWhy, how now, kinsman! wherefore storm you so?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',15.0,'0:2:89752600',0);
INSERT OR REPLACE INTO cards VALUES('node-00cc425b39c788863b69ce8bfa13e55b65dc32d1','9y9ve',replace('**Nurse**\n\nGo, girl, seek happy nights to happy days.\n\nExeunt','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',28.0,'0:2:898f5542',0);
INSERT OR REPLACE INTO cards VALUES('node-47b86faefea11a7a0f11ac32ae66d6ecd88cb68c','9y9ve',replace('**Romeo**\n\nIndeed, I should have ask''d you that before.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',23.0,'0:2:89a05f20',0);
INSERT OR REPLACE INTO cards VALUES('node-a73c7e26c569e76212f2ab3f07c305ed1f42ac36','9y9ve',replace('**First Musician**\n\nAn you re us and fa us, you note us.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',37.0,'0:2:89efa8f',0);
INSERT OR REPLACE INTO cards VALUES('node-a3c5a75459994718d8113d0e26488f54805ec219','9y9ve',replace('**Friar Laurence**\n\nAh, Juliet, I already know thy grief;\n\nIt strains me past the compass of my wits:\n\nI hear thou must, and nothing may prorogue it,\n\nOn Thursday next be married to this county.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',25.0,'0:2:8a2636a3',0);
INSERT OR REPLACE INTO cards VALUES('node-94b3e761579d452951592c4f755db67f053010c7','9y9ve',replace('**Capulet**\n\nGod''s bread! it makes me mad:\n\nDay, night, hour, tide, time, work, play,\n\nAlone, in company, still my care hath been\n\nTo have her match''d: and having now provided\n\nA gentleman of noble parentage,\n\nOf fair demesnes, youthful, and nobly train''d,\n\nStuff''d, as they say, with honourable parts,\n\nProportion''d as one''s thought would wish a man;\n\nAnd then to have a wretched puling fool,\n\nA whining mammet, in her fortune''s tender,\n\nTo answer ''I''ll not wed; I cannot love,\n\nI am too young; I pray you, pardon me.''\n\nBut, as you will not wed, I''ll pardon you:\n\nGraze where you will you shall not house with me:\n\nLook to''t, think on''t, I do not use to jest.\n\nThursday is near; lay hand on heart, advise:\n\nAn you be mine, I''ll give you to my friend;\n\nAnd you be not, hang, beg, starve, die in\n\nthe streets,\n\nFor, by my soul, I''ll ne''er acknowledge thee,\n\nNor what is mine shall never do thee good:\n\nTrust to''t, bethink you; I''ll not be forsworn.\n\nExit','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',56.0,'0:2:8a43e2b5',0);
INSERT OR REPLACE INTO cards VALUES('node-093ad4d62f096b1f8895c32bcc72ea9a1deafef5','9y9ve',replace('**Benvolio**\n\nWhat, art thou hurt?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',32.0,'0:2:8a61b374',0);
INSERT OR REPLACE INTO cards VALUES('node-028bb49de53f32b2b17edb2d2ca1e215d8af8f8d','9y9ve',replace('**Romeo**\n\nTut, I have lost myself; I am not here;\n\nThis is not Romeo, he''s some other where.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',79.0,'0:2:8bfb4',0);
INSERT OR REPLACE INTO cards VALUES('node-af428849f4b7e26c093b2ead9aad4c9b59a0a06e','9y9ve',replace('**Friar Laurence**\n\nSo smile the heavens upon this holy act,\n\nThat after hours with sorrow chide us not!','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',0.0,'0:2:8c028d6',0);
INSERT OR REPLACE INTO cards VALUES('node-4a1f7674c6d467900b6e7ec1690d87dc28245070','9y9ve',replace('**Paris**\n\nOf honourable reckoning are you both;\n\nAnd pity ''tis you lived at odds so long.\n\nBut now, my lord, what say you to my suit?','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',1.0,'0:2:8c64fc53',0);
INSERT OR REPLACE INTO cards VALUES('node-345c877abb34dee3d8b5cbd8a68edef0e415a509','9y9ve',replace('**Juliet**\n\nI''ll look to like, if looking liking move:\n\nBut no more deep will I endart mine eye\n\nThan your consent gives strength to make it fly.\n\nEnter a Servant','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',25.0,'0:2:8c9cf669',0);
INSERT OR REPLACE INTO cards VALUES('node-2259ea914d29a7ddba4367840af28efd24aaa6f0','9y9ve',replace('**Page**\n\nHe came with flowers to strew his lady''s grave;\n\nAnd bid me stand aloof, and so I did:\n\nAnon comes one with light to ope the tomb;\n\nAnd by and by my master drew on him;\n\nAnd then I ran away to call the watch.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',59.0,'0:2:8ca7b759',0);
INSERT OR REPLACE INTO cards VALUES('node-b4664be4140f3abc44c92f424887f8bcb5f4f93f','9y9ve',replace('**Benvolio**\n\nAnd what to?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',4.0,'0:2:8cd16d46',0);
INSERT OR REPLACE INTO cards VALUES('node-1cb58893afcef26b2176927bc0436f96feac410b','9y9ve',replace('**Friar Laurence**\n\nO, she knew well\n\nThy love did read by rote and could not spell.\n\nBut come, young waverer, come, go with me,\n\nIn one respect I''ll thy assistant be;\n\nFor this alliance may so happy prove,\n\nTo turn your households'' rancour to pure love.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',16.0,'0:2:8cff045c',0);
INSERT OR REPLACE INTO cards VALUES('node-63e0fed375ddb434b75ea956a10becf2c1487c5f','9y9ve',replace('**Capulet**\n\nContent thee, gentle coz, let him alone;\n\nHe bears him like a portly gentleman;\n\nAnd, to say truth, Verona brags of him\n\nTo be a virtuous and well-govern''d youth:\n\nI would not for the wealth of all the town\n\nHere in my house do him disparagement:\n\nTherefore be patient, take no note of him:\n\nIt is my will, the which if thou respect,\n\nShow a fair presence and put off these frowns,\n\nAnd ill-beseeming semblance for a feast.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',19.0,'0:2:8d32f300',0);
INSERT OR REPLACE INTO cards VALUES('node-3252a0549da1867dce707c12498e2409170975e6','9y9ve',replace('**Lady Capulet**\n\nWe follow thee.\n\nExit Servant\n\nJuliet, the county stays.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',27.0,'0:2:8d3713c8',0);
INSERT OR REPLACE INTO cards VALUES('node-a7a6209ec7bef3b6681c9f7fe02d57bdf282b7ed','9y9ve',replace('**Romeo**\n\nAnd I''ll still stay, to have thee still forget,\n\nForgetting any other home but this.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',50.0,'0:2:8dabfbdf',0);
INSERT OR REPLACE INTO cards VALUES('node-41c2efd095060a569d15d8a6b768147a58ef7cea','9y9ve',replace('**Mercutio**\n\nThat''s as much as to say, such a case as yours\n\nconstrains a man to bow in the hams.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',18.0,'0:2:8dacf115',0);
INSERT OR REPLACE INTO cards VALUES('node-0db7acd292c1457d5f4286da5b08d29dd3419dbe','9y9ve',replace('**First Servant**\n\nWhere''s Potpan, that he helps not to take away? He\n\nshift a trencher? he scrape a trencher!','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',0.0,'0:2:8ddefb23',0);
INSERT OR REPLACE INTO cards VALUES('node-ffe505b8b3cce8293c6f113abcd16a51821ccb16','9y9ve',replace('**Nurse**\n\nO, she says nothing, sir, but weeps and weeps;\n\nAnd now falls on her bed; and then starts up,\n\nAnd Tybalt calls; and then on Romeo cries,\n\nAnd then down falls again.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',29.0,'0:2:8df926aa',0);
INSERT OR REPLACE INTO cards VALUES('node-478f1903fac8878dffb8422bfb3e25c6bb506a25','9y9ve',replace('**Lady Capulet**\n\nThis is the matter: Nurse, give leave awhile,\n\nWe must talk in secret: Nurse, come back again;\n\nI have remember''d me, thou''s hear our counsel.\n\nThou know''st my daughter''s of a pretty age.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',5.0,'0:2:8e033b93',0);
INSERT OR REPLACE INTO cards VALUES('node-d9dd72aa4c53cc29b8496d110d4425b0c7333608','9y9ve',replace('**Romeo**\n\nO, let us hence; I stand on sudden haste.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',17.0,'0:2:8e9ec9e6',0);
INSERT OR REPLACE INTO cards VALUES('node-bc28ca3e67c116aa2e6fac88f9b25fc6228a7d8a','9y9ve',replace('**Capulet**\n\nO heavens! O wife, look how our daughter bleeds!\n\nThis dagger hath mista''en for, lo, his house\n\nIs empty on the back of Montague,\n\nAnd it mis-sheathed in my daughter''s bosom!','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',46.0,'0:2:8f156cfd',0);
INSERT OR REPLACE INTO cards VALUES('node-def67bab434961ca5db651c1919f2275d6f33d53','9y9ve',replace('**Benvolio**\n\nMadam, an hour before the worshipp''d sun\n\nPeer''d forth the golden window of the east,\n\nA troubled mind drave me to walk abroad;\n\nWhere, underneath the grove of sycamore\n\nThat westward rooteth from the city''s side,\n\nSo early walking did I see your son:\n\nTowards him I made, but he was ware of me\n\nAnd stole into the covert of the wood:\n\nI, measuring his affections by my own,\n\nThat most are busied when they''re most alone,\n\nPursued my humour not pursuing his,\n\nAnd gladly shunn''d who gladly fled from me.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',54.0,'0:2:8f905abd',0);
INSERT OR REPLACE INTO cards VALUES('node-c0755436cab4139e5a9dd90f87ef1a220ff85959','9y9ve',replace('**Capulet**\n\nHa! let me see her: out, alas! she''s cold:\n\nHer blood is settled, and her joints are stiff;\n\nLife and these lips have long been separated:\n\nDeath lies on her like an untimely frost\n\nUpon the sweetest flower of all the field.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',9.0,'0:2:9012cf6',0);
INSERT OR REPLACE INTO cards VALUES('node-29c808527d7dfad6d08c3402603ddc514567c823','9y9ve',replace('**Juliet**\n\n''Tis almost morning; I would have thee gone:\n\nAnd yet no further than a wanton''s bird;\n\nWho lets it hop a little from her hand,\n\nLike a poor prisoner in his twisted gyves,\n\nAnd with a silk thread plucks it back again,\n\nSo loving-jealous of his liberty.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',51.0,'0:2:9017ad62',0);
INSERT OR REPLACE INTO cards VALUES('node-ee7990d9da9831173cae281dde757bcf4a24f23d','9y9ve',replace('**First Citizen**\n\nClubs, bills, and partisans! strike! beat them down!\n\nDown with the Capulets! down with the Montagues!\n\nEnter Capulet in his gown, and Lady Capulet','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',44.0,'0:2:9063ec65',0);
INSERT OR REPLACE INTO cards VALUES('node-cadcbed8c24f00917cd9a34a58729fb5ed7fb6e5','9y9ve',replace('**Nurse**\n\nMy fan, Peter.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',48.0,'0:2:9135ae2a',0);
INSERT OR REPLACE INTO cards VALUES('node-250680da5bf62d511e1c88d2cf84c4c6cb892313','9y9ve',replace('**Romeo**\n\nGive me a torch: I am not for this ambling;\n\nBeing but heavy, I will bear the light.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',2.0,'0:2:915c2978',0);
INSERT OR REPLACE INTO cards VALUES('node-82198a0ad292afe647c9b930437b39ead3df796e','9y9ve',replace('**Nurse**\n\nFaith, here it is.\n\nRomeo is banish''d; and all the world to nothing,\n\nThat he dares ne''er come back to challenge you;\n\nOr, if he do, it needs must be by stealth.\n\nThen, since the case so stands as now it doth,\n\nI think it best you married with the county.\n\nO, he''s a lovely gentleman!\n\nRomeo''s a dishclout to him: an eagle, madam,\n\nHath not so green, so quick, so fair an eye\n\nAs Paris hath. Beshrew my very heart,\n\nI think you are happy in this second match,\n\nFor it excels your first: or if it did not,\n\nYour first is dead; or ''twere as good he were,\n\nAs living here and you no use of him.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',60.0,'0:2:9176971c',0);
INSERT OR REPLACE INTO cards VALUES('node-64f56d67464ed4314a5ee07c46223b62e96b57de','9y9ve',replace('**Romeo**\n\nThen plainly know my heart''s dear love is set\n\nOn the fair daughter of rich Capulet:\n\nAs mine on hers, so hers is set on mine;\n\nAnd all combined, save what thou must combine\n\nBy holy marriage: when and where and how\n\nWe met, we woo''d and made exchange of vow,\n\nI''ll tell thee as we pass; but this I pray,\n\nThat thou consent to marry us to-day.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',9.0,'0:2:918fe4f2',0);
INSERT OR REPLACE INTO cards VALUES('node-c73b9fa84b3b7e02d7101e540dc40f31c7e1efdc','9y9ve',replace('**Capulet**\n\nHow now, my headstrong! where have you been gadding?','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',8.0,'0:2:924966d1',0);
INSERT OR REPLACE INTO cards VALUES('node-37b7a48cddf9d8a2f79d7977d20c809f92a38bf3','9y9ve',replace('**Romeo**\n\nWhen the devout religion of mine eye\n\nMaintains such falsehood, then turn tears to fires;\n\nAnd these, who often drown''d could never die,\n\nTransparent heretics, be burnt for liars!\n\nOne fairer than my love! the all-seeing sun\n\nNe''er saw her match since first the world begun.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',26.0,'0:2:926baed1',0);
INSERT OR REPLACE INTO cards VALUES('node-c34ddc371161d47494dfaeed66957965de696318','9y9ve',replace('**Juliet**\n\nNurse, will you go with me into my closet,\n\nTo help me sort such needful ornaments\n\nAs you think fit to furnish me to-morrow?','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',13.0,'0:2:92ea3166',0);
INSERT OR REPLACE INTO cards VALUES('node-f2098c2fad6c4d2296b562528417270f1fd52755','9y9ve',replace('**Friar Laurence**\n\nHold, daughter: I do spy a kind of hope,\n\nWhich craves as desperate an execution.\n\nAs that is desperate which we would prevent.\n\nIf, rather than to marry County Paris,\n\nThou hast the strength of will to slay thyself,\n\nThen is it likely thou wilt undertake\n\nA thing like death to chide away this shame,\n\nThat copest with death himself to scape from it:\n\nAnd, if thou darest, I''ll give thee remedy.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',27.0,'0:2:930636a7',0);
INSERT OR REPLACE INTO cards VALUES('node-1763f922f7573f10ed6b9a0e37512da7c7b18289','9y9ve',replace('**Montague**\n\nWho set this ancient quarrel new abroach?\n\nSpeak, nephew, were you by when it began?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',51.0,'0:2:931058ed',0);
INSERT OR REPLACE INTO cards VALUES('node-5ebf1be7f755339396d4d140041293a763b7a71a','9y9ve',replace('**Juliet**\n\nWhat devil art thou, that dost torment me thus?\n\nThis torture should be roar''d in dismal hell.\n\nHath Romeo slain himself? say thou but ''I,''\n\nAnd that bare vowel ''I'' shall poison more\n\nThan the death-darting eye of cockatrice:\n\nI am not I, if there be such an I;\n\nOr those eyes shut, that make thee answer ''I.''\n\nIf he be slain, say ''I''; or if not, no:\n\nBrief sounds determine of my weal or woe.','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',6.0,'0:2:9311eec0',0);
INSERT OR REPLACE INTO cards VALUES('node-4b8ec9e226cb0c80fe9ae91706dc65183fa17ddc','9y9ve',replace('**Juliet**\n\nI'' faith, I am sorry that thou art not well.\n\nSweet, sweet, sweet nurse, tell me, what says my love?','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',10.0,'0:2:93288370',0);
INSERT OR REPLACE INTO cards VALUES('node-c18a7af927face8b7c9cc60e29cb3fc938f5f597','9y9ve',replace('**Prince**\n\nWe still have known thee for a holy man.\n\nWhere''s Romeo''s man? what can he say in this?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',56.0,'0:2:933169bd',0);
INSERT OR REPLACE INTO cards VALUES('node-b350bfb6136922c0bbc0e850bd234f52c465c3af','9y9ve',replace('**Juliet**\n\nNurse?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',7.0,'0:2:933d830d',0);
INSERT OR REPLACE INTO cards VALUES('node-1f0521fd859dd9008a0ec686347fedd636039b1c','9y9ve',replace('**Nurse**\n\nLord, how my head aches! what a head have I!\n\nIt beats as it would fall in twenty pieces.\n\nMy back o'' t'' other side, O, my back, my back!\n\nBeshrew your heart for sending me about,\n\nTo catch my death with jaunting up and down!','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',9.0,'0:2:9398c6f6',0);
INSERT OR REPLACE INTO cards VALUES('node-f339e0c305019660ed63d2430f6dba518071d950','9y9ve',replace('**Benvolio**\n\nRomeo! my cousin Romeo!','\n',char(10)),'node-5e06ac8ec784ae358a0c61c64cf0e889bc3d30ef',1.0,'0:2:93bd96ee',0);
INSERT OR REPLACE INTO cards VALUES('node-d881cb083411fac0e8d9715254a425315927245a','9y9ve',replace('**Benvolio**\n\nNo, coz, I rather weep.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',74.0,'0:2:952fcaa7',0);
INSERT OR REPLACE INTO cards VALUES('node-1bdf67aea36ecd2dad567cc02d37aedb0b5bc56c','9y9ve',replace('**Mercutio**\n\nGood king of cats, nothing but one of your nine\n\nlives; that I mean to make bold withal, and as you\n\nshall use me hereafter, drybeat the rest of the\n\neight. Will you pluck your sword out of his pitcher\n\nby the ears? make haste, lest mine be about your\n\nears ere it be out.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',26.0,'0:2:95492689',0);
INSERT OR REPLACE INTO cards VALUES('node-20c78a50613a7f1cc35448d4a7c5c2ab56c45ae8','9y9ve',replace('**Capulet**\n\nYoung Romeo is it?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',17.0,'0:2:95cffe84',0);
INSERT OR REPLACE INTO cards VALUES('node-32b931f48b60a79a24f580ea6643851ce54b3405','9y9ve',replace('**Mercutio**\n\nWhy, may one ask?','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',16.0,'0:2:9634c45d',0);
INSERT OR REPLACE INTO cards VALUES('node-a91f633affd55aa3cce49ae6286f3b30ebc19bef','9y9ve',replace('**Friar Laurence**\n\nGo with me to the vault.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',23.0,'0:2:9683a71f',0);
INSERT OR REPLACE INTO cards VALUES('node-7129246100325f920ba72b0f0e788adf23d44567','9y9ve',replace('**Capulet**\n\nWhy, I am glad on''t; this is well: stand up:\n\nThis is as''t should be. Let me see the county;\n\nAy, marry, go, I say, and fetch him hither.\n\nNow, afore God! this reverend holy friar,\n\nOur whole city is much bound to him.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',12.0,'0:2:968bb773',0);
INSERT OR REPLACE INTO cards VALUES('node-5f2a5bea9aeef82240c8a5d1ce233e253b42723b','9y9ve',replace('**Tybalt**\n\nThou, wretched boy, that didst consort him here,\n\nShalt with him hence.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',43.0,'0:2:968c13ee',0);
INSERT OR REPLACE INTO cards VALUES('node-942c887df8b0d270b473413e3b8862ca39919e8a','9y9ve',replace('**Sampson**\n\nFear me not.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',20.0,'0:2:96a0c37d',0);
INSERT OR REPLACE INTO cards VALUES('node-483c9e731e8e9eda7e82e019ab0f930f16c262c7','9y9ve',replace('**Paris**\n\nPoor soul, thy face is much abused with tears.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',16.0,'0:2:9733b18e',0);
INSERT OR REPLACE INTO cards VALUES('node-7bee4e9b12f598e1b3d461179b437dd9283278d6','9y9ve',replace('**Friar Laurence**\n\nA gentler judgment vanish''d from his lips,\n\nNot body''s death, but body''s banishment.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',4.0,'0:2:9734ab69',0);
INSERT OR REPLACE INTO cards VALUES('node-ef1d54a051e9a01c8346e004439e5de109428a66','9y9ve',replace('**First Watchman**\n\nHold him in safety, till the prince come hither.\n\nRe-enter others of the Watch, with Friar Laurence','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',36.0,'0:2:9764e56f',0);
INSERT OR REPLACE INTO cards VALUES('node-85c75513ea902c727a3c6e44d63952434146c4ef','9y9ve',replace('**Mercutio**\n\nThou art like one of those fellows that when he\n\nenters the confines of a tavern claps me his sword\n\nupon the table and says ''God send me no need of\n\nthee!'' and by the operation of the second cup draws\n\nit on the drawer, when indeed there is no need.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',1.0,'0:2:97849833',0);
INSERT OR REPLACE INTO cards VALUES('node-ed361acd8531f9676f3bf9d7a79a5b9eb88a5cc3','9y9ve',replace('Two households, both alike in dignity,\n\nIn fair Verona, where we lay our scene,\n\nFrom ancient grudge break to new mutiny,\n\nWhere civil blood makes civil hands unclean.\n\nFrom forth the fatal loins of these two foes\n\nA pair of star-cross''d lovers take their life;\n\nWhose misadventured piteous overthrows\n\nDo with their death bury their parents'' strife.\n\nThe fearful passage of their death-mark''d love,\n\nAnd the continuance of their parents'' rage,\n\nWhich, but their children''s end, nought could remove,\n\nIs now the two hours'' traffic of our stage;\n\nThe which if you with patient ears attend,\n\nWhat here shall miss, our toil shall strive to mend.','\n',char(10)),'node-cb1f860179a6439ca5acee77a5532a41772089b9',0.0,'0:2:97e63309',0);
INSERT OR REPLACE INTO cards VALUES('node-cea6dc86be01e35a8e0cdefe54005550189f7db1','9y9ve',replace('**Friar Laurence**\n\nThis same should be the voice of Friar John.\n\nWelcome from Mantua: what says Romeo?\n\nOr, if his mind be writ, give me his letter.','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',1.0,'0:2:97fb60f3',0);
INSERT OR REPLACE INTO cards VALUES('node-4c8ed6a75157cf174e8d73edee6a25ff8626a8ab','9y9ve',replace('**Mercutio**\n\nHelp me into some house, Benvolio,\n\nOr I shall faint. A plague o'' both your houses!\n\nThey have made worms'' meat of me: I have it,\n\nAnd soundly too: your houses!\n\nExeunt Mercutio and Benvolio','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',37.0,'0:2:9810ea8b',0);
INSERT OR REPLACE INTO cards VALUES('node-723e61d52268cfd414ee6aae2410e249433e22aa','9y9ve',replace('**Nurse**\n\nO, he is even in my mistress'' case,\n\nJust in her case! O woful sympathy!\n\nPiteous predicament! Even so lies she,\n\nBlubbering and weeping, weeping and blubbering.\n\nStand up, stand up; stand, and you be a man:\n\nFor Juliet''s sake, for her sake, rise and stand;\n\nWhy should you fall into so deep an O?','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',25.0,'0:2:98127c23',0);
INSERT OR REPLACE INTO cards VALUES('node-e441d3c426d019ad2ae09de46719e6c156a1681c','9y9ve',replace('**Romeo**\n\nI fear, too early: for my mind misgives\n\nSome consequence yet hanging in the stars\n\nShall bitterly begin his fearful date\n\nWith this night''s revels and expire the term\n\nOf a despised life closed in my breast\n\nBy some vile forfeit of untimely death.\n\nBut He, that hath the steerage of my course,\n\nDirect my sail! On, lusty gentlemen.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',26.0,'0:2:982d2b6c',0);
INSERT OR REPLACE INTO cards VALUES('node-1ce35cfa75046377f4f09d6f2129097a6b6ad598','9y9ve',replace('**Benvolio**\n\nAn I were so apt to quarrel as thou art, any man\n\nshould buy the fee-simple of my life for an hour and a quarter.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',6.0,'0:2:983826bb',0);
INSERT OR REPLACE INTO cards VALUES('node-d303ec01a32bedb0699d01ba3e9d0f439dd30e3e','9y9ve',replace('**Romeo**\n\n''Tis the way\n\nTo call hers exquisite, in question more:\n\nThese happy masks that kiss fair ladies'' brows\n\nBeing black put us in mind they hide the fair;\n\nHe that is strucken blind cannot forget\n\nThe precious treasure of his eyesight lost:\n\nShow me a mistress that is passing fair,\n\nWhat doth her beauty serve, but as a note\n\nWhere I may read who pass''d that passing fair?\n\nFarewell: thou canst not teach me to forget.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',93.0,'0:2:98a1e624',0);
INSERT OR REPLACE INTO cards VALUES('node-6856f877fcba0e62c5495e5995443a3b06a2e312','9y9ve',replace('**Second Capulet**\n\n''Tis more, ''tis more, his son is elder, sir;\n\nHis son is thirty.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',9.0,'0:2:98ceb41f',0);
INSERT OR REPLACE INTO cards VALUES('node-43200287955b910aa1e17b95cd8dcf3ac9f6db51','9y9ve',replace('**Romeo**\n\nAnd trust me, love, in my eye so do you:\n\nDry sorrow drinks our blood. Adieu, adieu!\n\nExit','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',16.0,'0:2:99737051',0);
INSERT OR REPLACE INTO cards VALUES('node-79927df9bc5ca1b3d1eea797dda1baa565654537','9y9ve',replace('**Montague**\n\nBoth by myself and many other friends:\n\nBut he, his own affections'' counsellor,\n\nIs to himself. I will not say how true.\n\nBut to himself so secret and so close,\n\nSo far from sounding and discovery,\n\nAs is the bud bit with an envious worm,\n\nEre he can spread his sweet leaves to the air,\n\nOr dedicate his beauty to the sun.\n\nCould we but learn from whence his sorrows grow.\n\nWe would as willingly give cure as know.\n\n```\nEnter Romeo\n```\n\n','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',59.0,'0:2:99865e92',0);
INSERT OR REPLACE INTO cards VALUES('node-2010278506fd33fbefa28a4ba69099e72cb292b7','9y9ve',replace('**Capulet**\n\nReady to go, but never to return.\n\nO son! the night before thy wedding-day\n\nHath Death lain with thy wife. There she lies,\n\nFlower as she was, deflowered by him.\n\nDeath is my son-in-law, Death is my heir;\n\nMy daughter he hath wedded: I will die,\n\nAnd leave him all; life, living, all is Death''s.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',14.0,'0:2:99d357e1',0);
INSERT OR REPLACE INTO cards VALUES('node-7932a760653cda015f536f2426b7676b4f1accd5','9y9ve',replace('**Nurse**\n\nMadam!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',6.0,'0:2:9a0ab699',0);
INSERT OR REPLACE INTO cards VALUES('node-756870b195fc0fe3cc35d67825ec3d622ce98074','9y9ve',replace('**Juliet**\n\nWho is''t that calls? is it my lady mother?\n\nIs she not down so late, or up so early?\n\nWhat unaccustom''d cause procures her hither?\n\nEnter Lady Capulet','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',19.0,'0:2:9a78e5af',0);
INSERT OR REPLACE INTO cards VALUES('node-82bb9eed5cc4215278acccad7e33c8fba70e391e','9y9ve',replace('**Friar Laurence**\n\nWisely and slow; they stumble that run fast.\n\nExeunt','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',18.0,'0:2:9aea0750',0);
INSERT OR REPLACE INTO cards VALUES('node-e660c9a1f6c3ff7febba5a08291d7daefa8b2819','9y9ve',replace('**Sampson**\n\nMy naked weapon is out: quarrel, I will back thee.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',18.0,'0:2:9b170fae',0);
INSERT OR REPLACE INTO cards VALUES('node-8bffaa096089be7f85d38ef53443c3441f337693','9y9ve',replace('**Friar Laurence**\n\nGo hence; good night; and here stands all your state:\n\nEither be gone before the watch be set,\n\nOr by the break of day disguised from hence:\n\nSojourn in Mantua; I''ll find out your man,\n\nAnd he shall signify from time to time\n\nEvery good hap to you that chances here:\n\nGive me thy hand; ''tis late: farewell; good night.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',36.0,'0:2:9b396f02',0);
INSERT OR REPLACE INTO cards VALUES('node-b7065704824419a89703c69f2c6b22c4ef58f64d','9y9ve',replace('**Paris**\n\nHave I thought long to see this morning''s face,\n\nAnd doth it give me such a sight as this?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',15.0,'0:2:9bb10470',0);
INSERT OR REPLACE INTO cards VALUES('node-6aa363d799ff2b88a714dc6baa9017f83a98c387','9y9ve',replace('**Juliet**\n\nMy ears have not yet drunk a hundred words\n\nOf that tongue''s utterance, yet I know the sound:\n\nArt thou not Romeo and a Montague?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',9.0,'0:2:9bbd6949',0);
INSERT OR REPLACE INTO cards VALUES('node-38f8438dde61a869c74f6d639e267c1fdca3aa19','9y9ve',replace('**Second Servant**\n\nI have a head, sir, that will find out logs,\n\nAnd never trouble Peter for the matter.\n\nExit','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',9.0,'0:2:9bdc9053',0);
INSERT OR REPLACE INTO cards VALUES('node-43fc32ca849b8c8ca0d3792a28725acdc53bab5d','9y9ve',replace('**Friar Laurence**\n\nHoly Saint Francis, what a change is here!\n\nIs Rosaline, whom thou didst love so dear,\n\nSo soon forsaken? young men''s love then lies\n\nNot truly in their hearts, but in their eyes.\n\nJesu Maria, what a deal of brine\n\nHath wash''d thy sallow cheeks for Rosaline!\n\nHow much salt water thrown away in waste,\n\nTo season love, that of it doth not taste!\n\nThe sun not yet thy sighs from heaven clears,\n\nThy old groans ring yet in my ancient ears;\n\nLo, here upon thy cheek the stain doth sit\n\nOf an old tear that is not wash''d off yet:\n\nIf e''er thou wast thyself and these woes thine,\n\nThou and these woes were all for Rosaline:\n\nAnd art thou changed? pronounce this sentence then,\n\nWomen may fall, when there''s no strength in men.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',10.0,'0:2:9c3a433d',0);
INSERT OR REPLACE INTO cards VALUES('node-cb369656e42b686c11742accd5be2ecbd20f0bd1','9y9ve',replace('**Paris**\n\nCome you to make confession to this father?','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',10.0,'0:2:9c6382e7',0);
INSERT OR REPLACE INTO cards VALUES('node-d3bf59ebd04393519c6da2943a841057de6101c4','9y9ve',replace('**Romeo**\n\nI take thee at thy word:\n\nCall me but love, and I''ll be new baptized;\n\nHenceforth I never will be Romeo.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',6.0,'0:2:9c8261a5',0);
INSERT OR REPLACE INTO cards VALUES('node-57159d576febbf9289a1c60ae168dbb466731c81','9y9ve',replace('**Sampson**\n\nNo, sir, I do not bite my thumb at you, sir, but I\n\nbite my thumb, sir.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',30.0,'0:2:9caf3474',0);
INSERT OR REPLACE INTO cards VALUES('node-958beb36e8eeed4b267219743cd3f90fde91357a','9y9ve',replace('**Sampson**\n\nI do bite my thumb, sir.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',26.0,'0:2:9cee90cd',0);
INSERT OR REPLACE INTO cards VALUES('node-030688fb937c49e90d51049592f7d1f412b863a0','9y9ve',replace('**First Watchman**\n\nThe ground is bloody; search about the churchyard:\n\nGo, some of you, whoe''er you find attach.\n\nPitiful sight! here lies the county slain,\n\nAnd Juliet bleeding, warm, and newly dead,\n\nWho here hath lain these two days buried.\n\nGo, tell the prince: run to the Capulets:\n\nRaise up the Montagues: some others search:\n\nWe see the ground whereon these woes do lie;\n\nBut the true ground of all these piteous woes\n\nWe cannot without circumstance descry.\n\nRe-enter some of the Watch, with Balthasar','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',34.0,'0:2:9dae2033',0);
INSERT OR REPLACE INTO cards VALUES('node-370d378ef66a14ec55b7f0447d4bf9fa05a5ac12','9y9ve',replace('**Nurse**\n\nRomeo can,\n\nThough heaven cannot: O Romeo, Romeo!\n\nWho ever would have thought it? Romeo!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',5.0,'0:2:9dc91814',0);
INSERT OR REPLACE INTO cards VALUES('node-bfa5324870cc3f15427aab6645b68f28e567a0ee','9y9ve',replace('**First Watchman**\n\nHere is a friar, and slaughter''d Romeo''s man;\n\nWith instruments upon them, fit to open\n\nThese dead men''s tombs.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',45.0,'0:2:9dca0f0c',0);
INSERT OR REPLACE INTO cards VALUES('node-0ed724bbd7342fc69b4a1af4a771e7c421f55a35','9y9ve',replace('**Mercutio**\n\nMore than prince of cats, I can tell you. O, he is\n\nthe courageous captain of compliments. He fights as\n\nyou sing prick-song, keeps time, distance, and\n\nproportion; rests me his minim rest, one, two, and\n\nthe third in your bosom: the very butcher of a silk\n\nbutton, a duellist, a duellist; a gentleman of the\n\nvery first house, of the first and second cause:\n\nah, the immortal passado! the punto reverso! the\n\nhai!','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',10.0,'0:2:9e00f89d',0);
INSERT OR REPLACE INTO cards VALUES('node-f9b8f9d77afaedcaa8d1f4abd4cef921b9f3ff07','9y9ve',replace('**Friar Laurence**\n\nO deadly sin! O rude unthankfulness!\n\nThy fault our law calls death; but the kind prince,\n\nTaking thy part, hath rush''d aside the law,\n\nAnd turn''d that black word death to banishment:\n\nThis is dear mercy, and thou seest it not.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',8.0,'0:2:9e3ab424',0);
INSERT OR REPLACE INTO cards VALUES('node-a7b6bc193e21b6c47bc8d170eb39b97864fa8b1c','9y9ve',replace('**Romeo**\n\nI''ll tell thee, ere thou ask it me again.\n\nI have been feasting with mine enemy,\n\nWhere on a sudden one hath wounded me,\n\nThat''s by me wounded: both our remedies\n\nWithin thy help and holy physic lies:\n\nI bear no hatred, blessed man, for, lo,\n\nMy intercession likewise steads my foe.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',7.0,'0:2:9e549df7',0);
INSERT OR REPLACE INTO cards VALUES('node-0974da261adaee588d6435ea07ad18d6bee16e29','9y9ve',replace('**First Musician**\n\nFaith, we may put up our pipes, and be gone.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',23.0,'0:2:9e7a0f70',0);
INSERT OR REPLACE INTO cards VALUES('node-de538ec17377aff3ad94510eb8e5945442c99e0d','9y9ve',replace('**Benvolio**\n\nHave you importuned him by any means?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',58.0,'0:2:9eff0593',0);
INSERT OR REPLACE INTO cards VALUES('node-13317514461469bc3b7a8ef4826dcddc7e30ce0f','9y9ve',replace('**Tybalt**\n\nFollow me close, for I will speak to them.\n\nGentlemen, good den: a word with one of you.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',10.0,'0:2:9f037cc5',0);
INSERT OR REPLACE INTO cards VALUES('node-ebc2e0a37a470599bc123b1ac8a9d5929b43ed51','9y9ve',replace('**Nurse**\n\nMarry, bachelor,\n\nHer mother is the lady of the house,\n\nAnd a good lady, and a wise and virtuous\n\nI nursed her daughter, that you talk''d withal;\n\nI tell you, he that can lay hold of her\n\nShall have the chinks.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',37.0,'0:2:9fa1c346',0);
INSERT OR REPLACE INTO cards VALUES('node-883f04239d5267d229f5a3c4761461b1d1016e78','9y9ve',replace('**Juliet**\n\nI have.','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',16.0,'0:2:9fc17690',0);
INSERT OR REPLACE INTO cards VALUES('node-85db9093a768994b836a2bda5204750b3b919a77','9y9ve',replace('**Benvolio**\n\nTut, man, one fire burns out another''s burning,\n\nOne pain is lessen''d by another''s anguish;\n\nTurn giddy, and be holp by backward turning;\n\nOne desperate grief cures with another''s languish:\n\nTake thou some new infection to thy eye,\n\nAnd the rank poison of the old will die.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',6.0,'0:2:a00bf34d',0);
INSERT OR REPLACE INTO cards VALUES('node-1d04ac3da98b5cf63dc141c20cdf234d5f528665','9y9ve',replace('**Lady Capulet**\n\nSpeak briefly, can you like of Paris'' love?','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',24.0,'0:2:a06e291b',0);
INSERT OR REPLACE INTO cards VALUES('node-98e0c006a23a025cce270190e45b008d64f9e8a6','9y9ve',replace('**Nurse**\n\nThey call for dates and quinces in the pastry.\n\n```\nEnter Capulet\n```\n\n','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',1.0,'0:2:a0732845',0);
INSERT OR REPLACE INTO cards VALUES('node-b4d609f38e1cd462f8b701d39d5ed5684cad8310','9y9ve',replace('**Capulet**\n\nGo, nurse, go with her: we''ll to church to-morrow.\n\nExeunt Juliet and Nurse','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',15.0,'0:2:a078f585',0);
INSERT OR REPLACE INTO cards VALUES('node-a292d8e8c61a4f2bb2ef8ead4b54ed172899bfe0','9y9ve',replace('**Benvolio**\n\nI pray thee, good Mercutio, let''s retire:\n\nThe day is hot, the Capulets abroad,\n\nAnd, if we meet, we shall not scape a brawl;\n\nFor now, these hot days, is the mad blood stirring.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',0.0,'0:2:a08b6bc5',0);
INSERT OR REPLACE INTO cards VALUES('node-067d5aaaaaa1d94ccc86dd6373ee270b84de42b2','9y9ve',replace('**Romeo**\n\nIn bed asleep, while they do dream things true.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',21.0,'0:2:a0e11ef2',0);
INSERT OR REPLACE INTO cards VALUES('node-b812d0b56074c1577cd9089cb374491d413e5cba','9y9ve',replace('**Romeo**\n\nIs she a Capulet?\n\nO dear account! my life is my foe''s debt.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',38.0,'0:2:a1143dfd',0);
INSERT OR REPLACE INTO cards VALUES('node-bfbb1168b5cf5e08af911e47ca489bda1a358045','9y9ve',replace('**Romeo**\n\nAy, nurse; what of that? both with an R.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',86.0,'0:2:a1219f6a',0);
INSERT OR REPLACE INTO cards VALUES('node-9639b768664003b131bc9fb208116771665ba08c','9y9ve',replace('**Nurse**\n\nYou say well.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',58.0,'0:2:a1a2c0',0);
INSERT OR REPLACE INTO cards VALUES('node-fc504fa044feec833ae91704ab71ebe2f972fd3c','9y9ve',replace('**Mercutio**\n\nCome, sir, your passado.\n\nThey fight','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',29.0,'0:2:a1aae633',0);
INSERT OR REPLACE INTO cards VALUES('node-72ba5785eda6378b2d5c4d3063d72506273b98bf','9y9ve',replace('**Romeo**\n\nBy a name\n\nI know not how to tell thee who I am:\n\nMy name, dear saint, is hateful to myself,\n\nBecause it is an enemy to thee;\n\nHad I it written, I would tear the word.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',8.0,'0:2:a1bde755',0);
INSERT OR REPLACE INTO cards VALUES('node-0d873fb7bafcd4a7af40151295a7f0d85231894c','9y9ve',replace('**Romeo**\n\nBy love, who first did prompt me to inquire;\n\nHe lent me counsel and I lent him eyes.\n\nI am no pilot; yet, wert thou as far\n\nAs that vast shore wash''d with the farthest sea,\n\nI would adventure for such merchandise.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',18.0,'0:2:a1eacddd',0);
INSERT OR REPLACE INTO cards VALUES('node-cf258f1514c5afd5e05546ca85a2dfc8ce9c8458','9y9ve',replace('**Romeo**\n\nThis day''s black fate on more days doth depend;\n\nThis but begins the woe, others must end.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',40.0,'0:2:a1ee9573',0);
INSERT OR REPLACE INTO cards VALUES('node-ef64666372f91beeb1e61167686a90efde6289e5','9y9ve',replace('**Romeo**\n\nCommend me to thy lady.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',88.0,'0:2:a212cb4',0);
INSERT OR REPLACE INTO cards VALUES('node-eec3b822fd50298ad3cd6e3d7984e9b37b2958d6','9y9ve',replace('**Romeo**\n\nI stretch it out for that word ''broad;'' which added\n\nto the goose, proves thee far and wide a broad goose.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',37.0,'0:2:a270eec8',0);
INSERT OR REPLACE INTO cards VALUES('node-08bab77ad1be5c0cb5d557263122e5038e5e8809','9y9ve',replace('**Romeo**\n\nArt thou so bare and full of wretchedness,\n\nAnd fear''st to die? famine is in thy cheeks,\n\nNeed and oppression starveth in thine eyes,\n\nContempt and beggary hangs upon thy back;\n\nThe world is not thy friend nor the world''s law;\n\nThe world affords no law to make thee rich;\n\nThen be not poor, but break it, and take this.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',10.0,'0:2:a2721957',0);
INSERT OR REPLACE INTO cards VALUES('node-577d6fea6d016d154b2a3e177c59bf77bc4a9c1e','9y9ve',replace('**Nurse**\n\n```\nWithin\n```\n\nLet me come in, and you shall know\n\nmy errand;\n\nI come from Lady Juliet.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',21.0,'0:2:a29f0fd2',0);
INSERT OR REPLACE INTO cards VALUES('node-1cdc3a412558d6fbe1edf9f1a4bd8e422d165d78','9y9ve',replace('**Juliet**\n\nSpeakest thou from thy heart?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',61.0,'0:2:a2a87173',0);
INSERT OR REPLACE INTO cards VALUES('node-4509b66002715d33ad5a44d8ec8652699b72dd89','9y9ve',replace('**Romeo**\n\nTybalt, the reason that I have to love thee\n\nDoth much excuse the appertaining rage\n\nTo such a greeting: villain am I none;\n\nTherefore farewell; I see thou know''st me not.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',21.0,'0:2:a2bdefe3',0);
INSERT OR REPLACE INTO cards VALUES('node-b4d06bb709353f8913b2b14ae314e8ba75371ff2','9y9ve',replace('**Juliet**\n\nAt what o''clock to-morrow\n\nShall I send to thee?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',45.0,'0:2:a323d5c0',0);
INSERT OR REPLACE INTO cards VALUES('node-5da6f19f33658db16a99d8f59edda6d0203b56d8','9y9ve',replace('**Romeo**\n\nIf I may trust the flattering truth of sleep,\n\nMy dreams presage some joyful news at hand:\n\nMy bosom''s lord sits lightly in his throne;\n\nAnd all this day an unaccustom''d spirit\n\nLifts me above the ground with cheerful thoughts.\n\nI dreamt my lady came and found me dead\n\nStrange dream, that gives a dead man leave\n\nto think!\n\nAnd breathed such life with kisses in my lips,\n\nThat I revived, and was an emperor.\n\nAh me! how sweet is love itself possess''d,\n\nWhen but love''s shadows are so rich in joy!\n\nEnter Balthasar, booted\n\nNews from Verona! How now, Balthasar!\n\nDost thou not bring me letters from the friar?\n\nHow doth my lady? Is my father well?\n\nHow fares my Juliet? that I ask again;\n\nFor nothing can be ill, if she be well.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',0.0,'0:2:a36f9904',0);
INSERT OR REPLACE INTO cards VALUES('node-c26912acf8cf36082ac31cbf4a362a7daf86b5df','9y9ve',replace('**First Watchman**\n\nA great suspicion: stay the friar too.\n\nEnter the Prience and Attendants','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',38.0,'0:2:a3849811',0);
INSERT OR REPLACE INTO cards VALUES('node-a3f8d9f906fc4afc77d32a8c22b500b895a412fb','9y9ve',replace('**Romeo**\n\nThou wast never with me for any thing when thou wast\n\nnot there for the goose.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',31.0,'0:2:a3ea7a07',0);
INSERT OR REPLACE INTO cards VALUES('node-3dd58e831556ee1f835dcfd14389a728dc5f90de','9y9ve',replace('**Benvolio**\n\nI''ll pay that doctrine, or else die in debt.\n\n`Exeunt`','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',94.0,'0:2:a412dab5',0);
INSERT OR REPLACE INTO cards VALUES('node-66b9c0239bdceeee46d1655c93d35b4c1bac88f7','9y9ve',replace('**Juliet**\n\nWell, do not swear: although I joy in thee,\n\nI have no joy of this contract to-night:\n\nIt is too rash, too unadvised, too sudden;\n\nToo like the lightning, which doth cease to be\n\nEre one can say ''It lightens.'' Sweet, good night!\n\nThis bud of love, by summer''s ripening breath,\n\nMay prove a beauteous flower when next we meet.\n\nGood night, good night! as sweet repose and rest\n\nCome to thy heart as that within my breast!','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',25.0,'0:2:a4284d60',0);
INSERT OR REPLACE INTO cards VALUES('node-aef55110eadf3e7d5ba7f02f3be903d839dff9af','9y9ve',replace('**Romeo**\n\nDo so, and bid my sweet prepare to chide.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',33.0,'0:2:a4327bd4',0);
INSERT OR REPLACE INTO cards VALUES('node-6558953ce0ed5f1fc65793d8e5fbc8dfee18d735','9y9ve',replace('**Second Servant**\n\nWhen good manners shall lie all in one or two men''s\n\nhands and they unwashed too, ''tis a foul thing.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',1.0,'0:2:a45d334e',0);
INSERT OR REPLACE INTO cards VALUES('node-aaf3e8f8a6eaaaa1803e009f823359d5b0fd1b0e','9y9ve',replace('**Romeo**\n\nO blessed, blessed night! I am afeard.\n\nBeing in night, all this is but a dream,\n\nToo flattering-sweet to be substantial.\n\nRe-enter Juliet, above','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',32.0,'0:2:a481ceb5',0);
INSERT OR REPLACE INTO cards VALUES('node-6aa61851aef367da4d566c08446bed8aea4d01d8','9y9ve',replace('**Juliet**\n\nIf I do so, it will be of more price,\n\nBeing spoke behind your back, than to your face.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',15.0,'0:2:a4d19e06',0);
INSERT OR REPLACE INTO cards VALUES('node-587d64023c22972282957e54c5dedc8ef14b1197','9y9ve',replace('**Friar Laurence**\n\nYou say you do not know the lady''s mind:\n\nUneven is the course, I like it not.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',2.0,'0:2:a5930022',0);
INSERT OR REPLACE INTO cards VALUES('node-c68a250db1789970ee538a1801aff258982d4909','9y9ve',replace('**Peter**\n\nI saw no man use you a pleasure; if I had, my weapon\n\nshould quickly have been out, I warrant you: I dare\n\ndraw as soon as another man, if I see occasion in a\n\ngood quarrel, and the law on my side.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',70.0,'0:2:a5c54128',0);
INSERT OR REPLACE INTO cards VALUES('node-1d9d8d564172ff93250a776853222e15c1c17697','9y9ve',replace('**Friar John**\n\nHoly Franciscan friar! brother, ho!\n\nEnter Friar Laurence','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',0.0,'0:2:a63b1ceb',0);
INSERT OR REPLACE INTO cards VALUES('node-791e55bc180c034456cb0f38b084b0625a55d8e2','9y9ve',replace('**Benvolio**\n\nSoft! I will go along;\n\nAn if you leave me so, you do me wrong.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',78.0,'0:2:a670dfc4',0);
INSERT OR REPLACE INTO cards VALUES('node-c8396ab27f66bd92f17f10b4f705ec6300d8ecaf','9y9ve',replace('**Friar Laurence**\n\nThat''s my good son: but where hast thou been, then?','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',6.0,'0:2:a6d720c7',0);
INSERT OR REPLACE INTO cards VALUES('node-65fa0d0c0241365e60c229c0305ebb22f63c6592','9y9ve',replace('**Lady Capulet**\n\nWhat, are you busy, ho? need you my help?','\n',char(10)),'node-5a5e52ac71bf638baf35f7e8ff54a68376bf2aa7',1.0,'0:2:a7042044',0);
INSERT OR REPLACE INTO cards VALUES('node-cc15a079550c1b1e2a706b8e371c96b9957c679b','9y9ve',replace('**Balthasar**\n\nRomeo.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',20.0,'0:2:a736fd02',0);
INSERT OR REPLACE INTO cards VALUES('node-cc45601f9674d4eb692c5082f5d171351c5133be','9y9ve',replace('**Romeo**\n\nTush, thou art deceived:\n\nLeave me, and do the thing I bid thee do.\n\nHast thou no letters to me from the friar?','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',4.0,'0:2:a770c0a',0);
INSERT OR REPLACE INTO cards VALUES('node-3e479f969482452548089dd22587d8efee2b50ae','9y9ve',replace('**Mercutio**\n\nThou hast most kindly hit it.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',20.0,'0:2:a77dbe67',0);
INSERT OR REPLACE INTO cards VALUES('node-352d085d9d5eb1f98b10e387522c77207d3f0717','9y9ve',replace('**Prince**\n\nGive me the letter; I will look on it.\n\nWhere is the county''s page, that raised the watch?\n\nSirrah, what made your master in this place?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',58.0,'0:2:a78b0bdb',0);
INSERT OR REPLACE INTO cards VALUES('node-aaf5a12f6a769b5d68bc9ab4721b37ac337b9951','9y9ve',replace('**Nurse**\n\nO woe! O woful, woful, woful day!\n\nMost lamentable day, most woful day,\n\nThat ever, ever, I did yet behold!\n\nO day! O day! O day! O hateful day!\n\nNever was seen so black a day as this:\n\nO woful day, O woful day!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',17.0,'0:2:a7cb931',0);
INSERT OR REPLACE INTO cards VALUES('node-d65a5683acfd5ec15f9b90f61d5e5221360209d9','9y9ve',replace('**Juliet**\n\nWhat storm is this that blows so contrary?\n\nIs Romeo slaughter''d, and is Tybalt dead?\n\nMy dear-loved cousin, and my dearer lord?\n\nThen, dreadful trumpet, sound the general doom!\n\nFor who is living, if those two are gone?','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',10.0,'0:2:a7fb217',0);
INSERT OR REPLACE INTO cards VALUES('node-129cd009d6917cc99bf80196fbc4489545cb5bd5','9y9ve',replace('**Juliet**\n\nWhat man art thou that thus bescreen''d in night\n\nSo stumblest on my counsel?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',7.0,'0:2:a91013aa',0);
INSERT OR REPLACE INTO cards VALUES('node-fd20cfea9ebab322663b49c7f654cdd9fec64f16','9y9ve',replace('**Romeo**\n\nBid a sick man in sadness make his will:\n\nAh, word ill urged to one that is so ill!\n\nIn sadness, cousin, I do love a woman.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',83.0,'0:2:a96c9b8c',0);
INSERT OR REPLACE INTO cards VALUES('node-19c2820ad8f93abf2dfc06414687bc8850b2a051','9y9ve',replace('**Capulet**\n\nWell get you gone: o'' Thursday be it, then.\n\nGo you to Juliet ere you go to bed,\n\nPrepare her, wife, against this wedding-day.\n\nFarewell, my lord. Light to my chamber, ho!\n\nAfore me! it is so very very late,\n\nThat we may call it early by and by.\n\nGood night.\n\nExeunt','\n',char(10)),'node-1ea0a4f951159826ef795e37aa3321911ddb6685',7.0,'0:2:a973648',0);
INSERT OR REPLACE INTO cards VALUES('node-ed17dbff902d250ec82d7241c954d157fee2bc73','9y9ve',replace('**Mercutio**\n\nNo, ''tis not so deep as a well, nor so wide as a\n\nchurch-door; but ''tis enough,''twill serve: ask for\n\nme to-morrow, and you shall find me a grave man. I\n\nam peppered, I warrant, for this world. A plague o''\n\nboth your houses! ''Zounds, a dog, a rat, a mouse, a\n\ncat, to scratch a man to death! a braggart, a\n\nrogue, a villain, that fights by the book of\n\narithmetic! Why the devil came you between us? I\n\nwas hurt under your arm.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',35.0,'0:2:ab38e869',0);
INSERT OR REPLACE INTO cards VALUES('node-bff28f00c41d3267c5ce13b2e3c12942dfe8beba','9y9ve',replace('**Gregory**\n\nBut thou art not quickly moved to strike.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',5.0,'0:2:ab674aa0',0);
INSERT OR REPLACE INTO cards VALUES('node-4b5a8c800ac5436740c4f0cf5c9c759c5fa6e0d3','9y9ve',replace('**Juliet**\n\nO fortune, fortune! all men call thee fickle:\n\nIf thou art fickle, what dost thou with him.\n\nThat is renown''d for faith? Be fickle, fortune;\n\nFor then, I hope, thou wilt not keep him long,\n\nBut send him back.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',17.0,'0:2:ab94baaf',0);
INSERT OR REPLACE INTO cards VALUES('node-48468fb2eb8d0fe40b60810026700742a7d635ea','9y9ve',replace('**Capulet**\n\nSir Paris, I will make a desperate tender\n\nOf my child''s love: I think she will be ruled\n\nIn all respects by me; nay, more, I doubt it not.\n\nWife, go you to her ere you go to bed;\n\nAcquaint her here of my son Paris'' love;\n\nAnd bid her, mark you me, on Wednesday next \n\nBut, soft! what day is this?','\n',char(10)),'node-1ea0a4f951159826ef795e37aa3321911ddb6685',3.0,'0:2:abe1359a',0);
INSERT OR REPLACE INTO cards VALUES('node-52797f1a33fcbbdc5da997e16041d301d032a16d','9y9ve',replace('**Romeo**\n\nCan I go forward when my heart is here?\n\nTurn back, dull earth, and find thy centre out.\n\nHe climbs the wall, and leaps down within it\n\nEnter Benvolio and Mercutio','\n',char(10)),'node-5e06ac8ec784ae358a0c61c64cf0e889bc3d30ef',0.0,'0:2:abf7ab2e',0);
INSERT OR REPLACE INTO cards VALUES('node-0f94255893fca860dfa27805e4f046f544b44695','9y9ve',replace('**Abraham**\n\nQuarrel sir! no, sir.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',32.0,'0:2:ac370b74',0);
INSERT OR REPLACE INTO cards VALUES('node-2288641d57eff75ce49a47f5897c8d81b1b048d9','9y9ve',replace('**Juliet**\n\nThen, window, let day in, and let life out.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',9.0,'0:2:ad08b6b8',0);
INSERT OR REPLACE INTO cards VALUES('node-092ebbe328653c4800c178d30c79f9440d865b86','9y9ve',replace('**Romeo**\n\nA torch for me: let wantons light of heart\n\nTickle the senseless rushes with their heels,\n\nFor I am proverb''d with a grandsire phrase;\n\nI''ll be a candle-holder, and look on.\n\nThe game was ne''er so fair, and I am done.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',11.0,'0:2:ad10da19',0);
INSERT OR REPLACE INTO cards VALUES('node-864427ff56e7ea6279569fcbf8cd2ffc3de4fba5','9y9ve',replace('**Lady Capulet**\n\nWell, well, thou hast a careful father, child;\n\nOne who, to put thee from thy heaviness,\n\nHath sorted out a sudden day of joy,\n\nThat thou expect''st not nor I look''d not for.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',36.0,'0:2:ad238e76',0);
INSERT OR REPLACE INTO cards VALUES('node-b6ff6362ae4f720df2019e4db63b50ad36c80843','9y9ve',replace('**Juliet**\n\nThat may be, sir, when I may be a wife.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',6.0,'0:2:ad823643',0);
INSERT OR REPLACE INTO cards VALUES('node-658e5f6147aa353a4bcb304257e5b22698aa8960','9y9ve',replace('**Nurse**\n\nIs it good den?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',52.0,'0:2:ade38231',0);
INSERT OR REPLACE INTO cards VALUES('node-448e8f10d9f153ed6078c90d190134bbba868bd1','9y9ve',replace('**Friar Laurence**\n\nCome, is the bride ready to go to church?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',13.0,'0:2:ae53d546',0);
INSERT OR REPLACE INTO cards VALUES('node-3306bfd10329e956d2efa02645610abdc762578d','9y9ve',replace('**Friar Laurence**\n\nThese violent delights have violent ends\n\nAnd in their triumph die, like fire and powder,\n\nWhich as they kiss consume: the sweetest honey\n\nIs loathsome in his own deliciousness\n\nAnd in the taste confounds the appetite:\n\nTherefore love moderately; long love doth so;\n\nToo swift arrives as tardy as too slow.\n\nEnter Juliet\n\nHere comes the lady: O, so light a foot\n\nWill ne''er wear out the everlasting flint:\n\nA lover may bestride the gossamer\n\nThat idles in the wanton summer air,\n\nAnd yet not fall; so light is vanity.','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',2.0,'0:2:ae58a681',0);
INSERT OR REPLACE INTO cards VALUES('node-33d1dc27bffc9292abae4869a84d5ca9cb145a99','9y9ve',replace('**Capulet**\n\nSend for the county; go tell him of this:\n\nI''ll have this knot knit up to-morrow morning.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',10.0,'0:2:aebf5464',0);
INSERT OR REPLACE INTO cards VALUES('node-52574c99da7fecd43e23028be73a3dd845a457ff','9y9ve',replace('**Lady Capulet**\n\nWe shall be short in our provision:\n\n''Tis now near night.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',16.0,'0:2:aee63a87',0);
INSERT OR REPLACE INTO cards VALUES('node-0728c9081f7473f915f1e247353a1b82833a6795','9y9ve',replace('**Romeo**\n\nThere is thy gold, worse poison to men''s souls,\n\nDoing more murders in this loathsome world,\n\nThan these poor compounds that thou mayst not sell.\n\nI sell thee poison; thou hast sold me none.\n\nFarewell: buy food, and get thyself in flesh.\n\nCome, cordial and not poison, go with me\n\nTo Juliet''s grave; for there must I use thee.\n\nExeunt','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',14.0,'0:2:af06e075',0);
INSERT OR REPLACE INTO cards VALUES('node-207043a3df91138bbf6b3a32bea09f7898f80649','9y9ve',replace('**Juliet**\n\nBy whose direction found''st thou out this place?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',17.0,'0:2:af0746e1',0);
INSERT OR REPLACE INTO cards VALUES('node-ad9d2ec96110ee936ecf31b685ff1e6b479eeccd','9y9ve',replace('**Friar Laurence**\n\nCome, come with me, and we will make short work;\n\nFor, by your leaves, you shall not stay alone\n\nTill holy church incorporate two in one.\n\nExeunt','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',8.0,'0:2:af9864aa',0);
INSERT OR REPLACE INTO cards VALUES('node-048d98230cc474317896f03db61a74d532f9e6d5','9y9ve',replace('**Nurse**\n\nA man, young lady! lady, such a man\n\nAs all the world, why, he''s a man of wax.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',19.0,'0:2:afa7bd43',0);
INSERT OR REPLACE INTO cards VALUES('node-9bcf4a5190fc8f488791aa3014e80bfc849c4564','9y9ve',replace('**Mercutio**\n\nTut, dun''s the mouse, the constable''s own word:\n\nIf thou art dun, we''ll draw thee from the mire\n\nOf this sir-reverence love, wherein thou stick''st\n\nUp to the ears. Come, we burn daylight, ho!','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',12.0,'0:2:afe61c68',0);
INSERT OR REPLACE INTO cards VALUES('node-b7feceb3dfbf86564a95fe4202d96e1eb3053bf4','9y9ve',replace('**Lady Capulet**\n\nMarry, that ''marry'' is the very theme\n\nI came to talk of. Tell me, daughter Juliet,\n\nHow stands your disposition to be married?','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',15.0,'0:2:afefc863',0);
INSERT OR REPLACE INTO cards VALUES('node-6bd0b24c9091b85bae16544a27ec29aea47abd03','9y9ve',replace('**Benvolio**\n\nThe date is out of such prolixity:\n\nWe''ll have no Cupid hoodwink''d with a scarf,\n\nBearing a Tartar''s painted bow of lath,\n\nScaring the ladies like a crow-keeper;\n\nNor no without-book prologue, faintly spoke\n\nAfter the prompter, for our entrance:\n\nBut let them measure us by what they will;\n\nWe''ll measure them a measure, and be gone.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',1.0,'0:2:affdde78',0);
INSERT OR REPLACE INTO cards VALUES('node-661e69f72e8fd2e47a8b7b33bc8ba38cfb5da5ac','9y9ve',replace('**Capulet**\n\nNay, gentlemen, prepare not to be gone;\n\nWe have a trifling foolish banquet towards.\n\nIs it e''en so? why, then, I thank you all\n\nI thank you, honest gentlemen; good night.\n\nMore torches here! Come on then, let''s to bed.\n\nAh, sirrah, by my fay, it waxes late:\n\nI''ll to my rest.\n\nExeunt all but Juliet and Nurse','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',41.0,'0:2:b040bf2f',0);
INSERT OR REPLACE INTO cards VALUES('node-41beee9e2e10798a11480f85ac0359cd4ef428ad','9y9ve',replace('**Lady Capulet**\n\nSo shall you feel the loss, but not the friend\n\nWhich you weep for.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',24.0,'0:2:b0cba1db',0);
INSERT OR REPLACE INTO cards VALUES('node-f7f5bc0c52636a7886dbb9eb38a00cd17fd71594','9y9ve',replace('**Sampson**\n\nI strike quickly, being moved.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',4.0,'0:2:b11cd458',0);
INSERT OR REPLACE INTO cards VALUES('node-24d65b9ff65415db14df50f799a203b185d76bb0','9y9ve',replace('**Romeo**\n\nMeaning, to court''sy.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',19.0,'0:2:b14354f4',0);
INSERT OR REPLACE INTO cards VALUES('node-bc99b8c7f466218dac83b71f2b3571de5d532b9e','9y9ve',replace('**Juliet**\n\nMadam, I am here.\n\nWhat is your will?','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',4.0,'0:2:b1ba6869',0);
INSERT OR REPLACE INTO cards VALUES('node-fe74de5cdb66b9942b5e776d44fc2cfd9305fcc9','9y9ve',replace('**Mercutio**\n\nFarewell, ancient lady; farewell,\n\nSinging\n\n''lady, lady, lady.''\n\nExeunt Mercutio and Benvolio','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',66.0,'0:2:b1c64c37',0);
INSERT OR REPLACE INTO cards VALUES('node-7c3bcabfd5f065e84bfb52535126957cbbb44282','9y9ve',replace('**Paris**\n\nHappily met, my lady and my wife!','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',5.0,'0:2:b2786db4',0);
INSERT OR REPLACE INTO cards VALUES('node-eadc17391107f2a4fc8f4358c6315a69cfa40a56','9y9ve',replace('**Peter**\n\nO, I cry you mercy; you are the singer: I will say\n\nfor you. It is ''music with her silver sound,''\n\nbecause musicians have no gold for sounding:\n\n''Then music with her silver sound\n\nWith speedy help doth lend redress.''\n\nExit','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',45.0,'0:2:b28e137a',0);
INSERT OR REPLACE INTO cards VALUES('node-bd5b9c0bfe24157a9976a6ce2808d184771371ea','9y9ve',replace('**Juliet**\n\nWash they his wounds with tears: mine shall be spent,\n\nWhen theirs are dry, for Romeo''s banishment.\n\nTake up those cords: poor ropes, you are beguiled,\n\nBoth you and I; for Romeo is exiled:\n\nHe made you for a highway to my bed;\n\nBut I, a maid, die maiden-widowed.\n\nCome, cords, come, nurse; I''ll to my wedding-bed;\n\nAnd death, not Romeo, take my maidenhead!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',20.0,'0:2:b4011c96',0);
INSERT OR REPLACE INTO cards VALUES('node-c7924d214e0a419eec703d2e26147d513f7f2fbf','9y9ve',replace('**Romeo**\n\nNo matter: get thee gone,\n\nAnd hire those horses; I''ll be with thee straight.\n\nExit Balthasar\n\nWell, Juliet, I will lie with thee to-night.\n\nLet''s see for means: O mischief, thou art swift\n\nTo enter in the thoughts of desperate men!\n\nI do remember an apothecary,\n\nAnd hereabouts he dwells, which late I noted\n\nIn tatter''d weeds, with overwhelming brows,\n\nCulling of simples; meagre were his looks,\n\nSharp misery had worn him to the bones:\n\nAnd in his needy shop a tortoise hung,\n\nAn alligator stuff''d, and other skins\n\nOf ill-shaped fishes; and about his shelves\n\nA beggarly account of empty boxes,\n\nGreen earthen pots, bladders and musty seeds,\n\nRemnants of packthread and old cakes of roses,\n\nWere thinly scatter''d, to make up a show.\n\nNoting this penury, to myself I said\n\n''An if a man did need a poison now,\n\nWhose sale is present death in Mantua,\n\nHere lives a caitiff wretch would sell it him.''\n\nO, this same thought did but forerun my need;\n\nAnd this same needy man must sell it me.\n\nAs I remember, this should be the house.\n\nBeing holiday, the beggar''s shop is shut.\n\nWhat, ho! apothecary!\n\n```\nEnter Apothecary\n```\n\n','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',6.0,'0:2:b4de4bfb',0);
INSERT OR REPLACE INTO cards VALUES('node-039d00b749454c5dbe63bc3e755e34b467822675','9y9ve',replace('**Mercutio**\n\nAnd but one word with one of us? couple it with\n\nsomething; make it a word and a blow.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',11.0,'0:2:b5844d2a',0);
INSERT OR REPLACE INTO cards VALUES('node-45221a5b3834ab1f649651999293b23d40fd111c','9y9ve',replace('**Mercutio**\n\nRight.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',24.0,'0:2:b595540d',0);
INSERT OR REPLACE INTO cards VALUES('node-dd4ec76f385a731ad73b326ca82cff7a4f0fd284','9y9ve',replace('**Juliet**\n\nGood father, I beseech you on my knees,\n\nHear me with patience but to speak a word.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',47.0,'0:2:b59f3d7c',0);
INSERT OR REPLACE INTO cards VALUES('node-fa11b3ec66add534da4a829b8cea3107f1d2f4bf','9y9ve',replace('**Mercutio**\n\nThe fee-simple! O simple!','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',7.0,'0:2:b5af9326',0);
INSERT OR REPLACE INTO cards VALUES('node-59736aac96526eaf8c50584988c888c23187885c','9y9ve',replace('**Juliet**\n\nGood even to my ghostly confessor.','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',3.0,'0:2:b5b6d746',0);
INSERT OR REPLACE INTO cards VALUES('node-b0a04f51b293f0f2599bff31702866030c6108e2','9y9ve',replace('**Nurse**\n\nShe''s dead, deceased, she''s dead; alack the day!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',7.0,'0:2:b6d9118b',0);
INSERT OR REPLACE INTO cards VALUES('node-e163759a8a8b8faef646868565d3dbf88fb6f785','9y9ve',replace('**Juliet**\n\nA rhyme I learn''d even now\n\nOf one I danced withal.\n\nOne calls within ''Juliet.''','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',52.0,'0:2:b79a12cd',0);
INSERT OR REPLACE INTO cards VALUES('node-6577d64d87e7d81a27bf096f128fce4e1aa7aa08','9y9ve',replace('**Juliet**\n\nNow, by Saint Peter''s Church and Peter too,\n\nHe shall not make me there a joyful bride.\n\nI wonder at this haste; that I must wed\n\nEre he, that should be husband, comes to woo.\n\nI pray you, tell my lord and father, madam,\n\nI will not marry yet; and, when I do, I swear,\n\nIt shall be Romeo, whom you know I hate,\n\nRather than Paris. These are news indeed!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',39.0,'0:2:b7bcf78c',0);
INSERT OR REPLACE INTO cards VALUES('node-811c7b3bdfb6ffcbb54648731c0ca739e3bac74e','9y9ve',replace('**Lady Capulet**\n\nShe''s not fourteen.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',7.0,'0:2:b83a452c',0);
INSERT OR REPLACE INTO cards VALUES('node-35783fa2e134b9619d796b9726af6db7c8995756','9y9ve',replace('**Prince**\n\nSeal up the mouth of outrage for a while,\n\nTill we can clear these ambiguities,\n\nAnd know their spring, their head, their\n\ntrue descent;\n\nAnd then will I be general of your woes,\n\nAnd lead you even to death: meantime forbear,\n\nAnd let mischance be slave to patience.\n\nBring forth the parties of suspicion.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',52.0,'0:2:b866387d',0);
INSERT OR REPLACE INTO cards VALUES('node-43003792a4d0e9268219441f658ab166057568e6','9y9ve',replace('**Nurse**\n\nO lamentable day!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',2.0,'0:2:b88d1187',0);
INSERT OR REPLACE INTO cards VALUES('node-6f2eda71bd0b08364346b6748cf3ef6229c5ddf2','9y9ve',replace('**Nurse**\n\nIs your man secret? Did you ne''er hear say,\n\nTwo may keep counsel, putting one away?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',83.0,'0:2:b8b7756f',0);
INSERT OR REPLACE INTO cards VALUES('node-77da00638326eb85532af4801ae71e9e4b34762c','9y9ve',replace('**Tybalt**\n\nWhy, uncle, ''tis a shame.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',22.0,'0:2:b8d5dad8',0);
INSERT OR REPLACE INTO cards VALUES('node-d33dc4ca98bda223c3609b88939bc3d61f84ec92','9y9ve',replace('**Servant**\n\nTo supper; to our house.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',20.0,'0:2:b9e0d2a8',0);
INSERT OR REPLACE INTO cards VALUES('node-42489583da157895041c3c171f2852848f5835f3','9y9ve',replace('**Nurse**\n\nPeter, take my fan, and go before and apace.\n\nExeunt','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',91.0,'0:2:baac7e71',0);
INSERT OR REPLACE INTO cards VALUES('node-a8de81fd0dd71cf40ed3a844be046a5bab07b529','9y9ve',replace('**Benvolio**\n\nO Romeo, Romeo, brave Mercutio''s dead!\n\nThat gallant spirit hath aspired the clouds,\n\nWhich too untimely here did scorn the earth.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',39.0,'0:2:bb010645',0);
INSERT OR REPLACE INTO cards VALUES('node-8a96d9ee68d3334747e867bdf905d139f7a80a0d','9y9ve',replace('**First Musician**\n\nWhat will you give us?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',33.0,'0:2:bb3a4835',0);
INSERT OR REPLACE INTO cards VALUES('node-7771cc13629e535b4bcc12b38693a4d846a2f6f3','9y9ve',replace('**Friar Laurence**\n\nI will be brief, for my short date of breath\n\nIs not so long as is a tedious tale.\n\nRomeo, there dead, was husband to that Juliet;\n\nAnd she, there dead, that Romeo''s faithful wife:\n\nI married them; and their stol''n marriage-day\n\nWas Tybalt''s dooms-day, whose untimely death\n\nBanish''d the new-made bridegroom from the city,\n\nFor whom, and not for Tybalt, Juliet pined.\n\nYou, to remove that siege of grief from her,\n\nBetroth''d and would have married her perforce\n\nTo County Paris: then comes she to me,\n\nAnd, with wild looks, bid me devise some mean\n\nTo rid her from this second marriage,\n\nOr in my cell there would she kill herself.\n\nThen gave I her, so tutor''d by my art,\n\nA sleeping potion; which so took effect\n\nAs I intended, for it wrought on her\n\nThe form of death: meantime I writ to Romeo,\n\nThat he should hither come as this dire night,\n\nTo help to take her from her borrow''d grave,\n\nBeing the time the potion''s force should cease.\n\nBut he which bore my letter, Friar John,\n\nWas stay''d by accident, and yesternight\n\nReturn''d my letter back. Then all alone\n\nAt the prefixed hour of her waking,\n\nCame I to take her from her kindred''s vault;\n\nMeaning to keep her closely at my cell,\n\nTill I conveniently could send to Romeo:\n\nBut when I came, some minute ere the time\n\nOf her awaking, here untimely lay\n\nThe noble Paris and true Romeo dead.\n\nShe wakes; and I entreated her come forth,\n\nAnd bear this work of heaven with patience:\n\nBut then a noise did scare me from the tomb;\n\nAnd she, too desperate, would not go with me,\n\nBut, as it seems, did violence on herself.\n\nAll this I know; and to the marriage\n\nHer nurse is privy: and, if aught in this\n\nMiscarried by my fault, let my old life\n\nBe sacrificed, some hour before his time,\n\nUnto the rigour of severest law.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',55.0,'0:2:bb82caa8',0);
INSERT OR REPLACE INTO cards VALUES('node-54c360b1653908baca01df2d8681894377b5958e','9y9ve',replace('**Benvolio**\n\nHere comes the furious Tybalt back again.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',41.0,'0:2:bbab55cc',0);
INSERT OR REPLACE INTO cards VALUES('node-15010d68538c37307b537290cfc69e00c186d0d9','9y9ve',replace('**First Musician**\n\nWhy ''Heart''s ease?''','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',27.0,'0:2:bbb73cfe',0);
INSERT OR REPLACE INTO cards VALUES('node-0cb631386f4b6bdd2660b547dd4c3780b6a68a73','9y9ve',replace('**First Musician**\n\nAy, by my troth, the case may be amended.\n\n```\nEnter Peter\n```\n\n','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',25.0,'0:2:bbe1ceb7',0);
INSERT OR REPLACE INTO cards VALUES('node-a376d414f4d9915f3b78f0a8c6928f908898f3a5','9y9ve',replace('**Juliet**\n\nHie to high fortune! Honest nurse, farewell.\n\nExeunt','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',18.0,'0:2:bbf8523b',0);
INSERT OR REPLACE INTO cards VALUES('node-4b2fb04d71e2e8e6c193a756de3123594735c6e7','9y9ve',replace('**Romeo**\n\nPink for flower.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',23.0,'0:2:bbf8804a',0);
INSERT OR REPLACE INTO cards VALUES('node-4fdfcef806b22db222ed30576c59f929803a279a','9y9ve',replace('**Romeo**\n\nThe exchange of thy love''s faithful vow for mine.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',28.0,'0:2:bc289927',0);
INSERT OR REPLACE INTO cards VALUES('node-66c408b806d683d40d9d7ceaa0a7c0560df99dc9','9y9ve',replace('**Mercutio**\n\nWell said: follow me this jest now till thou hast\n\nworn out thy pump, that when the single sole of it\n\nis worn, the jest may remain after the wearing sole singular.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',26.0,'0:2:bc360b6f',0);
INSERT OR REPLACE INTO cards VALUES('node-f8f60bc001ed32ba5bd23e4aee7db27ee05a3dff','9y9ve',replace('**Mercutio**\n\nIf love be rough with you, be rough with love;\n\nPrick love for pricking, and you beat love down.\n\nGive me a case to put my visage in:\n\nA visor for a visor! what care I\n\nWhat curious eye doth quote deformities?\n\nHere are the beetle brows shall blush for me.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',9.0,'0:2:bd990c2e',0);
INSERT OR REPLACE INTO cards VALUES('node-2c047a34cd74b5a16f239094ec607c9a4d1e6512','9y9ve',replace('**Montague**\n\nMany a morning hath he there been seen,\n\nWith tears augmenting the fresh morning dew.\n\nAdding to clouds more clouds with his deep sighs;\n\nBut all so soon as the all-cheering sun\n\nShould in the furthest east begin to draw\n\nThe shady curtains from Aurora''s bed,\n\nAway from the light steals home my heavy son,\n\nAnd private in his chamber pens himself,\n\nShuts up his windows, locks far daylight out\n\nAnd makes himself an artificial night:\n\nBlack and portentous must this humour prove,\n\nUnless good counsel may the cause remove.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',55.0,'0:2:bdcadde7',0);
INSERT OR REPLACE INTO cards VALUES('node-d41914109f67ed6d540fa058c41f2fecf87789d4','9y9ve',replace('**Juliet**\n\nGallop apace, you fiery-footed steeds,\n\nTowards Phoebus'' lodging: such a wagoner\n\nAs Phaethon would whip you to the west,\n\nAnd bring in cloudy night immediately.\n\nSpread thy close curtain, love-performing night,\n\nThat runaway''s eyes may wink and Romeo\n\nLeap to these arms, untalk''d of and unseen.\n\nLovers can see to do their amorous rites\n\nBy their own beauties; or, if love be blind,\n\nIt best agrees with night. Come, civil night,\n\nThou sober-suited matron, all in black,\n\nAnd learn me how to lose a winning match,\n\nPlay''d for a pair of stainless maidenhoods:\n\nHood my unmann''d blood, bating in my cheeks,\n\nWith thy black mantle; till strange love, grown bold,\n\nThink true love acted simple modesty.\n\nCome, night; come, Romeo; come, thou day in night;\n\nFor thou wilt lie upon the wings of night\n\nWhiter than new snow on a raven''s back.\n\nCome, gentle night, come, loving, black-brow''d night,\n\nGive me my Romeo; and, when he shall die,\n\nTake him and cut him out in little stars,\n\nAnd he will make the face of heaven so fine\n\nThat all the world will be in love with night\n\nAnd pay no worship to the garish sun.\n\nO, I have bought the mansion of a love,\n\nBut not possess''d it, and, though I am sold,\n\nNot yet enjoy''d: so tedious is this day\n\nAs is the night before some festival\n\nTo an impatient child that hath new robes\n\nAnd may not wear them. O, here comes my nurse,\n\nAnd she brings news; and every tongue that speaks\n\nBut Romeo''s name speaks heavenly eloquence.\n\nEnter Nurse, with cords\n\nNow, nurse, what news? What hast thou there? the cords\n\nThat Romeo bid thee fetch?','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',0.0,'0:2:be1c81ed',0);
INSERT OR REPLACE INTO cards VALUES('node-5304ca524c779e0b5b7c69fd80257298a90d1f8d','9y9ve',replace('**Juliet**\n\nO shut the door! and when thou hast done so,\n\nCome weep with me; past hope, past cure, past help!','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',24.0,'0:2:be6391b5',0);
INSERT OR REPLACE INTO cards VALUES('node-d3581bcaab08b04e7300ca4aff55f1d80635572d','9y9ve',replace('**Peter**\n\nYou will not, then?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',30.0,'0:2:beb26062',0);
INSERT OR REPLACE INTO cards VALUES('node-359af8f6e455e161961519abb4fc8234fa4eacbe','9y9ve',replace('**Juliet**\n\nMadam, in happy time, what day is that?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',37.0,'0:2:bf00d9f8',0);
INSERT OR REPLACE INTO cards VALUES('node-508bb96796e4cb1d23da302e80faa4c595043eec','9y9ve',replace('**Benvolio**\n\nIt was. What sadness lengthens Romeo''s hours?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',66.0,'0:2:bf5b1811',0);
INSERT OR REPLACE INTO cards VALUES('node-fc3ed15cf524025170c64cb981f57da6d452f258','9y9ve',replace('**Capulet**\n\nCome, stir, stir, stir! the second cock hath crow''d,\n\nThe curfew-bell hath rung, ''tis three o''clock:\n\nLook to the baked meats, good Angelica:\n\nSpare not for the cost.','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',2.0,'0:2:bfbb4300',0);
INSERT OR REPLACE INTO cards VALUES('node-4595079722d03b21ab2b2ffdcdeca37b4d1d1e3c','9y9ve',replace('**Nurse**\n\nNo less! nay, bigger; women grow by men.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',23.0,'0:2:bfc19ca6',0);
INSERT OR REPLACE INTO cards VALUES('node-84d7b850fc656738f06b58d154c4075c7cb999a1','9y9ve',replace('**Juliet**\n\nAs much to him, else is his thanks too much.','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',5.0,'0:2:bfc91603',0);
INSERT OR REPLACE INTO cards VALUES('node-b0fef9aa2fa0e9d696efa91929fa0fad2fba1ebd','9y9ve',replace('**Tybalt**\n\nWhat, drawn, and talk of peace! I hate the word,\n\nAs I hate hell, all Montagues, and thee:\n\nHave at thee, coward!\n\n```\nThey fight. Enter, several of both houses, who join the fray; then enter Citizens, with clubs\n```','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',43.0,'0:2:bfed9127',0);
INSERT OR REPLACE INTO cards VALUES('node-18011ea6046b218e6f4309a42e096c6d01d6103d','9y9ve',replace('**Juliet**\n\nYet let me weep for such a feeling loss.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',23.0,'0:2:c006cc9b',0);
INSERT OR REPLACE INTO cards VALUES('node-d9815a970aecc08b5855813d70ad9e0db6649a4d','9y9ve',replace('**Romeo**\n\nLet me be ta''en, let me be put to death;\n\nI am content, so thou wilt have it so.\n\nI''ll say yon grey is not the morning''s eye,\n\n''Tis but the pale reflex of Cynthia''s brow;\n\nNor that is not the lark, whose notes do beat\n\nThe vaulty heaven so high above our heads:\n\nI have more care to stay than will to go:\n\nCome, death, and welcome! Juliet wills it so.\n\nHow is''t, my soul? let''s talk; it is not day.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',3.0,'0:2:c0294c07',0);
INSERT OR REPLACE INTO cards VALUES('node-99ae2a2feefdb62d064c026c34a54dd1457be989','9y9ve',replace('**Benvolio**\n\nStrike, drum.\n\nExeunt','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',27.0,'0:2:c09c42e0',0);
INSERT OR REPLACE INTO cards VALUES('node-61660263e9198cfea0e76e74fc9a1bcdd77a9db3','9y9ve',replace('**Friar Laurence**\n\nWho bare my letter, then, to Romeo?','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',3.0,'0:2:c0b0d6a9',0);
INSERT OR REPLACE INTO cards VALUES('node-b5f557e01dfd0869ce0b3400dcb66919619b8056','9y9ve',replace('**Gregory**\n\nSay ''better:'' here comes one of my master''s kinsmen.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',36.0,'0:2:c0ca9304',0);
INSERT OR REPLACE INTO cards VALUES('node-ae6424715ad89182f0502dc4b16a59d541453470','9y9ve',replace('**Friar Laurence**\n\nThou fond mad man, hear me but speak a word.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',10.0,'0:2:c0fb3986',0);
INSERT OR REPLACE INTO cards VALUES('node-4e00b4f7b606138447015d1878cb3c926490823c','9y9ve',replace('**Romeo**\n\nThis gentleman, the prince''s near ally,\n\nMy very friend, hath got his mortal hurt\n\nIn my behalf; my reputation stain''d\n\nWith Tybalt''s slander, Tybalt, that an hour\n\nHath been my kinsman! O sweet Juliet,\n\nThy beauty hath made me effeminate\n\nAnd in my temper soften''d valour''s steel!\n\nRe-enter Benvolio','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',38.0,'0:2:c10a7ee4',0);
INSERT OR REPLACE INTO cards VALUES('node-868e4eb72328025fce9725192284e0d6539d2c78','9y9ve',replace('**Juliet**\n\nAy, pilgrim, lips that they must use in prayer.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',28.0,'0:2:c162c7cb',0);
INSERT OR REPLACE INTO cards VALUES('node-6cea1884e11760c96854ff91b61903ea5cc2c87e','9y9ve',replace('**Servant**\n\nMy master''s.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',22.0,'0:2:c169fb6c',0);
INSERT OR REPLACE INTO cards VALUES('node-95c22733d8ea2a6dcae7e9efc193d2f7553b6802','9y9ve',replace('**Romeo**\n\nI dream''d a dream to-night.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',17.0,'0:2:c1b0c6a3',0);
INSERT OR REPLACE INTO cards VALUES('node-1518861fe58572abd20bc71bbdbe09060684c396','9y9ve',replace('**Nurse**\n\nThen hie you hence to Friar Laurence'' cell;\n\nThere stays a husband to make you a wife:\n\nNow comes the wanton blood up in your cheeks,\n\nThey''ll be in scarlet straight at any news.\n\nHie you to church; I must another way,\n\nTo fetch a ladder, by the which your love\n\nMust climb a bird''s nest soon when it is dark:\n\nI am the drudge and toil in your delight,\n\nBut you shall bear the burden soon at night.\n\nGo; I''ll to dinner: hie you to the cell.','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',17.0,'0:2:c1cb7e6',0);
INSERT OR REPLACE INTO cards VALUES('node-f8eb7afe81bda41d320100f888bc93467d76e2b8','9y9ve',replace('**Second Musician**\n\nI say ''silver sound,'' because musicians sound for silver.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',42.0,'0:2:c1f72c0a',0);
INSERT OR REPLACE INTO cards VALUES('node-5b7408bda186f6d3930afa7893c90a5025fa4bcc','9y9ve',replace('**Romeo**\n\nI do protest, I never injured thee,\n\nBut love thee better than thou canst devise,\n\nTill thou shalt know the reason of my love:\n\nAnd so, good Capulet, which name I tender\n\nAs dearly as my own, be satisfied.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',23.0,'0:2:c2657b9f',0);
INSERT OR REPLACE INTO cards VALUES('node-a8fbdbcc7eeb897828a905aa8f7375351563da9c','9y9ve',replace('**Nurse**\n\nO Lord, I could have stay''d here all the night\n\nTo hear good counsel: O, what learning is!\n\nMy lord, I''ll tell my lady you will come.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',32.0,'0:2:c2ea6f82',0);
INSERT OR REPLACE INTO cards VALUES('node-1f38e826585411973cca10278ad7b3b2f74818e1','9y9ve',replace('**Mercutio**\n\nI am hurt.\n\nA plague o'' both your houses! I am sped.\n\nIs he gone, and hath nothing?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',31.0,'0:2:c3304871',0);
INSERT OR REPLACE INTO cards VALUES('node-334fd91a3da1f4aa329652655461f8239aa765cc','9y9ve',replace('**Paris**\n\nMy father Capulet will have it so;\n\nAnd I am nothing slow to slack his haste.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',1.0,'0:2:c3b87c03',0);
INSERT OR REPLACE INTO cards VALUES('node-447645ea6988e3127b042410748698aab3922716','9y9ve',replace('**Second Servant**\n\nYou shall have none ill, sir; for I''ll try if they\n\ncan lick their fingers.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',1.0,'0:2:c3c95552',0);
INSERT OR REPLACE INTO cards VALUES('node-30948bb708458d89587a8f0d2b5b5fcf289dd262','9y9ve',replace('**Benvolio**\n\nGo, then; for ''tis in vain\n\nTo seek him here that means not to be found.\n\nExeunt','\n',char(10)),'node-5e06ac8ec784ae358a0c61c64cf0e889bc3d30ef',9.0,'0:2:c3ef5686',0);
INSERT OR REPLACE INTO cards VALUES('node-cee4b9758a5810eaf17114da6dc276061e4ce534','9y9ve',replace('**Mercutio**\n\nAnd so did I.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',18.0,'0:2:c3f6c338',0);
INSERT OR REPLACE INTO cards VALUES('node-7442b36bef4436c9f11541d28e77f4d6b3459643','9y9ve',replace('**Nurse**\n\nAn honour! were not I thine only nurse,\n\nI would say thou hadst suck''d wisdom from thy teat.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',17.0,'0:2:c4a2b129',0);
INSERT OR REPLACE INTO cards VALUES('node-144f48ab8daa226aab0a846860668fc45cffa183','9y9ve',replace('**Romeo**\n\nDraw, Benvolio; beat down their weapons.\n\nGentlemen, for shame, forbear this outrage!\n\nTybalt, Mercutio, the prince expressly hath\n\nForbidden bandying in Verona streets:\n\nHold, Tybalt! good Mercutio!\n\nTybalt under Romeo''s arm stabs Mercutio, and flies with his followers','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',30.0,'0:2:c4f6506c',0);
INSERT OR REPLACE INTO cards VALUES('node-9f546386cdb83c3aea29108023f8f8677e636396','9y9ve',replace('**Juliet**\n\nI will not fail: ''tis twenty years till then.\n\nI have forgot why I did call thee back.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',47.0,'0:2:c5426cc7',0);
INSERT OR REPLACE INTO cards VALUES('node-081a713a3d8c06163d72cb208e72a98c5a6ee13a','9y9ve',replace('**Friar Laurence**\n\nI am the greatest, able to do least,\n\nYet most suspected, as the time and place\n\nDoth make against me of this direful murder;\n\nAnd here I stand, both to impeach and purge\n\nMyself condemned and myself excused.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',53.0,'0:2:c57dc2e6',0);
INSERT OR REPLACE INTO cards VALUES('node-9347f18abc2aeeddb63cca2af10c9606e0f93b55','9y9ve',replace('**Benvolio**\n\nO noble prince, I can discover all\n\nThe unlucky manage of this fatal brawl:\n\nThere lies the man, slain by young Romeo,\n\nThat slew thy kinsman, brave Mercutio.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',52.0,'0:2:c5bdb7d3',0);
INSERT OR REPLACE INTO cards VALUES('node-672b4ed390baa84a1a962b05f13aaff0b8fd1ec0','9y9ve',replace('**Juliet**\n\nO God!O nurse, how shall this be prevented?\n\nMy husband is on earth, my faith in heaven;\n\nHow shall that faith return again to earth,\n\nUnless that husband send it me from heaven\n\nBy leaving earth? comfort me, counsel me.\n\nAlack, alack, that heaven should practise stratagems\n\nUpon so soft a subject as myself!\n\nWhat say''st thou? hast thou not a word of joy?\n\nSome comfort, nurse.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',59.0,'0:2:c63b7fa9',0);
INSERT OR REPLACE INTO cards VALUES('node-7518b0f306c754b147c619c78d2b6bf7fc19deb0','9y9ve',replace('**Nurse**\n\nMarry, that, I think, be young Petrucio.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',45.0,'0:2:c63c9b60',0);
INSERT OR REPLACE INTO cards VALUES('node-f2786b8495c1a130f519f5e1ce16a26a4cf44d94','9y9ve',replace('**Benvolio**\n\nPart, fools!\n\nPut up your swords; you know not what you do.\n\nBeats down their swords\n\n```\nEnter Tybalt\n```\n\n','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',40.0,'0:2:c691d157',0);
INSERT OR REPLACE INTO cards VALUES('node-b7718113c6cfca442c90f1d96de458c63c436894','9y9ve',replace('**Lady Capulet**\n\nThat same villain, Romeo.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',28.0,'0:2:c74d2809',0);
INSERT OR REPLACE INTO cards VALUES('node-2c5400d6488e47e3f2ecaef270ced75c1af7e1ad','9y9ve',replace('**Romeo**\n\nCome hither, man. I see that thou art poor:\n\nHold, there is forty ducats: let me have\n\nA dram of poison, such soon-speeding gear\n\nAs will disperse itself through all the veins\n\nThat the life-weary taker may fall dead\n\nAnd that the trunk may be discharged of breath\n\nAs violently as hasty powder fired\n\nDoth hurry from the fatal cannon''s womb.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',8.0,'0:2:c7fb183',0);
INSERT OR REPLACE INTO cards VALUES('node-2debf5ec74e6af54b74abfc4fc1d6c7e5f19b043','9y9ve',replace('**Benvolio**\n\nBut new struck nine.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',64.0,'0:2:c800bc1f',0);
INSERT OR REPLACE INTO cards VALUES('node-a77fdb3b185102283cf0de8adcb88415a7daf875','9y9ve',replace('**Benvolio**\n\nThere lies that Tybalt.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',49.0,'0:2:c80dec4c',0);
INSERT OR REPLACE INTO cards VALUES('node-20491416dd40cdd6946dc8fa75e280af787a449e','9y9ve',replace('**Lady Capulet**\n\nYou are too hot.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',55.0,'0:2:c845b691',0);
INSERT OR REPLACE INTO cards VALUES('node-ca6bfbe08002bedcbbbba7d6e0e3b6575864b721','9y9ve',replace('**Mercutio**\n\nO calm, dishonourable, vile submission!\n\nAlla stoccata carries it away.\n\nDraws\n\nTybalt, you rat-catcher, will you walk?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',24.0,'0:2:c8798590',0);
INSERT OR REPLACE INTO cards VALUES('node-cff16583e1f40c482dd40176d73329c543b8e089','9y9ve',replace('**Romeo**\n\nAlack, there lies more peril in thine eye\n\nThan twenty of their swords: look thou but sweet,\n\nAnd I am proof against their enmity.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',14.0,'0:2:c892028b',0);
INSERT OR REPLACE INTO cards VALUES('node-e79a95499758e4ad6f7466b168c2aa738319d239','9y9ve',replace('**Benvolio**\n\nAt this same ancient feast of Capulet''s\n\nSups the fair Rosaline whom thou so lovest,\n\nWith all the admired beauties of Verona:\n\nGo thither; and, with unattainted eye,\n\nCompare her face with some that I shall show,\n\nAnd I will make thee think thy swan a crow.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',25.0,'0:2:c8bffb3c',0);
INSERT OR REPLACE INTO cards VALUES('node-6de3d92393681424e998cc1ec2c6fbfc497f529d','9y9ve',replace('**Romeo**\n\nO, she doth teach the torches to burn bright!\n\nIt seems she hangs upon the cheek of night\n\nLike a rich jewel in an Ethiope''s ear;\n\nBeauty too rich for use, for earth too dear!\n\nSo shows a snowy dove trooping with crows,\n\nAs yonder lady o''er her fellows shows.\n\nThe measure done, I''ll watch her place of stand,\n\nAnd, touching hers, make blessed my rude hand.\n\nDid my heart love till now? forswear it, sight!\n\nFor I ne''er saw true beauty till this night.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',13.0,'0:2:c9674483',0);
INSERT OR REPLACE INTO cards VALUES('node-c1d870c1d8033ac823853199e142e4f178ddd867','9y9ve',replace('**Romeo**\n\nI''ll go along, no such sight to be shown,\n\nBut to rejoice in splendor of mine own.\n\nExeunt','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',28.0,'0:2:c982e7ac',0);
INSERT OR REPLACE INTO cards VALUES('node-9c15a280b31df21bb69d6a96c8b6c0b34844e32e','9y9ve',replace('**Romeo**\n\nLady, by yonder blessed moon I swear\n\nThat tips with silver all these fruit-tree tops','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',20.0,'0:2:c9ced8a5',0);
INSERT OR REPLACE INTO cards VALUES('node-091c08ba0752093f8c86494b40d7e5536dbf9199','9y9ve',replace('**Romeo**\n\nI have night''s cloak to hide me from their sight;\n\nAnd but thou love me, let them find me here:\n\nMy life were better ended by their hate,\n\nThan death prorogued, wanting of thy love.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',16.0,'0:2:c9d92a6b',0);
INSERT OR REPLACE INTO cards VALUES('node-0de4717fe08f7e7121b91f4d027cb8b41f8acd11','9y9ve',replace('**Gregory**\n\nThe heads of the maids?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',13.0,'0:2:c9e606b6',0);
INSERT OR REPLACE INTO cards VALUES('node-66078d260861e3575097b6081befd60fbae80cb6','9y9ve',replace('**Benvolio**\n\nWhy dost thou stay?\n\nExit Romeo\n\nEnter Citizens,  & c','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',47.0,'0:2:ca201599',0);
INSERT OR REPLACE INTO cards VALUES('node-4aa6160cb2f377615ef7ead0a1bb21302d6ae8fb','9y9ve',replace('**Nurse**\n\nGo, you cot-quean, go,\n\nGet you to bed; faith, You''ll be sick to-morrow\n\nFor this night''s watching.','\n',char(10)),'node-a1722858da78f25b61ad82162f279424f94ad504',3.0,'0:2:caa622cb',0);
INSERT OR REPLACE INTO cards VALUES('node-a4b8895ea12abd80708613b9a324a3eb6aa0f81d','9y9ve',replace('**Benvolio**\n\nWe talk here in the public haunt of men:\n\nEither withdraw unto some private place,\n\nAnd reason coldly of your grievances,\n\nOr else depart; here all eyes gaze on us.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',16.0,'0:2:cb28e9f5',0);
INSERT OR REPLACE INTO cards VALUES('node-d158fbcd393f98683987fb131f086339d1ab85b5','9y9ve',replace('**Friar Laurence**\n\nI hear some noise. Lady, come from that nest\n\nOf death, contagion, and unnatural sleep:\n\nA greater power than we can contradict\n\nHath thwarted our intents. Come, come away.\n\nThy husband in thy bosom there lies dead;\n\nAnd Paris too. Come, I''ll dispose of thee\n\nAmong a sisterhood of holy nuns:\n\nStay not to question, for the watch is coming;\n\nCome, go, good Juliet,\n\nNoise again\n\nI dare no longer stay.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',29.0,'0:2:cb50ccbb',0);
INSERT OR REPLACE INTO cards VALUES('node-20dff10457a9ef069a1ae11aec424bbacf276880','9y9ve',replace('**Gregory**\n\nHow! turn thy back and run?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',19.0,'0:2:cb7115cf',0);
INSERT OR REPLACE INTO cards VALUES('node-54ed5ac597a1dff9fb48c22dba5b4c47d15e7d60','9y9ve',replace('**Nurse**\n\nWell, you have made a simple choice; you know not\n\nhow to choose a man: Romeo! no, not he; though his\n\nface be better than any man''s, yet his leg excels\n\nall men''s; and for a hand, and a foot, and a body,\n\nthough they be not to be talked on, yet they are\n\npast compare: he is not the flower of courtesy,\n\nbut, I''ll warrant him, as gentle as a lamb. Go thy\n\nways, wench; serve God. What, have you dined at home?','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',7.0,'0:2:cbae30ad',0);
INSERT OR REPLACE INTO cards VALUES('node-cb9a066fdc02352794db68dc05aa843d0ee77d1e','9y9ve',replace('**Capulet**\n\nFor shame, bring Juliet forth; her lord is come.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',6.0,'0:2:cc100819',0);
INSERT OR REPLACE INTO cards VALUES('node-a62a4f64f49ef578a948806be88492a3b44281ac','9y9ve',replace('**Juliet**\n\nNow, good sweet nurse, O Lord, why look''st thou sad?\n\nThough news be sad, yet tell them merrily;\n\nIf good, thou shamest the music of sweet news\n\nBy playing it to me with so sour a face.','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',2.0,'0:2:cc3386f0',0);
INSERT OR REPLACE INTO cards VALUES('node-1e37df91d601edc86aec16c8ba5955a6ad61dc99','9y9ve',replace('**Nurse**\n\nAnon, anon!\n\nCome, let''s away; the strangers all are gone.\n\nExeunt','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',53.0,'0:2:cc7ec674',0);
INSERT OR REPLACE INTO cards VALUES('node-543971cbe105d4c89d7a8e3ee67866373d740c84','9y9ve',replace('**Romeo**\n\nI can tell you; but young Romeo will be older when\n\nyou have found him than he was when you sought him:\n\nI am the youngest of that name, for fault of a worse.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',57.0,'0:2:ccb2363a',0);
INSERT OR REPLACE INTO cards VALUES('node-28118de52932422d896fa1f3ebb3448f0c32e992','9y9ve',replace('**Juliet**\n\nI shall forget, to have thee still stand there,\n\nRemembering how I love thy company.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',49.0,'0:2:ccf28142',0);
INSERT OR REPLACE INTO cards VALUES('node-c86581cbb69e0af19e50fe78c69e77d6af8e4683','9y9ve',replace('**Paris**\n\nThat may be must be, love, on Thursday next.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',7.0,'0:2:ccf3ac39',0);
INSERT OR REPLACE INTO cards VALUES('node-e69b0f08ee5e49936427e71655129c72f4194333','9y9ve',replace('**Capulet**\n\nPeace, you mumbling fool!\n\nUtter your gravity o''er a gossip''s bowl;\n\nFor here we need it not.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',54.0,'0:2:cd764523',0);
INSERT OR REPLACE INTO cards VALUES('node-f5783fa5d071c500886df0ce652e3cd7dc9b57c8','9y9ve',replace('**Balthasar**\n\nIt doth so, holy sir; and there''s my master,\n\nOne that you love.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',18.0,'0:2:cd948e14',0);
INSERT OR REPLACE INTO cards VALUES('node-857ea2ea0963ad38c73dbb561c80a7fe538405ea','9y9ve',replace('**Juliet**\n\nWhere is my mother! why, she is within;\n\nWhere should she be? How oddly thou repliest!\n\n''Your love says, like an honest gentleman,\n\nWhere is your mother?''','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',12.0,'0:2:cda5c76d',0);
INSERT OR REPLACE INTO cards VALUES('node-2e2967cee02ea5a0b90ca62c0a35684621dc79e0','9y9ve',replace('**Juliet**\n\nAmen!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',63.0,'0:2:cdd6a2c1',0);
INSERT OR REPLACE INTO cards VALUES('node-ca6ee86dc48224524d792d612197ee053730a10a','9y9ve',replace('**Nurse**\n\nWill you speak well of him that kill''d your cousin?','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',17.0,'0:2:cdfcbd95',0);
INSERT OR REPLACE INTO cards VALUES('node-28425f4430047be4ae42bf750162035fb699d962','9y9ve',replace('**Benvolio**\n\nShe will indite him to some supper.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',61.0,'0:2:ce772f62',0);
INSERT OR REPLACE INTO cards VALUES('node-cadb5976f3660c70f296f1109d329492344588d4','9y9ve',replace('**Friar Laurence**\n\nNow must I to the monument alone;\n\nWithin three hours will fair Juliet wake:\n\nShe will beshrew me much that Romeo\n\nHath had no notice of these accidents;\n\nBut I will write again to Mantua,\n\nAnd keep her at my cell till Romeo come;\n\nPoor living corse, closed in a dead man''s tomb!\n\nExit','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',7.0,'0:2:ce7e1c15',0);
INSERT OR REPLACE INTO cards VALUES('node-b5543f1f386ff03465531d0cfcc5c3220ab359b5','9y9ve',replace('**Juliet**\n\nIt may be so, for it is not mine own.\n\nAre you at leisure, holy father, now;\n\nOr shall I come to you at evening mass?','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',21.0,'0:2:ce7f7563',0);
INSERT OR REPLACE INTO cards VALUES('node-ba9466a3421f6d85a9a3546866775b60ef169073','9y9ve',replace('**Servant**\n\nPerhaps you have learned it without book: but, I\n\npray, can you read any thing you see?','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',14.0,'0:2:ce833d26',0);
INSERT OR REPLACE INTO cards VALUES('node-d9fdaffe2a11648b4cdf2872bc7a0c3ed7b4e8bd','9y9ve',replace('**Romeo**\n\n```\nTo a Servingman\n```\n\nWhat lady is that, which doth\n\nenrich the hand\n\nOf yonder knight?','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',11.0,'0:2:ceac51fc',0);
INSERT OR REPLACE INTO cards VALUES('node-56516526b29ccbb389b7139a458a8fb17f3eb0a2','9y9ve',replace('**First Citizen**\n\nWhich way ran he that kill''d Mercutio?\n\nTybalt, that murderer, which way ran he?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',48.0,'0:2:cf920449',0);
INSERT OR REPLACE INTO cards VALUES('node-dad46058294f22db54b8fff0ecb648967e063ace','9y9ve',replace('**Romeo**\n\nThou chid''st me oft for loving Rosaline.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',11.0,'0:2:d0463353',0);
INSERT OR REPLACE INTO cards VALUES('node-2195ee8e3b82e1789157af2853024c62278970fd','9y9ve',replace('**Sampson**\n\nIf you do, sir, I am for you: I serve as good a man as you.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',33.0,'0:2:d07c317a',0);
INSERT OR REPLACE INTO cards VALUES('node-1612b32653efee968380f1366e359cd230795257','9y9ve',replace('**Friar Laurence**\n\nOn Thursday, sir? the time is very short.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',0.0,'0:2:d0adca15',0);
INSERT OR REPLACE INTO cards VALUES('node-17f03f3b53fe98b606b081794761be29b339f83d','9y9ve',replace('**Capulet**\n\nWill you tell me that?\n\nHis son was but a ward two years ago.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',10.0,'0:2:d0f9ab0c',0);
INSERT OR REPLACE INTO cards VALUES('node-55153767d53356e25b3db9e9da71230b5d08a579','9y9ve',replace('**Lady Capulet**\n\nA crutch, a crutch! why call you for a sword?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',46.0,'0:2:d1a2fdbf',0);
INSERT OR REPLACE INTO cards VALUES('node-00ec231f6817d14c2ca4e7761735026ae30566dc','9y9ve',replace('**Romeo**\n\nWell, what was yours?','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',19.0,'0:2:d214b594',0);
INSERT OR REPLACE INTO cards VALUES('node-f34dfdc45b0d49b14d026ab961f8769173acc9b9','9y9ve',replace('**Prince**\n\nBenvolio, who began this bloody fray?','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',54.0,'0:2:d24e00ae',0);
INSERT OR REPLACE INTO cards VALUES('node-08863166416c140cd5fb777cad9f7f89d172808d','9y9ve',replace('**Benvolio**\n\nHere comes Romeo, here comes Romeo.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',13.0,'0:2:d26b71b1',0);
INSERT OR REPLACE INTO cards VALUES('node-7315b09046b2e31f29201e54cd58328fa8aa11b9','9y9ve',replace('**Abraham**\n\nDo you bite your thumb at us, sir?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',25.0,'0:2:d27bc301',0);
INSERT OR REPLACE INTO cards VALUES('node-7a4241b218f8b6f26c7b34bba10d23bc95d162e1','9y9ve',replace('**Capulet**\n\nSo many guests invite as here are writ.\n\nExit First Servant\n\nSirrah, go hire me twenty cunning cooks.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',0.0,'0:2:d33ebff4',0);
INSERT OR REPLACE INTO cards VALUES('node-b669b2b20edb6ef9af443a90b44c5a09d22d8b58','9y9ve',replace('**Nurse**\n\nO Tybalt, Tybalt, the best friend I had!\n\nO courteous Tybalt! honest gentleman!\n\nThat ever I should live to see thee dead!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',9.0,'0:2:d35b6ddb',0);
INSERT OR REPLACE INTO cards VALUES('node-9011dddb26cda48264a3a59b4d3535bd1f888cfe','9y9ve',replace('**Romeo**\n\nO, I am fortune''s fool!','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',46.0,'0:2:d3d5c1a1',0);
INSERT OR REPLACE INTO cards VALUES('node-9c46abe126eeb52290e22a39f83366891981b685','9y9ve',replace('**Mercutio**\n\nNay, I''ll conjure too.\n\nRomeo! humours! madman! passion! lover!\n\nAppear thou in the likeness of a sigh:\n\nSpeak but one rhyme, and I am satisfied;\n\nCry but ''Ay me!'' pronounce but ''love'' and ''dove;''\n\nSpeak to my gossip Venus one fair word,\n\nOne nick-name for her purblind son and heir,\n\nYoung Adam Cupid, he that shot so trim,\n\nWhen King Cophetua loved the beggar-maid!\n\nHe heareth not, he stirreth not, he moveth not;\n\nThe ape is dead, and I must conjure him.\n\nI conjure thee by Rosaline''s bright eyes,\n\nBy her high forehead and her scarlet lip,\n\nBy her fine foot, straight leg and quivering thigh\n\nAnd the demesnes that there adjacent lie,\n\nThat in thy likeness thou appear to us!','\n',char(10)),'node-5e06ac8ec784ae358a0c61c64cf0e889bc3d30ef',4.0,'0:2:d3efcf90',0);
INSERT OR REPLACE INTO cards VALUES('node-4907ed79afd61f30e8fb84ed1c0a9faa98e1d423','9y9ve',replace('**Lady Capulet**\n\nAlack the day, she''s dead, she''s dead, she''s dead!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',8.0,'0:2:d404e54',0);
INSERT OR REPLACE INTO cards VALUES('node-ed007369e5034358b7aba938bfbc3bada5226e72','9y9ve',replace('**Romeo**\n\nWhat less than dooms-day is the prince''s doom?','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',3.0,'0:2:d41fc8f3',0);
INSERT OR REPLACE INTO cards VALUES('node-5fa1979a27343d0e2cc7e347e3c0eb0a61dc7e13','9y9ve',replace('**Romeo**\n\nAnd bad''st me bury love.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',13.0,'0:2:d4419fae',0);
INSERT OR REPLACE INTO cards VALUES('node-a898748dd9d4b17ba7c26cbc8834307bc93e06d5','9y9ve',replace('**Nurse**\n\nMarry, I will; and this is wisely done.\n\nExit','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',66.0,'0:2:d4575694',0);
INSERT OR REPLACE INTO cards VALUES('node-0f926485fe073cf5a2010ad6bc6bd6e79700f6b8','9y9ve',replace('**Benvolio**\n\nTybalt, here slain, whom Romeo''s hand did slay;\n\nRomeo that spoke him fair, bade him bethink\n\nHow nice the quarrel was, and urged withal\n\nYour high displeasure: all this uttered\n\nWith gentle breath, calm look, knees humbly bow''d,\n\nCould not take truce with the unruly spleen\n\nOf Tybalt deaf to peace, but that he tilts\n\nWith piercing steel at bold Mercutio''s breast,\n\nWho all as hot, turns deadly point to point,\n\nAnd, with a martial scorn, with one hand beats\n\nCold death aside, and with the other sends\n\nIt back to Tybalt, whose dexterity,\n\nRetorts it: Romeo he cries aloud,\n\n''Hold, friends! friends, part!'' and, swifter than\n\nhis tongue,\n\nHis agile arm beats down their fatal points,\n\nAnd ''twixt them rushes; underneath whose arm\n\nAn envious thrust from Tybalt hit the life\n\nOf stout Mercutio, and then Tybalt fled;\n\nBut by and by comes back to Romeo,\n\nWho had but newly entertain''d revenge,\n\nAnd to ''t they go like lightning, for, ere I\n\nCould draw to part them, was stout Tybalt slain.\n\nAnd, as he fell, did Romeo turn and fly.\n\nThis is the truth, or let Benvolio die.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',55.0,'0:2:d46deb27',0);
INSERT OR REPLACE INTO cards VALUES('node-787957b94f448d6b8fee7e899ccf154f972f2df8','9y9ve',replace('**Juliet**\n\nBy and by, I come: \n\nTo cease thy suit, and leave me to my grief:\n\nTo-morrow will I send.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',37.0,'0:2:d478cbc2',0);
INSERT OR REPLACE INTO cards VALUES('node-2c27c8924f844d1a4c78dadc871b6867fe96b841','9y9ve',replace('**Romeo**\n\nSo shalt thou show me friendship. Take thou that:\n\nLive, and be prosperous: and farewell, good fellow.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',5.0,'0:2:d5c1b7dc',0);
INSERT OR REPLACE INTO cards VALUES('node-c0d95869635adc9c40369f955e3940490b78e50d','9y9ve',replace('**Nurse**\n\nYour mother.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',3.0,'0:2:d5f4fa20',0);
INSERT OR REPLACE INTO cards VALUES('node-e8eebb0b34a734bc68ca911e43c10737ba9c0573','9y9ve',replace('**Juliet**\n\nNo, no: but all this did I know before.\n\nWhat says he of our marriage? what of that?','\n',char(10)),'node-89804a04f41a6fcbd289f576d7252237d821323b',8.0,'0:2:d6640a3b',0);
INSERT OR REPLACE INTO cards VALUES('node-021a2c325f39d39d29b792ff41dfef2462148cde','9y9ve',replace('**Juliet**\n\nWell, thou hast comforted me marvellous much.\n\nGo in: and tell my lady I am gone,\n\nHaving displeased my father, to Laurence'' cell,\n\nTo make confession and to be absolved.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',65.0,'0:2:d68a5902',0);
INSERT OR REPLACE INTO cards VALUES('node-d966f4b6fc73ebf3ed4939cc351c71aa16042355','9y9ve',replace('**Nurse**\n\nI know not.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',47.0,'0:2:d68c192f',0);
INSERT OR REPLACE INTO cards VALUES('node-b50d72a4ce8de6d4bd1cd6fa73c397f88eba1e8d','9y9ve',replace('**Nurse**\n\nNay, he''s a flower; in faith, a very flower.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',21.0,'0:2:d69659a2',0);
INSERT OR REPLACE INTO cards VALUES('node-22f257138d6e38ba49d67c27a0ff6b282cfcd157','9y9ve',replace('**Capulet**\n\nBut Montague is bound as well as I,\n\nIn penalty alike; and ''tis not hard, I think,\n\nFor men so old as we to keep the peace.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',0.0,'0:2:d6a963b2',0);
INSERT OR REPLACE INTO cards VALUES('node-43adb813fa295dcfca8bd8503dacc1a16466ce12','9y9ve',replace('**Mercutio**\n\nIf love be blind, love cannot hit the mark.\n\nNow will he sit under a medlar tree,\n\nAnd wish his mistress were that kind of fruit\n\nAs maids call medlars, when they laugh alone.\n\nRomeo, that she were, O, that she were\n\nAn open et caetera, thou a poperin pear!\n\nRomeo, good night: I''ll to my truckle-bed;\n\nThis field-bed is too cold for me to sleep:\n\nCome, shall we go?','\n',char(10)),'node-5e06ac8ec784ae358a0c61c64cf0e889bc3d30ef',8.0,'0:2:d6ec325f',0);
INSERT OR REPLACE INTO cards VALUES('node-b5039a4a7cf47d9eb901e361b5189fc51ba4e9f3','9y9ve',replace('**Apothecary**\n\nMy poverty, but not my will, consents.','\n',char(10)),'node-fed86a9c8690de30299826b286f34ee2c8e812e0',11.0,'0:2:d72a5c96',0);
INSERT OR REPLACE INTO cards VALUES('node-644cc6177347aee76a49be9eb3a353263167a988','9y9ve',replace('**Romeo**\n\nNot mad, but bound more than a mad-man is;\n\nShut up in prison, kept without my food,\n\nWhipp''d and tormented and God-den, good fellow.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',11.0,'0:2:d74064e5',0);
INSERT OR REPLACE INTO cards VALUES('node-7203a197517dc43a8a639580ac42ea40a11adb70','9y9ve',replace('**Lady Capulet**\n\nHe is a kinsman to the Montague;\n\nAffection makes him false; he speaks not true:\n\nSome twenty of them fought in this black strife,\n\nAnd all those twenty could but kill one life.\n\nI beg for justice, which thou, prince, must give;\n\nRomeo slew Tybalt, Romeo must not live.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',56.0,'0:2:d786f4',0);
INSERT OR REPLACE INTO cards VALUES('node-cd97a4bb56f3781bfd378259abd83d25f1f0d2af','9y9ve',replace('**Lady Capulet**\n\nWe will have vengeance for it, fear thou not:\n\nThen weep no more. I''ll send to one in Mantua,\n\nWhere that same banish''d runagate doth live,\n\nShall give him such an unaccustom''d dram,\n\nThat he shall soon keep Tybalt company:\n\nAnd then, I hope, thou wilt be satisfied.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',32.0,'0:2:d79a40a6',0);
INSERT OR REPLACE INTO cards VALUES('node-5d42aaa336ac3ad9864df3914af06ba295da2bdf','9y9ve',replace('**Nurse**\n\nNow God in heaven bless thee! Hark you, sir.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',81.0,'0:2:d7f67081',0);
INSERT OR REPLACE INTO cards VALUES('node-2bdab99175d99206a5dd62c22c02c8965eb0f63b','9y9ve',replace('**Mercutio**\n\nA sail, a sail!','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',44.0,'0:2:d81298df',0);
INSERT OR REPLACE INTO cards VALUES('node-cdc998988f9e32a30aa256b734d56b511dad0f84','9y9ve',replace('**Mercutio**\n\nGood Peter, to hide her face; for her fan''s the\n\nfairer face.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',49.0,'0:2:d81524f1',0);
INSERT OR REPLACE INTO cards VALUES('node-98e663d66d48b4feeaf591c51f8ceebe59091a32','9y9ve',replace('**Benvolio**\n\nWhy, Romeo, art thou mad?','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',10.0,'0:2:d82ee84',0);
INSERT OR REPLACE INTO cards VALUES('node-3b9d4394100e91abadef5d2fb48687a17ea57e48','9y9ve',replace('**Second Servant**\n\nMarry, sir, ''tis an ill cook that cannot lick his\n\nown fingers: therefore he that cannot lick his\n\nfingers goes not with me.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',3.0,'0:2:d8f464b7',0);
INSERT OR REPLACE INTO cards VALUES('node-dd213aef21045ee3f70fe3e4ca3c7b7b6cfc569c','9y9ve',replace('**Nurse**\n\nTybalt is gone, and Romeo banished;\n\nRomeo that kill''d him, he is banished.','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',11.0,'0:2:d96d7663',0);
INSERT OR REPLACE INTO cards VALUES('node-7a1c98241a921b381e0e43f51a30f031e6fa37f8','9y9ve',replace('**Friar Laurence**\n\nSir, go you in; and, madam, go with him;\n\nAnd go, Sir Paris; every one prepare\n\nTo follow this fair corse unto her grave:\n\nThe heavens do lour upon you for some ill;\n\nMove them no more by crossing their high will.\n\nExeunt Capulet, Lady Capulet, Paris, and Friar Laurence','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',22.0,'0:2:d978fbad',0);
INSERT OR REPLACE INTO cards VALUES('node-70b522558e1e4b72bed4b9d24f05fcfb1478b619','9y9ve',replace('**Servant**\n\nNow I''ll tell you without asking: my master is the\n\ngreat rich Capulet; and if you be not of the house\n\nof Montagues, I pray, come and crush a cup of wine.\n\nRest you merry!\n\nExit','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',24.0,'0:2:d9a0fcc3',0);
INSERT OR REPLACE INTO cards VALUES('node-bdd6d2d2af29d52285617322305bff7762ff4ffc','9y9ve',replace('**Servant**\n\nYe say honestly: rest you merry!','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',16.0,'0:2:d9ad10a0',0);
INSERT OR REPLACE INTO cards VALUES('node-ad3dac8e981e7abda742a6c251c2667e6ced0c7e','9y9ve',replace('**Capulet**\n\nDespised, distressed, hated, martyr''d, kill''d!\n\nUncomfortable time, why camest thou now\n\nTo murder, murder our solemnity?\n\nO child! O child! my soul, and not my child!\n\nDead art thou! Alack! my child is dead;\n\nAnd with my child my joys are buried.','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',19.0,'0:2:d9ad5033',0);
INSERT OR REPLACE INTO cards VALUES('node-dd141c39665aff4e710203fe8841856b27d19a16','9y9ve',replace('**Tybalt**\n\nRomeo, the hate I bear thee can afford\n\nNo better term than this, thou art a villain.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',20.0,'0:2:da0d52fb',0);
INSERT OR REPLACE INTO cards VALUES('node-0ee792e683a22abc04aa5781d24dbd215edb9148','9y9ve',replace('**Balthasar**\n\nI brought my master news of Juliet''s death;\n\nAnd then in post he came from Mantua\n\nTo this same place, to this same monument.\n\nThis letter he early bid me give his father,\n\nAnd threatened me with death, going in the vault,\n\nI departed not and left him there.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',57.0,'0:2:dbd343a3',0);
INSERT OR REPLACE INTO cards VALUES('node-ac171711c613fe277f4872562b2015eff283f21a','9y9ve',replace('**Nurse**\n\nHonest goodfellows, ah, put up, put up;\n\nFor, well you know, this is a pitiful case.\n\nExit','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',24.0,'0:2:dd6b9182',0);
INSERT OR REPLACE INTO cards VALUES('node-60c9730c4c63ee54a232f9015fc3fbefe33de2e4','9y9ve',replace('**Romeo**\n\nBut that a joy past joy calls out on me,\n\nIt were a grief, so brief to part with thee: Farewell.\n\nExeunt','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',37.0,'0:2:dd9f732c',0);
INSERT OR REPLACE INTO cards VALUES('node-b17ff6d6c6797ad42017bccdc03942bd02434d40','9y9ve',replace('**Romeo**\n\nWith love''s light wings did I o''er-perch these walls;\n\nFor stony limits cannot hold love out,\n\nAnd what love can do that dares love attempt;\n\nTherefore thy kinsmen are no let to me.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',12.0,'0:2:dda47d34',0);
INSERT OR REPLACE INTO cards VALUES('node-00a24bd2f71c0ffc00759d9612dc0b75785c6a04','9y9ve',replace('**Romeo**\n\nI doubt it not; and all these woes shall serve\n\nFor sweet discourses in our time to come.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',14.0,'0:2:ddb1520d',0);
INSERT OR REPLACE INTO cards VALUES('node-53b912c8d95b2d2e0c8807d0d16d5030ea71fe5e','9y9ve',replace('**Juliet**\n\nAncient damnation! O most wicked fiend!\n\nIs it more sin to wish me thus forsworn,\n\nOr to dispraise my lord with that same tongue\n\nWhich she hath praised him with above compare\n\nSo many thousand times? Go, counsellor;\n\nThou and my bosom henceforth shall be twain.\n\nI''ll to the friar, to know his remedy:\n\nIf all else fail, myself have power to die.\n\nExit','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',67.0,'0:2:dde84ede',0);
INSERT OR REPLACE INTO cards VALUES('node-2e048eb6a834a9e0c9818f074f8245e4da28dde1','9y9ve',replace('**Romeo**\n\nAy, mine own fortune in my misery.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',13.0,'0:2:de8bf483',0);
INSERT OR REPLACE INTO cards VALUES('node-83e9eac3050e6d5085d9f452abf801c05166c785','9y9ve',replace('**Tybalt**\n\nI am for you.\n\nDrawing','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',27.0,'0:2:dece3965',0);
INSERT OR REPLACE INTO cards VALUES('node-2bb3c6618e7b868904f15e145abdc508c85369a3','9y9ve',replace('**Servant**\n\nI know not, sir.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',12.0,'0:2:ded42ad',0);
INSERT OR REPLACE INTO cards VALUES('node-c65bd3e719e1711aa1c9484e38c42d6b8c3a5331','9y9ve',replace('**Sampson**\n\nYes, better, sir.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',37.0,'0:2:deeda249',0);
INSERT OR REPLACE INTO cards VALUES('node-98a400aba6a52b4914b54bb7f5fe19114ca0571d','9y9ve',replace('**Romeo**\n\nIt was the lark, the herald of the morn,\n\nNo nightingale: look, love, what envious streaks\n\nDo lace the severing clouds in yonder east:\n\nNight''s candles are burnt out, and jocund day\n\nStands tiptoe on the misty mountain tops.\n\nI must be gone and live, or stay and die.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',1.0,'0:2:df31b89e',0);
INSERT OR REPLACE INTO cards VALUES('node-881e1e096d893c54437240660cd4d880744d39fc','9y9ve',replace('**Lady Capulet**\n\nWell, think of marriage now; younger than you,\n\nHere in Verona, ladies of esteem,\n\nAre made already mothers: by my count,\n\nI was your mother much upon these years\n\nThat you are now a maid. Thus then in brief:\n\nThe valiant Paris seeks you for his love.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',18.0,'0:2:df8e5451',0);
INSERT OR REPLACE INTO cards VALUES('node-1b940fc2da0e01922093a2b2b8ae4c2450120063','9y9ve',replace('**Nurse**\n\nHis name is Romeo, and a Montague;\n\nThe only son of your great enemy.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',49.0,'0:2:dfe03d75',0);
INSERT OR REPLACE INTO cards VALUES('node-a94a4cbd3de14e44f0ee765c0c2f1ca9739e4da0','9y9ve',replace('**Mercutio**\n\nAy, ay, a scratch, a scratch; marry, ''tis enough.\n\nWhere is my page? Go, villain, fetch a surgeon.\n\nExit Page','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',33.0,'0:2:e03a5687',0);
INSERT OR REPLACE INTO cards VALUES('node-22761ff136c383c73334d0313a2e014e343aa921','9y9ve',replace('**Romeo**\n\nO, wilt thou leave me so unsatisfied?','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',26.0,'0:2:e0d3128f',0);
INSERT OR REPLACE INTO cards VALUES('node-404c5b800ffa8efeea2d976eed122e8addb25123','9y9ve',replace('**Sampson**\n\nA dog of the house of Montague moves me.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',6.0,'0:2:e0e92e2f',0);
INSERT OR REPLACE INTO cards VALUES('node-f90c6817f6561450af11e36bda38296df2ba1d9e','9y9ve',replace('**Benvolio**\n\nAlas, that love, so gentle in his view,\n\nShould be so tyrannous and rough in proof!','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',72.0,'0:2:e11ca050',0);
INSERT OR REPLACE INTO cards VALUES('node-629ac527785e7d4cf207fe6ef974e0f56c8fc590','9y9ve',replace('**Friar Laurence**\n\nHold, then; go home, be merry, give consent\n\nTo marry Paris: Wednesday is to-morrow:\n\nTo-morrow night look that thou lie alone;\n\nLet not thy nurse lie with thee in thy chamber:\n\nTake thou this vial, being then in bed,\n\nAnd this distilled liquor drink thou off;\n\nWhen presently through all thy veins shall run\n\nA cold and drowsy humour, for no pulse\n\nShall keep his native progress, but surcease:\n\nNo warmth, no breath, shall testify thou livest;\n\nThe roses in thy lips and cheeks shall fade\n\nTo paly ashes, thy eyes'' windows fall,\n\nLike death, when he shuts up the day of life;\n\nEach part, deprived of supple government,\n\nShall, stiff and stark and cold, appear like death:\n\nAnd in this borrow''d likeness of shrunk death\n\nThou shalt continue two and forty hours,\n\nAnd then awake as from a pleasant sleep.\n\nNow, when the bridegroom in the morning comes\n\nTo rouse thee from thy bed, there art thou dead:\n\nThen, as the manner of our country is,\n\nIn thy best robes uncover''d on the bier\n\nThou shalt be borne to that same ancient vault\n\nWhere all the kindred of the Capulets lie.\n\nIn the mean time, against thou shalt awake,\n\nShall Romeo by my letters know our drift,\n\nAnd hither shall he come: and he and I\n\nWill watch thy waking, and that very night\n\nShall Romeo bear thee hence to Mantua.\n\nAnd this shall free thee from this present shame;\n\nIf no inconstant toy, nor womanish fear,\n\nAbate thy valour in the acting it.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',29.0,'0:2:e1876808',0);
INSERT OR REPLACE INTO cards VALUES('node-14d769c62e9e518af5018f45ebdc41f0ce370991','9y9ve',replace('**Juliet**\n\nIs there no pity sitting in the clouds,\n\nThat sees into the bottom of my grief?\n\nO, sweet my mother, cast me not away!\n\nDelay this marriage for a month, a week;\n\nOr, if you do not, make the bridal bed\n\nIn that dim monument where Tybalt lies.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',57.0,'0:2:e1cedf',0);
INSERT OR REPLACE INTO cards VALUES('node-cdc119ae1b691edb5b05c55637474c3d5a19e6a7','9y9ve',replace('**Romeo**\n\nNot having that, which, having, makes them short.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',67.0,'0:2:e22ce7ee',0);
INSERT OR REPLACE INTO cards VALUES('node-e6fe58f256ce7644a54eedcf42e421f20c0ddda6','9y9ve',replace('**Benvolio**\n\nWhy, what is Tybalt?','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',9.0,'0:2:e25aa10e',0);
INSERT OR REPLACE INTO cards VALUES('node-bdb368041715583124089b8662a58c9253c20e4e','9y9ve',replace('**Lady Capulet**\n\nNo, not till Thursday; there is time enough.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',14.0,'0:2:e2771ca1',0);
INSERT OR REPLACE INTO cards VALUES('node-c8c318379eaea1aec92277fd455268a86790af20','9y9ve',replace('**Mercutio**\n\nI mean, sir, in delay\n\nWe waste our lights in vain, like lamps by day.\n\nTake our good meaning, for our judgment sits\n\nFive times in that ere once in our five wits.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',14.0,'0:2:e28ea227',0);
INSERT OR REPLACE INTO cards VALUES('node-9a6c364431a8f59526f9f80771663ce36052cf77','9y9ve',replace('**Juliet**\n\nDo not swear at all;\n\nOr, if thou wilt, swear by thy gracious self,\n\nWhich is the god of my idolatry,\n\nAnd I''ll believe thee.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',23.0,'0:2:e2bc2b44',0);
INSERT OR REPLACE INTO cards VALUES('node-16d3f42200d33e0377acbdc6d1edc8dc4df2b563','9y9ve',replace('**Romeo**\n\nThere is no world without Verona walls,\n\nBut purgatory, torture, hell itself.\n\nHence-banished is banish''d from the world,\n\nAnd world''s exile is death: then banished,\n\nIs death mis-term''d: calling death banishment,\n\nThou cutt''st my head off with a golden axe,\n\nAnd smilest upon the stroke that murders me.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',7.0,'0:2:e2fb0941',0);
INSERT OR REPLACE INTO cards VALUES('node-3baf02c34e770d667f57be1e3acb69bc1e27b301','9y9ve',replace('**Juliet**\n\nI met the youthful lord at Laurence'' cell;\n\nAnd gave him what becomed love I might,\n\nNot step o''er the bounds of modesty.','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',11.0,'0:2:e3140926',0);
INSERT OR REPLACE INTO cards VALUES('node-191eb73c1551464df259dc45a641504a67c24efe','9y9ve',replace('**Romeo**\n\nHere''s goodly gear!\n\nEnter Nurse and Peter','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',43.0,'0:2:e3e5c731',0);
INSERT OR REPLACE INTO cards VALUES('node-b0bd6dd4e364be6b03c460f34cfba22f5ffc4b53','9y9ve',replace('**Nurse**\n\nMadam, your mother craves a word with you.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',35.0,'0:2:e3ef3870',0);
INSERT OR REPLACE INTO cards VALUES('node-9719510a46696d7d2300ffc6a16d2425ddad26e0','9y9ve',replace('**Romeo**\n\nO, then, dear saint, let lips do what hands do;\n\nThey pray, grant thou, lest faith turn to despair.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',29.0,'0:2:e40d2695',0);
INSERT OR REPLACE INTO cards VALUES('node-9e6b1dd8592e04fbf70098352138a1bf78b11440','9y9ve',replace('**Lady Montague**\n\nO, where is Romeo? saw you him to-day?\n\nRight glad I am he was not at this fray.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',53.0,'0:2:e437b21b',0);
INSERT OR REPLACE INTO cards VALUES('node-2be2d895e25e786338f17baeebd563cee202a755','9y9ve',replace('**Lady Capulet**\n\nFind thou the means, and I''ll find such a man.\n\nBut now I''ll tell thee joyful tidings, girl.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',34.0,'0:2:e455e681',0);
INSERT OR REPLACE INTO cards VALUES('node-7038d0ed17861e3ff4c3952c38f429b90359148b','9y9ve',replace('**Romeo**\n\nO, teach me how I should forget to think.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',91.0,'0:2:e4873fae',0);
INSERT OR REPLACE INTO cards VALUES('node-b73d0a1e3278d2f2984e35c93cbbd2d5347dcb82','9y9ve',replace('**Nurse**\n\nAh, well-a-day! he''s dead, he''s dead, he''s dead!\n\nWe are undone, lady, we are undone!\n\nAlack the day! he''s gone, he''s kill''d, he''s dead!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',3.0,'0:2:e4a39d48',0);
INSERT OR REPLACE INTO cards VALUES('node-28b9d7b61fefa0db9fe5d0ae23b0486b332c7841','9y9ve',replace('**Nurse**\n\nNo truly sir; not a penny.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',77.0,'0:2:e509b4a9',0);
INSERT OR REPLACE INTO cards VALUES('node-e2e7d1cd673e4c0877c2665377677c6882dd0b47','9y9ve',replace('**Capulet**\n\nWhen the sun sets, the air doth drizzle dew;\n\nBut for the sunset of my brother''s son\n\nIt rains downright.\n\nHow now! a conduit, girl? what, still in tears?\n\nEvermore showering? In one little body\n\nThou counterfeit''st a bark, a sea, a wind;\n\nFor still thy eyes, which I may call the sea,\n\nDo ebb and flow with tears; the bark thy body is,\n\nSailing in this salt flood; the winds, thy sighs;\n\nWho, raging with thy tears, and they with them,\n\nWithout a sudden calm, will overset\n\nThy tempest-tossed body. How now, wife!\n\nHave you deliver''d to her our decree?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',41.0,'0:2:e526e033',0);
INSERT OR REPLACE INTO cards VALUES('node-32735a7aacf677fcac7b5a09170d82392b77b9aa','9y9ve',replace('**Abraham**\n\nYou lie.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',38.0,'0:2:e5857a5',0);
INSERT OR REPLACE INTO cards VALUES('node-37c12d8dc72387d83b99f5c9c0ffa59c42009d5f','9y9ve',replace('**Benvolio**\n\nTut, you saw her fair, none else being by,\n\nHerself poised with herself in either eye:\n\nBut in that crystal scales let there be weigh''d\n\nYour lady''s love against some other maid\n\nThat I will show you shining at this feast,\n\nAnd she shall scant show well that now shows best.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',27.0,'0:2:e59844ee',0);
INSERT OR REPLACE INTO cards VALUES('node-9ee73d1ce79ebb859701eb04cc4cc5de8b5b4496','9y9ve',replace('**Nurse**\n\nYour lady mother is coming to your chamber:\n\nThe day is broke; be wary, look about.\n\nExit','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',8.0,'0:2:e5c1415',0);
INSERT OR REPLACE INTO cards VALUES('node-3490fe0781745aa21c2272eb2c3de31941df8478','9y9ve',replace('**Juliet**\n\nMy only love sprung from my only hate!\n\nToo early seen unknown, and known too late!\n\nProdigious birth of love it is to me,\n\nThat I must love a loathed enemy.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',50.0,'0:2:e68ce6a5',0);
INSERT OR REPLACE INTO cards VALUES('node-621b0f25cbe4dbc31c633bdef887ab51bdf6822d','9y9ve',replace('**Servant**\n\nMadam, the guests are come, supper served up, you\n\ncalled, my young lady asked for, the nurse cursed in\n\nthe pantry, and every thing in extremity. I must\n\nhence to wait; I beseech you, follow straight.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',26.0,'0:2:e71fdd6d',0);
INSERT OR REPLACE INTO cards VALUES('node-589a9d662d66f33fc35f28042dd868717a9ecde2','9y9ve',replace('**Romeo**\n\nGood morrow, father.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',1.0,'0:2:e7930306',0);
INSERT OR REPLACE INTO cards VALUES('node-51365e009e1f9e87fc6aa2b6cf5d1fdace446346','9y9ve',replace('**Gregory**\n\nNo, for then we should be colliers.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',1.0,'0:2:e8082443',0);
INSERT OR REPLACE INTO cards VALUES('node-904d8ec691e35ce10bbe1747c215c31dcb641fd9','9y9ve',replace('**Friar Laurence**\n\nThe grey-eyed morn smiles on the frowning night,\n\nChequering the eastern clouds with streaks of light,\n\nAnd flecked darkness like a drunkard reels\n\nFrom forth day''s path and Titan''s fiery wheels:\n\nNow, ere the sun advance his burning eye,\n\nThe day to cheer and night''s dank dew to dry,\n\nI must up-fill this osier cage of ours\n\nWith baleful weeds and precious-juiced flowers.\n\nThe earth that''s nature''s mother is her tomb;\n\nWhat is her burying grave that is her womb,\n\nAnd from her womb children of divers kind\n\nWe sucking on her natural bosom find,\n\nMany for many virtues excellent,\n\nNone but for some and yet all different.\n\nO, mickle is the powerful grace that lies\n\nIn herbs, plants, stones, and their true qualities:\n\nFor nought so vile that on the earth doth live\n\nBut to the earth some special good doth give,\n\nNor aught so good but strain''d from that fair use\n\nRevolts from true birth, stumbling on abuse:\n\nVirtue itself turns vice, being misapplied;\n\nAnd vice sometimes by action dignified.\n\nWithin the infant rind of this small flower\n\nPoison hath residence and medicine power:\n\nFor this, being smelt, with that part cheers each part;\n\nBeing tasted, slays all senses with the heart.\n\nTwo such opposed kings encamp them still\n\nIn man as well as herbs, grace and rude will;\n\nAnd where the worser is predominant,\n\nFull soon the canker death eats up that plant.\n\n```\nEnter Romeo\n```\n\n','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',0.0,'0:2:e83743dc',0);
INSERT OR REPLACE INTO cards VALUES('node-7e0dc781266930ac32dc738d3fae1dde72e70613','9y9ve',replace('**Nurse**\n\nMay not one speak?','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',53.0,'0:2:e84f88cb',0);
INSERT OR REPLACE INTO cards VALUES('node-c040ca5b42b15563874bdf458168ff56ad163b4b','9y9ve',replace('**Juliet**\n\nSaints do not move, though grant for prayers'' sake.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',30.0,'0:2:e86d29fb',0);
INSERT OR REPLACE INTO cards VALUES('node-3a657eb2b3a40617eda69208b6d2317cbaae5686','9y9ve',replace('**Juliet**\n\nFeeling so the loss,\n\nCannot choose but ever weep the friend.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',25.0,'0:2:e89ae979',0);
INSERT OR REPLACE INTO cards VALUES('node-46b2f391a7eefa2b5f06623c7bee9deb957bd6ac','9y9ve',replace('**Romeo**\n\nWith Rosaline, my ghostly father? no;\n\nI have forgot that name, and that name''s woe.','\n',char(10)),'node-ad60e1f7ab22723b7965c57f805b953c708b0685',5.0,'0:2:e906fc7a',0);
INSERT OR REPLACE INTO cards VALUES('node-7af2291c8f58832e439b8cbda1c1bdea93c402f3','9y9ve',replace('**Sampson**\n```\nAside to Gregory\n```\nIs the law of our side, if I say\n\nay?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',28.0,'0:2:e9168d0d',0);
INSERT OR REPLACE INTO cards VALUES('node-4ac2f5e15123d85057e4d7273b44da2a16d7f340','9y9ve',replace('**Prince**\n\nThen say at once what thou dost know in this.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',54.0,'0:2:e931856',0);
INSERT OR REPLACE INTO cards VALUES('node-66d9eaf0419efa4df922153f3f297fda92fb0830','9y9ve',replace('**Nurse**\n\nYes, madam: yet I cannot choose but laugh,\n\nTo think it should leave crying and say ''Ay.''\n\nAnd yet, I warrant, it had upon its brow\n\nA bump as big as a young cockerel''s stone;\n\nA parlous knock; and it cried bitterly:\n\n''Yea,'' quoth my husband,''fall''st upon thy face?\n\nThou wilt fall backward when thou comest to age;\n\nWilt thou not, Jule?'' it stinted and said ''Ay.''','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',12.0,'0:2:e98ccd25',0);
INSERT OR REPLACE INTO cards VALUES('node-f9ed4b82e67f967d671da0ba5c35c93d27c056d0','9y9ve',replace('**Lady Capulet**\n\nWhat noise is here?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',1.0,'0:2:ea1fd45a',0);
INSERT OR REPLACE INTO cards VALUES('node-dde5a85c1a9ff44fc2ba527c5294c0a098bf7fad','9y9ve',replace('**Mercutio**\n\nAh, that same pale hard-hearted wench, that Rosaline.\n\nTorments him so, that he will sure run mad.','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',2.0,'0:2:ea2b1f14',0);
INSERT OR REPLACE INTO cards VALUES('node-2fed7b0a911e4588d61181a01a9cdb947ce573c2','9y9ve',replace('**Benvolio**\n\nAway, begone; the sport is at the best.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',39.0,'0:2:ea640c90',0);
INSERT OR REPLACE INTO cards VALUES('node-b57a9736e4f7817f71a0f65031e0d28158ac7a62','9y9ve',replace('**Paris**\n\nGod shield I should disturb devotion!\n\nJuliet, on Thursday early will I rouse ye:\n\nTill then, adieu; and keep this holy kiss.\n\nExit','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',23.0,'0:2:ea79d60d',0);
INSERT OR REPLACE INTO cards VALUES('node-1bf4a0c836341e94f0e7c1f0f3a8ba1545bc1c4b','9y9ve',replace('**Lady Capulet**\n\nWhat say you? can you love the gentleman?\n\nThis night you shall behold him at our feast;\n\nRead o''er the volume of young Paris'' face,\n\nAnd find delight writ there with beauty''s pen;\n\nExamine every married lineament,\n\nAnd see how one another lends content\n\nAnd what obscured in this fair volume lies\n\nFind written in the margent of his eyes.\n\nThis precious book of love, this unbound lover,\n\nTo beautify him, only lacks a cover:\n\nThe fish lives in the sea, and ''tis much pride\n\nFor fair without the fair within to hide:\n\nThat book in many''s eyes doth share the glory,\n\nThat in gold clasps locks in the golden story;\n\nSo shall you share all that he doth possess,\n\nBy having him, making yourself no less.','\n',char(10)),'node-c9cd9dbd07b23ca6341c382993a133bf91f91051',22.0,'0:2:eb5ee609',0);
INSERT OR REPLACE INTO cards VALUES('node-e05bf3f5ac796f7e0adae8257423d9bc642d9c8a','9y9ve',replace('**Juliet**\n\nO, bid me leap, rather than marry Paris,\n\nFrom off the battlements of yonder tower;\n\nOr walk in thievish ways; or bid me lurk\n\nWhere serpents are; chain me with roaring bears;\n\nOr shut me nightly in a charnel-house,\n\nO''er-cover''d quite with dead men''s rattling bones,\n\nWith reeky shanks and yellow chapless skulls;\n\nOr bid me go into a new-made grave\n\nAnd hide me with a dead man in his shroud;\n\nThings that, to hear them told, have made me tremble;\n\nAnd I will do it without fear or doubt,\n\nTo live an unstain''d wife to my sweet love.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',28.0,'0:2:eca94a48',0);
INSERT OR REPLACE INTO cards VALUES('node-0e9bd8bda61034585c4846218e924ce8af87b1af','9y9ve',replace('**Romeo**\n\nI am too sore enpierced with his shaft\n\nTo soar with his light feathers, and so bound,\n\nI cannot bound a pitch above dull woe:\n\nUnder love''s heavy burden do I sink.','\n',char(10)),'node-726f6d7354034ee7b8ad4a4a633164166d103f2c',6.0,'0:2:ed6102ea',0);
INSERT OR REPLACE INTO cards VALUES('node-c98600ea45e47d92801a2dd766202a36b883e38b','9y9ve',replace('**Friar Laurence**\n\nThat''s a certain text.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',9.0,'0:2:ed77bcdf',0);
INSERT OR REPLACE INTO cards VALUES('node-994b92fa2b2b7c52f4722da22e9a9a936925f226','9y9ve',replace('**Gregory**\n\nDo you quarrel, sir?','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',31.0,'0:2:ed941f30',0);
INSERT OR REPLACE INTO cards VALUES('node-de1a53929f1342b48c4fb8614a51eec47269e12b','9y9ve',replace('**Paris**\n\nDo not deny to him that you love me.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',12.0,'0:2:ed9787b7',0);
INSERT OR REPLACE INTO cards VALUES('node-4d1ffd14c4290630302e2d30c7a50f09034838ca','9y9ve',replace('**Lady Capulet**\n\nWell, girl, thou weep''st not so much for his death,\n\nAs that the villain lives which slaughter''d him.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',26.0,'0:2:ee195326',0);
INSERT OR REPLACE INTO cards VALUES('node-2b299bb01f9852f5e07012263d026415df51df3b','9y9ve',replace('**Sampson**\n\nTrue; and therefore women, being the weaker vessels,\n\nare ever thrust to the wall: therefore I will push\n\nMontague''s men from the wall, and thrust his maids\n\nto the wall.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',10.0,'0:2:ee3ba482',0);
INSERT OR REPLACE INTO cards VALUES('node-b0d62732355fae24770b6506f71614980236347d','9y9ve',replace('**Prince**\n\nWhat fear is this which startles in our ears?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',42.0,'0:2:ee3d88ca',0);
INSERT OR REPLACE INTO cards VALUES('node-5174f71833a0d0147881ee0780bc4f383129f0fb','9y9ve',replace('**Capulet**\n\nAnd too soon marr''d are those so early made.\n\nThe earth hath swallow''d all my hopes but she,\n\nShe is the hopeful lady of my earth:\n\nBut woo her, gentle Paris, get her heart,\n\nMy will to her consent is but a part;\n\nAn she agree, within her scope of choice\n\nLies my consent and fair according voice.\n\nThis night I hold an old accustom''d feast,\n\nWhereto I have invited many a guest,\n\nSuch as I love; and you, among the store,\n\nOne more, most welcome, makes my number more.\n\nAt my poor house look to behold this night\n\nEarth-treading stars that make dark heaven light:\n\nSuch comfort as do lusty young men feel\n\nWhen well-apparell''d April on the heel\n\nOf limping winter treads, even such delight\n\nAmong fresh female buds shall you this night\n\nInherit at my house; hear all, all see,\n\nAnd like her most whose merit most shall be:\n\nWhich on more view, of many mine being one\n\nMay stand in number, though in reckoning none,\n\nCome, go with me.\n\nTo Servant, giving a paper\n\nGo, sirrah, trudge about\n\nThrough fair Verona; find those persons out\n\nWhose names are written there, and to them say,\n\nMy house and welcome on their pleasure stay.\n\nExeunt Capulet and Paris','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',4.0,'0:2:ee5ab82b',0);
INSERT OR REPLACE INTO cards VALUES('node-cde20d4f8ce0d3beff51b2f21f3ed2995360229f','9y9ve',replace('**Juliet**\n\nIndeed, I never shall be satisfied\n\nWith Romeo, till I behold him dead \n\nIs my poor heart for a kinsman vex''d.\n\nMadam, if you could find out but a man\n\nTo bear a poison, I would temper it;\n\nThat Romeo should, upon receipt thereof,\n\nSoon sleep in quiet. O, how my heart abhors\n\nTo hear him named, and cannot come to him.\n\nTo wreak the love I bore my cousin\n\nUpon his body that slaughter''d him!','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',33.0,'0:2:eea21455',0);
INSERT OR REPLACE INTO cards VALUES('node-d8383d6c3075703643b87e2cc500854af3cb1c74','9y9ve',replace('**Nurse**\n\nIt did, it did; alas the day, it did!','\n',char(10)),'node-77c5f9b6800158d495ef457b997aacf1352873ac',13.0,'0:2:ef20cf25',0);
INSERT OR REPLACE INTO cards VALUES('node-cfeeebb19262f770a86c486dbb0df79e6d3cf93d','9y9ve',replace('**Nurse**\n\nPeter!','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',46.0,'0:2:ef7a9e05',0);
INSERT OR REPLACE INTO cards VALUES('node-ebfa3682997fd7f3b9971ef51ad1e2266664e778','9y9ve',replace('**Sampson**\n\nDraw, if you be men. Gregory, remember thy swashing blow.\n\n```\nThey fight. Enter Benvolio\n```','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',39.0,'0:2:ef83e00c',0);
INSERT OR REPLACE INTO cards VALUES('node-ef8ba9a07c6d6b9ae019a8745f3479e26b3e55fc','9y9ve',replace('**Prince**\n\nAnd for that offence\n\nImmediately we do exile him hence:\n\nI have an interest in your hate''s proceeding,\n\nMy blood for your rude brawls doth lie a-bleeding;\n\nBut I''ll amerce you with so strong a fine\n\nThat you shall all repent the loss of mine:\n\nI will be deaf to pleading and excuses;\n\nNor tears nor prayers shall purchase out abuses:\n\nTherefore use none: let Romeo hence in haste,\n\nElse, when he''s found, that hour is his last.\n\nBear hence this body and attend our will:\n\nMercy but murders, pardoning those that kill.\n\nExeunt','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',59.0,'0:2:efbb2e45',0);
INSERT OR REPLACE INTO cards VALUES('node-8b512429b8f7b7054c7f8c885582bd4c0140d7f9','9y9ve',replace('**Capulet**\n\nMonday! ha, ha! Well, Wednesday is too soon,\n\nO'' Thursday let it be: o'' Thursday, tell her,\n\nShe shall be married to this noble earl.\n\nWill you be ready? do you like this haste?\n\nWe''ll keep no great ado, a friend or two;\n\nFor, hark you, Tybalt being slain so late,\n\nIt may be thought we held him carelessly,\n\nBeing our kinsman, if we revel much:\n\nTherefore we''ll have some half a dozen friends,\n\nAnd there an end. But what say you to Thursday?','\n',char(10)),'node-1ea0a4f951159826ef795e37aa3321911ddb6685',5.0,'0:2:efd3bd8d',0);
INSERT OR REPLACE INTO cards VALUES('node-d6ae1d2a19489baef1f98bce838d28730df8870d','9y9ve',replace('**First Musician**\n\nWhat a pestilent knave is this same!','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',46.0,'0:2:efe40a8c',0);
INSERT OR REPLACE INTO cards VALUES('node-324c271334dc735001bf8376371a17dba61f8045','9y9ve',replace('**Friar Laurence**\n\nLet me dispute with thee of thy estate.','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',16.0,'0:2:f095c7a8',0);
INSERT OR REPLACE INTO cards VALUES('node-a71efe52510ed07407a06a63f7ccc3c7c11ab02c','9y9ve',replace('**Tybalt**\n\nYou shall find me apt enough to that, sir, an you\n\nwill give me occasion.','\n',char(10)),'node-402cff602d4ada6a8bf74c395021bf1b16c0adac',12.0,'0:2:f0b267cf',0);
INSERT OR REPLACE INTO cards VALUES('node-e67e5907dfb852b7b4ee417533af171e9f279a19','9y9ve',replace('**Juliet**\n\nGood pilgrim, you do wrong your hand too much,\n\nWhich mannerly devotion shows in this;\n\nFor saints have hands that pilgrims'' hands do touch,\n\nAnd palm to palm is holy palmers'' kiss.','\n',char(10)),'node-3bb3141eeb113d0ef68382f4c90f7edfd221e921',26.0,'0:2:f121ed7b',0);
INSERT OR REPLACE INTO cards VALUES('node-9ce61a5cc2676d9d8603ec486ed8fb2aa0c7bcc0','9y9ve',replace('**Nurse**\n\nAy, a thousand times.\n\nExit Romeo\n\nPeter!','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',89.0,'0:2:f1313ae7',0);
INSERT OR REPLACE INTO cards VALUES('node-5392d091f0287f9d3c6581e580073c125ab9fc9e','9y9ve',replace('**Romeo**\n\nAh, Juliet, if the measure of thy joy\n\nBe heap''d like mine and that thy skill be more\n\nTo blazon it, then sweeten with thy breath\n\nThis neighbour air, and let rich music''s tongue\n\nUnfold the imagined happiness that both\n\nReceive in either by this dear encounter.','\n',char(10)),'node-91bfae6e49b5f3c14e09fe58e958a2c1566db4c9',6.0,'0:2:f17b76cf',0);
INSERT OR REPLACE INTO cards VALUES('node-bc88f412e69d4bc4a1ff877c57c15972556ae96b','9y9ve',replace('**Nurse**\n\nO holy friar, O, tell me, holy friar,\n\nWhere is my lady''s lord, where''s Romeo?','\n',char(10)),'node-c99854d2eeeb9e84c81bd35cb2844c671b376559',23.0,'0:2:f1a470df',0);
INSERT OR REPLACE INTO cards VALUES('node-b45f39b0e9b64e5cf383884b7663dc0e6136bc25','9y9ve',replace('**Peter**\n\nThen have at you with my wit! I will dry-beat you\n\nwith an iron wit, and put up my iron dagger. Answer\n\nme like men:\n\n''When griping grief the heart doth wound,\n\nAnd doleful dumps the mind oppress,\n\nThen music with her silver sound''\n\nwhy ''silver sound''? why ''music with her silver\n\nsound''? What say you, Simon Catling?','\n',char(10)),'node-1bb9f279c07e2050487fdda52c6b3760867e97ab',39.0,'0:2:f25d4980',0);
INSERT OR REPLACE INTO cards VALUES('node-69f84f65acccaeee0b804a34ed7cca9b1e944ef6','9y9ve',replace('**Juliet**\n\nThat is no slander, sir, which is a truth;\n\nAnd what I spake, I spake it to my face.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',19.0,'0:2:f2b79830',0);
INSERT OR REPLACE INTO cards VALUES('node-e0e89022e4f8dbed1ecc5b444975112d7688f916','9y9ve',replace('**Servant**\n\nUp.','\n',char(10)),'node-ef4179af31a173bebf93ba226a80aba790e71a74',18.0,'0:2:f2cd8282',0);
INSERT OR REPLACE INTO cards VALUES('node-1a52654be0bb92eb4114ca69f39eebc23513a6d7','9y9ve',replace('**Sampson**\n\nMe they shall feel while I am able to stand: and\n\n''tis known I am a pretty piece of flesh.','\n',char(10)),'node-f24e8e8450a613448ff815755e694fa1f9fbb5a5',16.0,'0:2:f330f8bb',0);
INSERT OR REPLACE INTO cards VALUES('node-0d36ea0fc08369dcb2bfc375c7b8beb1dcdc294a','9y9ve',replace('**Montague**\n\nO thou untaught! what manners is in this?\n\nTo press before thy father to a grave?','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',51.0,'0:2:f3a7e740',0);
INSERT OR REPLACE INTO cards VALUES('node-37649cc92df2207ed5e5f5e8fc779433681a635c','9y9ve',replace('**Montague**\n\nBut I can give thee more:\n\nFor I will raise her statue in pure gold;\n\nThat while Verona by that name is known,\n\nThere shall no figure at such rate be set\n\nAs that of true and faithful Juliet.','\n',char(10)),'node-00fa88e8e313c3e8f462c80db2841aac21334846',62.0,'0:2:f3e2e81a',0);
INSERT OR REPLACE INTO cards VALUES('node-a21aaecd442f6ead6e695d64f552be103ff04b3e','9y9ve',replace('**Juliet**\n\nTo answer that, I should confess to you.','\n',char(10)),'node-04476fcbc92873ed9e47e7da91f2ccc89d895e63',11.0,'0:2:f3f2c197',0);
INSERT OR REPLACE INTO cards VALUES('node-fd15842affbb2575fbad7e8e96ad4f5610e2c1b7','9y9ve',replace('**Romeo**\n\nShe speaks:\n\nO, speak again, bright angel! for thou art\n\nAs glorious to this night, being o''er my head\n\nAs is a winged messenger of heaven\n\nUnto the white-upturned wondering eyes\n\nOf mortals that fall back to gaze on him\n\nWhen he bestrides the lazy-pacing clouds\n\nAnd sails upon the bosom of the air.','\n',char(10)),'node-d759af24e37095a5d83a057be41dd22fbc5bff71',2.0,'0:2:f4123f24',0);
INSERT OR REPLACE INTO cards VALUES('node-fee9a54c5e591e15d686a4af9ce7e2acc76215af','9y9ve',replace('**Capulet**\n\nGo, be gone.\n\nExit Second Servant\n\nWe shall be much unfurnished for this time.\n\nWhat, is my daughter gone to Friar Laurence?','\n',char(10)),'node-c101081ff08d3da647718f58820c4a1af8e10f49',4.0,'0:2:f47bd667',0);
INSERT OR REPLACE INTO cards VALUES('node-ec8470edb8188c6bbad9f4a99933f1ef21dd77e6','9y9ve',replace('**Friar John**\n\nI could not send it, here it is again,\n\nNor get a messenger to bring it thee,\n\nSo fearful were they of infection.','\n',char(10)),'node-b0bc26d40f9b7505ff058217691f903e04566e34',4.0,'0:2:f48eda2b',0);
INSERT OR REPLACE INTO cards VALUES('node-d36e2c6b11fc3df7cb95024295f4f7346e87607b','9y9ve',replace('**Capulet**\n\nO, God ye god-den.','\n',char(10)),'node-0a343b5050c3f3a72feb4b0c962595c47a94b202',52.0,'0:2:f4a3ff12',0);
INSERT OR REPLACE INTO cards VALUES('node-427e821c8659aa740d95ec3b6670af8116ed5f3a','9y9ve',replace('**Mercutio**\n\nA bawd, a bawd, a bawd! so ho!','\n',char(10)),'node-00c260cb07c2d8dd2671f29e1c960cf3a015fb11',62.0,'0:2:f576e5e3',0);
